ESP.ORIGINAL:
ScriptName VNovacSniperVictimSCRIPT

float fSecondsPassed
float fTimeDelta

begin GameMode
	; if we are currently accused.
	if ((GetIsID MannyVargas && VFreeformNovac.nAccusedIndex == 2) || (GetIsID NobarkNoonan && VFreeformNovac.nAccusedIndex == 3) || (GetIsID DustyMcBride && VFreeformNovac.nAccusedIndex == 5) || (GetIsID AliceMcBride && VFreeformNovac.nAccusedIndex == 6) || (GetIsID RangerAndy && VFreeformNovac.nAccusedIndex == 7))
		if (VFreeformNovac.bTargetIsWaiting == 1 && CraigBooneREF.bGotRevenge == 0)
			set fTimeDelta to GetSecondsPassed
			if (fSecondsPassed < 10.0)
				set fSecondsPassed to fSecondsPassed + fTimeDelta
			elseif (SniperVictimTriggerREF.bPlayerInZone && SniperVictimTriggerREF.bVictimInZone && player.GetEquipped HatNCRBoonesBeret && CraigBooneREF.bGotRevenge == 0 && CraigBooneREF.bInNest)
				CraigBooneREF.RemoveScriptPackage;
				SetObjectiveCompleted VMS53 30 1;
				SetObjectiveDisplayed VMS53 40 1;
				KillActor CraigBooneREF, 1, 2
				set CraigBooneREF.bGotRevenge to 1;
				set VFreeformNovac.nSnipersRevengeStage to 20;
				CraigBooneREF.ResetAI;
			else
				set VFreeformNovac.bTargetIsWaiting to 0
				set VFreeformNovac.nAccusedIndex to 0	; invalidate the target
				set fSecondsPassed to 0.0
				RemoveScriptPackage;     Turn off the travel package
				EVP
				SetActorValue SpeedMult 100;		restore normal movement speed
			endif
		endif
	endif
end ;GameMode

begin OnHit CraigBooneREF
	if (GetObjectiveCompleted VMS53 30 == 0)
		SetObjectiveCompleted VMS53 30 1;
		SetObjectiveDisplayed VMS53 40 1;
		KillActor CraigBooneREF, 1, 2
		set CraigBooneREF.bGotRevenge to 1;
		set VFreeformNovac.nSnipersRevengeStage to 20;
		CraigBooneREF.ResetAI;
	endif
end ;OnHit

BEGIN OnHit player
	setav SpeedMult 100;	Reset speed mult if player attacks.
END

begin OnDeath CraigBooneREF
;	set VFreeformNovac.bBooneGotKill to 1;

end ;OnDeath

BEGIN OnDeath

	if(CraigBooneREF.bGotRevenge == 0)
		set VFreeformNovac.bTargetIsWaiting to 0;
		set VFreeformNovac.nAccusedIndex to 0;
		set fSecondsPassed to 0.0;
	endif
	
	if getquestcompleted VCG01
		if (GetIsID MannyVargas && GetObjectiveCompleted VMQ01 45 == 0)
			if(GetHasNote VNovacGreatKhansNote)
				SetObjectiveCompleted VMQ01 45 1;
				SetObjectiveDisplayed VMQ01 50 1;
			else
				SetObjectiveDisplayed VMQ01 45 0;
				SetObjectiveDisplayed VMQ01 46 1;
			endif
		endif
	endif

	; if all victims are killed fail the quest.
	if (GetObjectiveCompleted VMS53 30 == 0)
		if (NobarkREF.GetDead && DustyMcBrideREF.GetDead && AliceMcBrideREF.GetDead && MannyVargasREF.GetDead && JeannieMayCrawfordREF.GetDead && RangerAndyREF.GetDead && CliffBriscoeREF.GetDead)
			SetStage VMS53 255
		endif
	endif
END

ScriptName VCG01DocMitchellChairTriggerSCRIPT

short bDoneOnce;
short bRunTimer;
float fTimer;
short bDocInChair; We assume he is in the chair after x amount of time inside the trigger volume with the chair as his current furniture reference

BEGIN OnTrigger DocMitchellREF
	if(bDoneOnce == 0) && GetStageDone VCG00 85 == 1

		; track doc's seated status
		if (bDocInChair == 0 && DocMitchellREF.IsCurrentFurnitureRef DocMitchellChairREF && bRunTimer == 0)
			set fTimer to 2.5;
			set bRunTimer to 1;
		elseif (DocMitchellREF.IsCurrentFurnitureRef DocMitchellChairREF == 0)
			set bDocInChair to 0;
		endif;

		if(GetObjectiveCompleted VCG01 40 && VCG01DocMitchellCouchTriggerREF.bRunTimer == 0 && bDocInChair == 1)	;player sat on the couch before Doc got to his chair
				Set VCG01DocMitchellCouchTriggerREF.bRunTimer to 1;	start Doc talking.
				set bDoneOnce to 1;
				set bRunTimer to 0;
		elseif (VCG01DocMitchellCouchTriggerREF.bRunTimer == 1)	;player sat on the couch after Doc sat down, so the conversation should already be queued.
			set bDoneOnce to 1;
			set bRunTimer to 0;
		endif

	endif
END

BEGIN GameMode
	if (bRunTimer)
		if (fTimer > 0)
			set fTimer to fTimer - GetSecondsPassed;
		else	;doc should be in his chair by now.
			set bDocInChair to 1;
			set bRunTimer to 0;
		endif
	endif
	
END

ScriptName VCG01RemoveDocCarebearModeTriggerSCRIPT
short bDoOnce;
short bDoneImpulse;
float fTimer;	
short bActive;

BEGIN OnTriggerEnter player
	if (bDoOnce == 0) && ALTGO == 1
		EnablePlayerControls 1 1 1 1 1 1;
	;	Player.AddPerk CompanionSuite 1
	if GameHour > 7 && GameHour < 18
		ApplyImageSpaceModifier NVEyeAdjustISFX;
		ForceWeather NVWastelandClearWindy 1;
	endif
		StartingTumbleweedREF.Enable;
		PlayMusic musSCRGoodspringsStinger;
		set fTimer to 18;
		set bActive to 1;
		set bDoOnce to 1;
	
		; Activate radio stations
		RadioBlackMountainTAREF.enable
		RNVTARef.Enable		;enable Radio New Vegas
		StopQuest VCG02;
		GSVictorREF.MoveTo GSVictorCircuitStartMarkerREF;
		Set VFreeformGoodsprings.bVictorPatrol to 1;
		GSVictorREF.EVP;
		SetCellOwnership GSDocMitchellHouse DocMitchell

	;	If IsPS3 == 1											; Added this bit to kill the feature if the game is running on PS3 - Jorge
	;		VStreetLightStarterOneREF.Disable
	;		VStreetLightStarterTwoREF.Disable
	;		VStreetLightStarterThreeREF.Disable
	;		StopQuest VStreetLighting
	;		VStreetGamblerAREF.Disable					; Had to remove some NPCs for the PS3 version - Jorge
	;		VStreetGamblerBREF.Disable
	;		VStreetTravelerAREF.Disable
	;		VStripNCRTrooperCREF.Disable
	;		VStripNCRMPBREF.Disable
	;		VStreetGamblerAREF.MarkForDelete
	;		VStreetGamblerBREF.MarkForDelete
	;		VStreetTravelerAREF.MarkForDelete
	;		VStripNCRTrooperCREF.MarkForDelete
	;		VStripNCRMPBREF.MarkForDelete
	;	Endif
	endif
END

BEGIN GameMode
	if(bActive)
		set fTimer to fTimer - GetSecondsPassed;
		if (fTimer < 10 && bDoneImpulse == 0)
			VCG01TumbleweedHolderREF.Disable;
			VCG01TumbleweedHolderREF.MarkForDelete;
			VCG01TumbleweedImpulseREF.placeatme vsmallimpulseexplosion;
			set bDoneImpulse to 1;
		elseif (fTimer < 0)
			ReleaseWeatherOverride;		get rid of gusty wind
			MarkForDelete;
		endif
	endif
END

ScriptName VGenericTimerSCRIPT

short nEvent;
float fTimer;
ref rRef;

BEGIN GameMode
	if (fTimer > 0)
		set fTimer to fTimer - GetSecondsPassed;
	else
		if (nEvent == 1) 	; countdown to monorail guards in McCarran going hostile.
			if (VMcCarranMonorailWarning2TriggerREF.bPlayerPresent && Player.GetEquipped FactionGearNVNCR == 0)
				if(VMcCarranNCRTrooperMonorail1REF.GetDistance player < 256)
					VMcCarranNCRTrooperMonorail1REF.SayTo player VMS08MonorailGuardWarn2 1 1
					VMcCarranNCRTrooperMonorail1REF.StartCombat player
				endif
				if(VMcCarranNCRTrooperMonorail3REF.GetDistance player < 256)
					VMcCarranNCRTrooperMonorail3REF.SayTo player VMS08MonorailGuardWarn2 1 1
					VMcCarranNCRTrooperMonorail3REF.StartCombat player
				endif
			endif
			StopQuest VGenericTimer;
		elseif (nEvent == 2)	; Yes Man Uploads himself into Mr. House's computer
			SetStage VMQYesMan01 70
			StopQuest VGenericTimer;
		elseif(nEvent == 3)	; complete VCG01, start VCG02
			SetStage VCG01 200 
			if GetStage VCG02 == 0
				SetStage VCG02 5
			endif
			StopQuest VGenericTimer;
		elseif(nEvent == 4)	; VMS49 paladin leader goes away after a fade-out
			set nEvent to nEvent + 1;
			ApplyImageSpaceModifier FadeToBlackAndBackQuickExtendedISFX;
			set fTimer to 2.0;
			DisablePlayerControls 1 1 1 1 1 1 1
		elseif(nEvent == 5)
			set fTimer to 2.0;
			set nEvent to nEvent + 1;
			VMS49PaladinThreatEnablerREF.Disable;
			EnablePlayerControls 1 1 1 1 1 1 1
		elseif(nEvent == 6)
			set nEvent to 0;
			VeronicaREF.SayTo player VDialogueVeronicaBoSThreatReact;
			StopQuest VGenericTimer;
		elseif(nEvent == 7)  ;VMS49 paladin leader (outside HV) goes away after a fade-out
			set nEvent to nEvent + 1;
			ApplyImageSpaceModifier FadeToBlackAndBackQuickExtendedISFX;
			set fTimer to 2.0;
			DisablePlayerControls 1 1 1 1 1 1 1
		elseif (nEvent == 8) 
			VMS49HVPaladinEnablerREF.Disable;
			EnablePlayerControls 1 1 1 1 1 1 1
			if(VMS49.nFinalState == 2)
				Set VMS49.bBosLetGo to 1;
			endif
			set nEvent to nEvent + 1;
			set fTimer to 4;
		elseif (nEvent == 9)
			set nEvent to 0;
			Set VMS49.bForceGreet to 1;
			Set VMS49.nGreetingPending to 5;
			VeronicaREF.EVP;
			StopQuest VGenericTimer
		elseif (nEvent == 10)
			set nEvent to 0;
			EnablePlayerControls 0 1 1; 	; re-enable pip-boy and fighting (was disabled upon entering to prevent a frisk exploit)
			set VMS18.bPlayerFrisked to 0
			rRef.Activate;
			StopQuest VGenericTimer;
		; VMS49 Elder Confrontation 1 fails.
		elseif (nEvent == 11)
			if (VeronicaConfrontElderTriggerREF.bDoneDiscussion1Once == 0 || (VeronicaREF.IsTalking == 0 && EdgarHardinREF.IsTalking == 0 && NolanMcNamaraREF.IsTalking == 0))
				set VeronicaREF.bTalking to 0;
				set NolanMcNamaraREF.bTalking to 0;
				set EdgarHardinREF.bTalking to 0;
				Set VMS49.nConvoState to 0;
				Set VMS49.bConfrontElder to 0;
				Set VeronicaConfrontElderTriggerREF.bDoneDiscussion1Once to 0;
				VeronicaREF.ResetAI;
			endif
			set nEvent to 0;
			StopQuest VGenericTimer;
		; VMS49 Elder Confrontation 2 fails.
		elseif (nEvent == 12)
			if (VeronicaConfrontElderTriggerREF.bDoneDiscussion2Once == 0 || (VeronicaREF.IsTalking == 0 && EdgarHardinREF.IsTalking == 0 && NolanMcNamaraREF.IsTalking == 0))
				set VeronicaREF.bTalking to 0;
				set NolanMcNamaraREF.bTalking to 0;
				set EdgarHardinREF.bTalking to 0;
				Set VMS49.nConvoState to 0;
				Set VMS49.bConfrontElder to 0;
				Set VeronicaConfrontElderTriggerREF.bDoneDiscussion2Once to 0;
				VeronicaREF.ResetAI;
			endif
			set nEvent to 0;
			StopQuest VGenericTimer;
		endif

	endif
END

SetStage VMQ01 50
set VMQ01.bPrimmClosed to 1
Set VStoryEventKnowsKhansInvolved to 1;

; Set door to not destroyed so player can leave
GSDocMitchellHouseIntDoorREF.SetDestroyed 0

;VCG01CasualHardcoreMessageREF.Activate player 1;
