ESP.ORIGINAL:
Scn	NVDLC04NukedNCRGateSCRIPT

; Script to stop NPCs from following the player to Long 15.
; J.S. 8/12/11

Ref	rUser

BEGIN OnActivate

	If (IsActionRef Player == 1)
		Activate;
		Return;
	Else
		Set rUser to GetActionRef;
		If (rUser.IsInCombat == 1)
			rUser.StopCombat;
			Return;
		Else
			rUser.ResetAI;
			Return;
		Endif
	Endif

END

Scn	NVDLC04RoadTunnelDoor01SCRIPT

; Script attached to a door so that it stops tunnelers from activating it.
; J.S. (8/3/11).

Ref	rUser

BEGIN OnActivate

	If (IsActionRef Player == 1)
		Activate;
		Return;
	Else
		Set rUser to GetActionRef;
		If (rUser.GetInFaction NVDLC04TunnelerFaction != 1)
			Activate;
			Return;
		Else
			rUser.StopCombat;
			Return;
		Endif
	Endif

END

Scn		NVDLC04Silo02Floor01AwakenBotTriggerSCRIPT

; This script awakens the first floor sentry bot in Silo02 upon crossing its associated trigger.

; J.S. 7/21/11

Short	bDoOnce				; 1 = bot enabled, and trigger is now ready for deletion.

BEGIN OnTriggerEnter Player

	If (bDoOnce == 0)													; Simple DoOnce check, to enable the robot of silo's first floor. We keep it disabled to help performance, and aggro, when players are turning the silo's power back on (bottom level).
		If (NVDLC04Silo02SentryREF.GetDisabled == 1)		; Added this check so that if the sentry is already enabled (by having crossed a reference of the associated activator trigger elsewhere) we simply disable the volume rather than call Enable again.
			NVDLC04Silo02SentryREF.Enable;
			Set bDoOnce to 1;
			Disable;
			MarkForDelete;
		Else
			Set bDoOnce to 1;
			Disable;
			MarkForDelete;
		Endif
	Endif

END

scn NVDLC04BowieKnifeUniqueStaticSCRIPT


; Adds Blood-Nap to the player's inventory.
; CES 07/20/11


Short bDoOnce;		;runs script once


BEGIN OnActivate Player

	If ((GetActionRef == Player) && (bDoOnce == 0))

		NVDLC04BloodNapStaticREF.Disable;
		Player.AddItemHealthPercent NVDLC04WeapBowieKnifeUnique 1 .60;
		Set bDoOnce To 1;
		NVDLC04BloodNapStaticREF.MarkForDelete;

	EndIf

END

Scn	NVDLC04LaunchControlLeverSCRIPT

; Script that handles activation states of the lever activator tied to the missile launch in Road02.
; J.S. 7/12/11.


Short	bDoOnce		; 1 = Lever has been used, and remains in its final position thereafter.
Int		nState		; 0 = Lever is retracted (tandem console has not been opened by EDE); 1 = Lever is rising; 2 = Lever is extended, and ready for use; 3 = Lever has been used.
						; these states correspond, respectively, to the following animation groups on the lever: SpecialIdle, Forward, Backward, Left.


BEGIN OnActivate

	If (IsActionRef Player == 1)
		If (bDoOnce == 0)
			If (nState == 2)
				If (NVDLC04MissileLaunchTerminalREF.bAccess == 1)
					If (NVDLC04MissileLaunchTerminalREF.bLaunched == 0)
						SetDestroyed 1;
						NVDLC04MissileLaunchTerminalREF.SetDestroyed 1;
						NVDLC04EDETerminalLeverLaunch01REF.PlayGroup Left 0;
						;NVDLC04SiloBDControlsLightREF.Disable;
						PlaySound NVDLC04ObjSiloControlRight;
						Set NVDLC04MissileLaunchTerminalREF.bLaunched to 1;
						Set NVDLC04MissileLaunchTerminalREF.fTimer to 1;
						Set NVDLC04MissileLaunchTerminalREF.nTimerStage to 3;
						DisablePlayerControls;
						Return;
					Endif
					Set nState to 3;
					Set bDoOnce to 1;
					Return;
				Endif
			Elseif (nState == 3)
				ShowMessage NVDLC04EDELeverOnMSG;
			Endif
		Endif
	Endif

END

scn NVDLC04FlashBangSCRIPT


; Displayes a message the first time a flashbang is picked up
; CES 07/01/11


BEGIN OnAdd Player

	If (NVDLC04MQ00.bFlashbangInfo == 0)
		Set NVDLC04MQ00.bFlashbangInfo to 1;
		ShowMessage NVDLC04FlashbangAbilitiesMSG;
		AddNote NVDLC04FlashbangAbilitiesNote;
	EndIf

END

Scn	NVDLC04Silo02ElevatorCodeHolderTestSCRIPT

; This script controls the scripted scenes in the Silo02 Elevator ride. It will handle several scenes, and creature spawning, accounted by the var nScene.
; This script has several structural points where no actions are taken yet - the frame is such that it will allow quick work on it, if necessary, during polish phase (alpha).

; J.S. 3/15/11

Ref	rFire01
Ref	rFire02
Ref	rFire03
Ref	rFire04
Ref	rFire05
Ref	rTun01
Ref	rTun02
Ref	rTun03
Ref	rTun04
Ref	rTun05
Ref	rTun06

Short	bInit						; 1 = Initial operations that run once.
Short	bElevatorOn			; 1 = Elevator Activator has been triggered.
Short	bRideStart				; 1 = 
Short	bRideEnd				; 1 = 
Short	bRidePrep				; 1 = 
Short	bFinale					; 1 = 

Short	bPlayerInterference	; 1 = Player has carried out some action that would normally disrupt the flow of the scene in question.

Int		nDeadTunnelers		; Value corresponds to the number of killed tunnelers - set on the tunnelers' script (proper to the elevator sequence).
Int		nScene					; 1 - 3 = Large collection of events, each is discretely considered a "scene."
Int		nCount					; Operational integer.
Int		nState					; Operational integer - defines larger segments than nCount.
Float	fTimer					; Timer Float.


BEGIN GameMode

	SetAllVisible 1;

	If (GetDisabled == 1)																														; If the codebox is disabled, skip all code.
		Return;
	Else																																			; If the codebox is enabled, continue.
		If (bInit == 0)																															; Init check.
			Set rFire01 to NVDLC04Silo02ElevatorFire01REF;
			Set rFire02 to NVDLC04Silo02ElevatorFire02REF;
			Set rFire03 to NVDLC04Silo02ElevatorFire03REF;
			;Set rFire04 to NVDLC04Silo02ElevatorFire04REF;
			;Set rFire05 to NVDLC04Silo02ElevatorFire05REF;
			Set rTun01 to NVDLC04ElevTunnelerWave01AmbREF;
			Set rTun02 to NVDLC04ElevTunnelerWave01BruteREF;
			Set rTun03 to NVDLC04ElevTunnelerWave01VenREF;
			Set rTun04 to NVDLC04ElevTunnelerWave02AmbREF;
			Set rTun05 to NVDLC04ElevTunnelerWave02BruteREF;
			Set rTun06 to NVDLC04ElevTunnelerWave02VenREF;

			rTun01.SetAV Aggression 0;
			rTun02.SetAV Aggression 0;
			rTun03.SetAV Aggression 0;
			rTun04.SetAV Aggression 0;
			rTun05.SetAV Aggression 0;
			rTun06.SetAV Aggression 0;

			rTun01.SetGhost 1;
			rTun02.SetGhost 1;
			rTun03.SetGhost 1;
			rTun04.SetGhost 1;
			rTun05.SetGhost 1;
			rTun06.SetGhost 1;

			NVDLC04ElevTunnelerVictim02REF.SetGhost 1;
			NVDLC04ElevTunnelerVictim03REF.SetGhost 1;

			NVDLC04Silo02ElevatorAlarm01REF.Enable;

			Set bInit to 1;
			Return;
		Else																																		; When init conditions are set, start with scene 0.
			If (nScene == 0)
				If (bPlayerInterference == 0)																									; If players have not interfered with the scene, continue normally.
					If (bRideStart == 0)
						If (nState == 0)																												;
							Set fTimer to (fTimer + GetSecondsPassed);
							If (nCount == 0)
								If (fTimer >= 2)
									NVDLC04Silo02ElevatorAlarm01REF.PlaySound NPCSporeCarrierHissVox;
									Set fTimer to 0;
									Set nCount to 1;
									Return;
								Endif
							Elseif (nCount == 1)
								If (fTimer >= 1)
									NVDLC04ElevTunnelerVictim02REF.Enable;
									Set fTimer to 0;
									Set nCount to 2;
									Return;
								Endif
							Elseif (nCount == 2)
								NVDLC04ElevTunnelerVictim02REF.SetPos X 272;
								NVDLC04ElevTunnelerVictim02REF.SetPos Y 3458;
								NVDLC04ElevTunnelerVictim02REF.SetPos Z 2071;
								Set nCount to 3;
								Return;
							Elseif (nCount == 3)
								NVDLC04ElevTunnelerVictim02REF.PlayIdle SpecialIdleClimbbipAlt;
								Set nCount to 4;
								Return;
							Elseif (nCount == 4)
								If (NVDLC04ElevTunnelerVictim02REF.GetAnimAction == 13)
									NVDLC04ElevTunnelerVictim02REF.PlaySound NVDLC04NPCSporeCarrierFenceClimbAlt;
									NVDLC04ElevTunnelerVictim02REF.SetGhost 0;
									Set fTimer to 0;
									Set nCount to 0;
									Set nState to 1;																										; We've reset operational var, and move to state 1.
									Return;
								Else
									Set nCount to 3;
									Return;
								Endif
							Endif
						Elseif (nState == 1)																										; 
							If (nCount == 0)
								If (fTimer >= 2)
									NVDLC04ElevTunnelerVictim03REF.Enable;
									NVDLC04Silo02ElevatorAlarm01REF.PlaySound NPCSporeCarrierScream;
									Set fTimer to 0;
									Set nCount to 1;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
									Return;
								Endif
							Elseif (nCount == 1)
									NVDLC04ElevTunnelerVictim03REF.SAA 0;
									Set nCount to 2;
									Return;
							Elseif (nCount == 2)
									NVDLC04ElevTunnelerVictim03REF.PlayIdle SpecialIdleClimbbip;
									Set nCount to 3;
									Return;
							Elseif (nCount == 3)
								If (NVDLC04ElevTunnelerVictim03REF.GetAnimAction == 13)
									NVDLC04ElevTunnelerVictim03REF.PlaySound NVDLC04NPCSporeCarrierFenceClimb;
									Set nCount to 4;
									Return;
								Else
									Set nCount to 2;
									Return;
								Endif
							Elseif (nCount == 4)
								NVDLC04ElevTunnelerVictim03REF.SetGhost 0;
								NVDLC04ElevTunnelerVictim03REF.SAA 1;
								NVDLC04ElevTunnelerVictim03REF.SetAngle Z 90;
								If (bElevatorOn == 1)
									Set bRideStart to 1;
									Set NVDLC04Silo2ElevatorControlsREF.bActive to 1;
									Return;
								Endif
								Set nCount to 5;
								Return;
							Elseif (nCount == 5)
								Set fTimer to (fTimer + GetSecondsPassed);
								If (fTimer >= 4)
									NVDLC04Silo02ElevatorAlarm01REF.PlaySound NPCSporeCarrierHissVox;
									;NVDLC04ElevTunnelerVictim01REF.SMS Flames01;
									;NVDLC04SiloVictimFireLightREF.Disable;
									Set nCount to 0;
									Set fTimer to 0;
									Set nState to 2;																									; We've reset operational vars, and move to state 1.
								Else
									If (bElevatorOn == 1)
										Set bRideStart to 1;
										Set NVDLC04Silo2ElevatorControlsREF.bActive to 1;
										Return;
									Endif
								Endif
							Endif
						Elseif (nState == 2)																										; 
							If (bElevatorOn == 1)
								Set bRideStart to 1;
								Set NVDLC04Silo2ElevatorControlsREF.bActive to 1;
								Return;
							Endif
						Endif
					Else
						If (bRidePrep == 0)
							NVDLC04SiloElevatorStartCollision02REF.PlaySound NPCSporeCarrierScream;
							rTun01.Enable;
							rTun02.Enable;
							rTun03.Enable;
							Set nCount to 0;
							Set nState to 0;
							Set fTimer to 0;
							Set bRidePrep to 1;
							Return;
						Else
							If (nState == 0)
								Set fTimer to (fTimer + GetSecondsPassed);
								If (nCount == 0)
									If (fTimer >= 1.3)																									; Run the 2 sec timer.
										NVDLC04Silo02ElevatorFireVictimREF.PlaceAtMe NVDLC04Silo02ElevatorExplosionSpecial;
										NVDLC04ElevTunnelerVictim01REF.Enable;
										Set fTimer to 0;
										Set nCount to 1;
										Return;
									Endif
								Elseif (nCount == 1)
									If (fTimer >= 0.1)	
										NVDLC04ElevTunnelerVictim01REF.SetPos X -87;
										NVDLC04ElevTunnelerVictim01REF.SetPos Y 3395;
										NVDLC04ElevTunnelerVictim01REF.SetPos Z 2383;
										Set fTimer to 0;
										Set nCount to 2;
										Return;
									Endif
								Elseif (nCount == 2)
									If (fTimer >= 0.2)																									; Run the 2 sec timer.
										NVDLC04Silo02ElevatorFireVictimREF.PlaceAtMe NVDLC04Silo02ElevatorExplosionSpecial;
										Set fTimer to 0;
										Set nCount to 3;
										Return;
									Endif
								Elseif (nCount == 3)
									If (fTimer >= 0.2)																									; Run the 2 sec timer.
										NVDLC04ElevTunnelerVictim01REF.KillActor NVDLC04UlyssesREF;
										NVDLC04ElevTunnelerVictim01REF.PMS Flames01;
										Set nCount to 4;
										Return;
									Endif
								Elseif (nCount == 4)
									If (fTimer >= 2.4)
										rTun01.CIOS NVDLC04TunnelerLiftDT;
										rTun02.CIOS NVDLC04TunnelerLiftDT;
										rTun03.CIOS NVDLC04TunnelerLiftDT;
										Set nCount to 5;
										Set fTimer to 0;
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Elseif (nCount == 5)
									rTun01.Enable;
									Set nCount to 6;
									Return;
								Elseif (nCount == 6)
									;rTun01.MoveTo NVDLC04Silo02ElevatorAlarm01REF;
									;ShowWarning "Moved In Front."
									rTun01.SetAngle Z 0;
									rTun01.SAA 0;
									rTun01.SetAV Aggression 0;
									Set nCount to 7;
									Return;
								Elseif (nCount == 7)
									rTun01.MoveTo NVDLC04ElevTunnelerPoint01REF;
									Set nCount to 8;
									Return;
								Elseif (nCount == 8)
									If (fTimer >= 0.2)
										rTun01.SetAngle Z 0;
										If (rTun01.IsIdlePlaying != 1)
											rTun01.PlayIdle SpecialIdleClimbElevator;
											Set nCount to 9;
										Endif
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Elseif (nCount == 9)
									If (fTimer >= 0.3)
										rTun01.SetRestrained 1;
										rTun01.SetAngle Z 0;
										;rTun01.SetPos X -142
										rTun01.SetPos Y 3670
										;rTun01.SetPos Z 2064;
										rTun01.SAA 1;
										rTun01.SetGhost 0;
										Set nCount to 10;
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Elseif (nCount == 10)
									If (fTimer >= 3)
										Set nCount to 0;
										Set fTimer to 0;
										Set nState to 1;
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Endif
							Elseif (nState == 1)
								If (nCount == 0)
									If (fTimer >= 5)
										NVDLC04ElevTunnelerVictim01REF.SMS Flames01;
										NVDLC04SiloVictimFireLightREF.Disable;
										Set nCount to 1;
										Set fTimer to 0;
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Elseif (nCount == 1)
									rTun02.Enable;
									Set nCount to 2;
									Return;
								Elseif (nCount == 2)
									;rTun02.MoveTo NVDLC04Silo02ElevatorAlarm01REF;
									rTun02.SetAngle Z 0;
									rTun02.SAA 0;
									rTun02.SetAV Aggression 0;
									Set nCount to 3;
									Return;
								Elseif (nCount == 3)
									rTun02.MoveTo NVDLC04ElevTunnelerPoint02REF;
									Set nCount to 4;
									Return;
								Elseif (nCount == 4)
									If (fTimer >= 0.2)
										rTun02.SetAngle Z 0;
										If (rTun02.IsIdlePlaying != 1)
											rTun02.PlayIdle SpecialIdleClimbElevator;
											Set nCount to 5;
										Endif
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Elseif (nCount == 5)
									If (fTimer >= 0.3)
										rTun02.SetRestrained 1;
										rTun02.SetPos Y 3670
										rTun02.SetAngle Z 0;
										rTun02.SAA 1;
										rTun02.SetGhost 0;
										Set nCount to 6;
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Elseif (nCount == 6)
									If (fTimer >= 3)
										Set nCount to 0;
										Set fTimer to 0;
										Set nState to 2;
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Endif
							Elseif (nState == 2)
								If (nCount == 0)
									If (fTimer >= 5)
										;PlayISFX
										Set nCount to 1;
										Set fTimer to 0;
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Elseif (nCount == 1)
									rTun03.Enable;
									Set nCount to 2;
									Return;
								Elseif (nCount == 2)
									;rTun03.MoveTo NVDLC04Silo02ElevatorAlarm01REF;
									rTun03.SetAngle Z 90;
									rTun03.SAA 0;
									rTun03.SetAV Aggression 0;
									Set nCount to 3;
									Return;
								Elseif (nCount == 3)
									rTun03.MoveTo NVDLC04ElevTunnelerPoint03REF;
									Set nCount to 4;
									Return;
								Elseif (nCount == 4)
									If (fTimer >= 0.2)
										rTun03.SetAngle Z 90;
										If (rTun03.IsIdlePlaying != 1)
											rTun03.PlayIdle SpecialIdleClimbBipAlt;
											rTun03.PlaySound NVDLC04NPCSporeCarrierFenceClimbAlt;
											Set nCount to 5;
										Endif
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Elseif (nCount == 5)
									If (fTimer >= 0.3)
										rTun03.SetRestrained 1;
										rTun03.SetAngle Z 90;
										rTun03.SAA 1;
										rTun03.SetGhost 0;
										Set nCount to 6;
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Elseif (nCount == 6)
									If (fTimer >= 3)
										NVDLC04Silo02ElevatorAlarm01REF.PlaySound NPCSporeCarrierScream;
										Set nCount to 0;
										Set fTimer to 0;
										Set nState to 0;
										Set nScene to 1;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Endif
							Endif
						Endif
					Endif
				Endif
			Elseif (nScene == 1)																												; 
				If (bPlayerInterference == 0)																									; 
					If (nState == 0)																												; 
						If (nDeadTunnelers >= 1)
							If (nCount == 0)
								If (fTimer >= 10)
									;PlayISFX
									NVDLC04ElevTunnelerVictim01REF.Disable;
									NVDLC04Silo02ElevatorAlarm01REF.PlaySound NPCSporeCarrierHissVox;
									Set nCount to 1;
									Set fTimer to 0;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Elseif (nCount == 1)
								rTun04.Enable;
								Set nCount to 2;
								Return;
							Elseif (nCount == 2)
								rTun04.CIOS NVDLC04TunnelerLiftDT;
								rTun04.SAA 0;
								rTun04.SetAV Aggression 0;
								Set nCount to 3;
								Return;
							Elseif (nCount == 3)
								If (NVDLC04Silo2ElevatorControlsREF.fTimer < 60)
									rTun04.MoveTo NVDLC04ElevTunnelerPoint01REF;
								Else
									rTun04.MoveTo NVDLC04ElevTunnelerPoint03REF;
								Endif
								Set nCount to 4;
								Return;
							Elseif (nCount == 4)
								If (fTimer >= 0.2)
									If (rTun04.IsIdlePlaying != 1)
										If (NVDLC04Silo2ElevatorControlsREF.fTimer < 60)
											rTun04.PlayIdle SpecialIdleClimbElevator;
										Else
											rTun04.PlayIdle SpecialIdleClimbBip;
											rTun04.PlaySound NVDLC04NPCSporeCarrierFenceClimb;
										Endif
										Set nCount to 5;
									Endif
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Elseif (nCount == 5)
								If (fTimer >= 0.3)
									If (NVDLC04Silo2ElevatorControlsREF.fTimer < 60)
										rTun04.SetAngle Z 0;
										rTun04.SetPos Y 3670
									Else
										rTun04.SetAngle Z 90;
									Endif
									rTun04.SetRestrained 1;
									rTun04.SAA 1;
									rTun04.SetGhost 0;
									Set nCount to 6;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Elseif (nCount == 6)
								If (fTimer >= 3)
									Set nCount to 0;
									Set fTimer to 0;
									Set nState to 1;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Endif
						Endif
					Elseif (nState == 1)																										;
						If (nDeadTunnelers >= 2)
							If (nCount == 0)
								If (fTimer >= 1)
									;PlayISFX
									Set nCount to 1;
									Set fTimer to 0;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Elseif (nCount == 1)
								rTun05.Enable;
								Set nCount to 2;
								Return;
							Elseif (nCount == 2)
								rTun05.CIOS NVDLC04TunnelerLiftDT;
								rTun05.SAA 0;
								rTun05.SetAV Aggression 0;
								Set nCount to 3;
								Return;
							Elseif (nCount == 3)
								rTun05.MoveTo NVDLC04ElevTunnelerPoint04REF;
								Set nCount to 4;
								Return;
							Elseif (nCount == 4)
								If (fTimer >= 0.2)
									If (rTun05.IsIdlePlaying != 1)
										rTun05.PlayIdle SpecialIdleClimbBipAlt;
										rTun05.PlaySound NVDLC04NPCSporeCarrierFenceClimbAlt;
										Set nCount to 5;
									Endif
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Elseif (nCount == 5)
								If (fTimer >= 0.3)
									rTun05.SetRestrained 1;
									rTun05.SetAngle Z 270;
									rTun05.SAA 1;
									rTun05.SetGhost 0;
									Set nCount to 6;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Elseif (nCount == 6)
								If (fTimer >= 3)
									Set nCount to 0;
									Set fTimer to 0;
									Set nState to 2;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Endif
						Endif
					Elseif (nState == 2)
						If (nDeadTunnelers >= 3)
							If (nCount == 0)
								If (fTimer >= 1)
									;PlayISFX
									Set nCount to 1;
									Set fTimer to 0;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Elseif (nCount == 1)
								rTun06.Enable;
								Set nCount to 2;
								Return;
							Elseif (nCount == 2)
								rTun06.CIOS NVDLC04TunnelerLiftDT;
								rTun06.SAA 0;
								rTun06.SetAV Aggression 0;
								Set nCount to 3;
								Return;
							Elseif (nCount == 3)
								If (NVDLC04Silo2ElevatorControlsREF.fTimer < 60)
									rTun06.MoveTo NVDLC04ElevTunnelerPoint02REF;
								Else
									rTun06.MoveTo NVDLC04ElevTunnelerPoint03REF;
								Endif
								Set nCount to 4;
								Return;
							Elseif (nCount == 4)
								If (fTimer >= 0.2)
									If (rTun06.IsIdlePlaying != 1)
										If (NVDLC04Silo2ElevatorControlsREF.fTimer < 60)
											rTun06.PlayIdle SpecialIdleClimbElevator;
										Else
											rTun06.PlayIdle SpecialIdleClimbBipAlt;
											rTun06.PlaySound NVDLC04NPCSporeCarrierFenceClimbAlt;
										Endif
										Set nCount to 5;
									Endif
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Elseif (nCount == 5)
								If (fTimer >= 0.3)
									If (NVDLC04Silo2ElevatorControlsREF.fTimer < 60)
										rTun06.SetPos Y 3670
										rTun06.SetAngle Z 0;
									Else
										rTun06.SetAngle Z 90;
									Endif
									rTun06.SetRestrained 1;
									rTun06.SAA 1;
									rTun06.SetGhost 0;
									Set nCount to 6;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Elseif (nCount == 6)
								If (fTimer >= 3)
									Set nCount to 0;
									Set fTimer to 0;
									Set nState to 3;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Endif
						Endif
					Endif
				Endif
			Endif
		Endif
	Endif

END

scn NVDLC04TunnelerTESTSCRIPT


; This script has the functionality for the special tunneler behaviors. Variable04 is used to check whether tunneling behavior should begin or not.
; CES 1/25/11
; J.S. 06/22/11	- Reworked conditional dependency on GameMode Block
; J.S. 06/22/11	- Moved OnLoad work to the GameMode block, to prevent auto-load issues with scripts running.
; J.S. 06/22/11	- Added new var, nCount, for structural hierarchy when calling enable/disable functions, and animation functions.


Ref rEffectA;					; Reference for the effect that play at location A
Ref rEffectB;				; Reference for the effect that play at location B
Ref rLinkA;					; Reference for location A (Digging Hole)
Ref rLinkB;					; Reference for location B (Emergence Hole)

Short bTeleport;			; Variable to track the teleportation process
Short nStage;				; Tracks which stage of the process we are in
								; Stage 0 - Initial effects and animation at dig hole.
								; Stage 1 - Delay for effects, then disable NPC and Effects.
								; Stage 2 - Delay for tunneling time, then effects and animation at emergence hole.
								; Stage 3 - Delay for effects, then disable effects and cleanup.
Short	nCount				; Operational variable.

Short bInitialized;			; Tracks initializing settings

Short bRetreat;				; Tracks whether the tunneler has tried to flee after taking too much damage.

Float fTimer;				; Timer for delays.

Short bShaderLoaded	; 1 = Fancy shader FX loaded.


BEGIN OnDeath

	StopMagicShaderVisuals GlowingOneFXShader;

END


BEGIN GameMode

	If ( GetDead == 1 )
		Return;
	Else
		If (bInitialized == 0)
			Set rLinkA to GetLinkedRef;
			Set rLinkB to rLinkA.GetLinkedRef;
			Set bInitialized to 1;
		Else
			If (bRetreat == 0)																									; At 50% health, the tunneler runs for a digging hole and emerges elsewhere.
				If (GetHealthPercentage <= .50) && (GetActorValue Confidence != 0)
					Set bRetreat to 1;
					AddScriptPackage NVDLC04TunnelerFleePackage;
					Return;
				Endif
			Else
				If (bTeleport == 0)
					If (GetActorValue Variable04 == 1)																		; Running this off a character variable so we can use this in a generic form. Also need to have it set our variable since the value gets cleared when we disable.
						Set bTeleport to 1;
						SetActorValue Variable04 0;
						Return;
					Endif
				Else 																												; Only run the main gamemode block once we're ready		
					If ( nStage == 0 )
						If (nCount == 0)
							SetGhost 1;																							; Make the tunneler immune to death.
							SetActorValue Aggression 0;
							RemoveFromFaction NVDLC04TunnelerFaction;
							StopCombat Player;
							Set nCount to 1;
							Return;
						Elseif (nCount == 1)
							PlayIdle Burrow;
							Set nCount to 2;
							Return;
						Elseif (nCount == 2)
							If (GetAnimAction == 13)																			; Go to next Stage
								Set nStage to 1;
								Set nCount to 0;
								Return;
							Else
								Set ncount to 1;
								Return;
							Endif
						Endif
					ElseIf ( nStage == 1 )
						If (nCount == 0)
							If (GetAnimAction != 13)																			; Have a time delay before disabling actor, and effects. This is handled by the end of the script-forced animation called above.
								SetActorAlpha 0;																				; Disable the tunneler.
								Disable;
								Set nCount to 1;
								Return;
							EndIf
						Elseif (nCount == 1)
							Set nCount to 0;
							Set nStage to 2;
							Return;
						Endif
					ElseIf ( nStage == 2 )
						If (nCount == 0)
							If (fTimer >= 4)																						; Have a time delay before moving the disabled tunneler to its emerge spot.
								MoveTo rLinkB;
								Set nCount to 1;
								Return;
							Else
								Set fTimer to (fTimer + GetSecondsPassed);
								Return;				
							EndIf
						Elseif (nCount == 1)
							Enable; 																								; Enable the tunneler.
							Set fTimer to 0;
							Set nCount to 0;
							Set nStage to 3;
						Endif
					ElseIf ( nStage == 3 )
						If (nCount == 0) 																						; Play Emerge Animation
							;AddScriptPackage NVDLC04TunnelerEmergePackage;
							PlayIdle Emerge;
							SetActorAlpha 0;
							Set nCount to 1;
							Return;
						Elseif (nCount == 1)
							If (GetAnimAction == 13)
								SetActorAlpha 1;
								Set nCount to 0;
								Set nStage to 4;
								Return;
							Else
								Set nCount to 0;
								Return;
							Endif
						Endif
					ElseIf ( nStage == 4 )
						If (nCount == 0)
							SetGhost 0;
							Set nCount to 1;
							Return;
						Elseif (nCount == 1)
							If (GetAnimAction != 13)
								SetActorValue Aggression 2;
								AddToFaction NVDLC04TunnelerFaction 1;
								RemoveScriptPackage;
								Set nCount to 0;			
								Set nStage to 0;
								Set bTeleport to 0;																				; We're done here. Now the script will begin checking again the Variable04 conditional.
							EndIf
						EndIf
					EndIf
				Endif
			Endif
		Endif
	Endif

END ; GameMode


BEGIN OnDeath

	If ( IsKiller Player == 0 ) && ( IsKiller NVDLC04EDEREF== 0 ) && ( IsKiller NVDLC04UlyssesREF == 0 )
		KillActor Player 0;
	EndIf

END

BEGIN OnLoad

	; This initializes our variables and only runs once.
	If (bInitialized == 0)

		Set rLinkA to GetLinkedRef;
		Set rLinkB to rLinkA.GetLinkedRef;		
		Set bInitialized to 1;

	EndIf

END ; OnLoad


BEGIN GameMode

	If ( GetDead == 1 )
		Return;
	EndIf

	; At 50% health, the tunneler runs for a digging hole and emerges elsewhere.
	If ( GetHealthPercentage <= .50 ) && ( bRetreat == 0 ) && ( GetActorValue Confidence != 0 )

		Set bRetreat to 1;
		AddScriptPackage NVDLC04TunnelerFleePackage;

	EndIf

	; Running this off a character variable so we can use this in a generic form. Also need to have it set our variable since the value gets cleared when we disable.
	If ( GetActorValue Variable04 == 1 )

		Set bTeleport to 1;
		SetActorValue Variable04 0;

	EndIf

	If ( bTeleport == 1 )		;Only run the main gamemode block once we're ready		

		If ( nStage == 0 )

			;Make the NPC Unkillable
			;SetActorRefEssential 1;	
			SetGhost 1;
			SetActorValue Aggression 0;
			RemoveFromFaction NVDLC04TunnelerFaction;
			StopCombat Player;	

			; Play Effects
			; Set rEffectA to rLinkA.PlaceAtMe DLC04ShovelDirtExplosion 1;

			; Play Submerge Animation
			PlayIdle Burrow;
			;AddScriptPackage NVDLC04TunnelerSubmergePackage;

			Set fTimer to 2.0;
			; Go to next Stage
			Set nStage to 1;

		ElseIf ( nStage == 1 )

			; Have a time delay
			If ( fTimer > 0 )
				Set fTimer to fTimer - GetSecondsPassed;
			Else
				; Disable the NPC & Effects
				SetActorAlpha 0;
				; Disable;
				; rEffectA.Disable;
				; rEffectA.MarkForDelete;

				; Set the next time duration (for how long he stays tunneling)
				Set fTimer to 4.0;

				; Go to next Stage
				Set nStage to 2;

			EndIf

		ElseIf ( nStage == 2 )

			; Have a time delay
			If ( fTimer > 0 )

				Set fTimer to fTimer - GetSecondsPassed;

			Else

				MoveTo rLinkB;

				; Play Effects
				; Set rEffectB to rLinkB.PlaceAtMe DLC04ShovelDirtExplosion 1;

				; Enable the NPC
				SetActorAlpha 1;
				; Enable;

				; Set the next time duration
				Set fTimer to 3.0;

				; Go to next Stage
				Set nStage to 3;

			EndIf

		ElseIf ( nStage == 3 )

			; Play Emerge Animation
			AddScriptPackage NVDLC04TunnelerEmergePackage;
			;PlayIdle Emerge;

			; Set the next time duration
			Set fTimer to 4.0;

			; Go to next Stage
			Set nStage to 4;

		ElseIf ( nStage == 4 )

			; Have a time delay
			If (fTimer > 0)

				Set fTimer to fTimer - GetSecondsPassed;

			Else

				; Disable the Effects
				; rEffectB.Disable;
				; rEffectB.MarkForDelete;

				;Make the NPC killable
				;SetActorRefEssential 0;	
				SetGhost 0;
				SetActorValue Aggression 2;
				AddToFaction NVDLC04TunnelerFaction 1;
				RemoveScriptPackage;

				; Clear variables
				Set nStage to 0;

				;We're done here, set this gamemode block to not run.
				Set bTeleport to 0;

			EndIf

		EndIf

	EndIf

END ; GameMode

scn NVDLC04TunnelerAmbusherTESTSCRIPT


; This script has the functionality for the special tunneler behaviors. Variable04 is used to check whether tunneling behavior should begin or not.
; CES 1/25/11


Ref rEffectA;					; Reference for the effect that play at location A
Ref rEffectB;				; Reference for the effect that play at location B
Ref rLinkA;					; Reference for location A (Digging Hole)
Ref rLinkB;					; Reference for location B (Emergence Hole)

Short bTeleport;			; Variable to track the teleportation process
Short nStage;				; Tracks which stage of the process we are in
								; Stage 0 - Initial effects and animation at dig hole.
								; Stage 1 - Delay for effects, then disable NPC and Effects.
								; Stage 2 - Delay for tunneling time, then effects and animation at emergence hole.
								; Stage 3 - Delay for effects, then disable effects and cleanup.
Short	nCount				;

Short bInitialized;			; Tracks initializing settings

Short bRetreat;				; Tracks whether the tunneler has tried to flee after taking too much damage.

Float fTimer;				; Timer for delays.

Short bShaderLoaded	; 1 = Fancy shader FX loaded.


BEGIN OnDeath

	StopMagicShaderVisuals GlowingOneFXShader;

END


BEGIN OnStartCombat Player

	; When the tunneler first starts combat with the player, initiate his sneak attack sequence.
	If ( bRetreat == 0 ) && ( GetActorValue Confidence != 0 )

		Set bRetreat to 1;
		AddScriptPackage NVDLC04TunnelerFleePackage;

	EndIf

END


BEGIN GameMode

	If ( GetDead == 1 )
		Return;
	Else
		If (bInitialized == 0)
			Set rLinkA to GetLinkedRef;
			Set rLinkB to rLinkA.GetLinkedRef;
			Set bInitialized to 1;
		Else
			If (bRetreat == 1)																									; After starting combat, the tunneler runs for a digging hole and emerges elsewhere.
				If (bTeleport == 0)
					If (GetActorValue Variable04 == 1)																		; Running this off a character variable so we can use this in a generic form. Also need to have it set our variable since the value gets cleared when we disable.
						Set bTeleport to 1;
						SetActorValue Variable04 0;
						Return;
					Endif
				Else 																												; Only run the main gamemode block once we're ready		
					If ( nStage == 0 )
						If (nCount == 0)
							SetGhost 1;																							; Make the tunneler immune to death.
							SetActorValue Aggression 0;
							RemoveFromFaction NVDLC04TunnelerFaction;
							StopCombat Player;
							Set nCount to 1;
							Return;
						Elseif (nCount == 1)
							PlayIdle Burrow;
							Set nCount to 2;
							Return;
						Elseif (nCount == 2)
							If (GetAnimAction == 13)																			; Go to next Stage
								Set nStage to 1;
								Set nCount to 0;
								Return;
							Else
								Set ncount to 1;
								Return;
							Endif
						Endif
					ElseIf ( nStage == 1 )
						If (nCount == 0)
							If (GetAnimAction != 13)																			; Have a time delay before disabling actor, and effects. This is handled by the end of the script-forced animation called above.
								SetActorAlpha 0;																				; Disable the tunneler.
								Disable;
								Set nCount to 1;
								Return;
							EndIf
						Elseif (nCount == 1)
							Set nCount to 0;
							Set nStage to 2;
							Return;
						Endif
					ElseIf ( nStage == 2 )
						If (nCount == 0)
							If (fTimer >= 4)																						; Have a time delay before moving the disabled tunneler to its emerge spot.
								MoveTo rLinkB;
								Set nCount to 1;
								Return;
							Else
								Set fTimer to (fTimer + GetSecondsPassed);
								Return;				
							EndIf
						Elseif (nCount == 1)
							Enable; 																								; Enable the tunneler.
							Set fTimer to 0;
							Set nCount to 0;
							Set nStage to 3;
						Endif
					ElseIf ( nStage == 3 )
						If (nCount == 0) 																						; Play Emerge Animation
							;AddScriptPackage NVDLC04TunnelerEmergePackage;
							PlayIdle Emerge;
							SetActorAlpha 0;
							Set nCount to 1;
							Return;
						Elseif (nCount == 1)
							If (GetAnimAction == 13)
								SetActorAlpha 1;
								Set nCount to 0;
								Set nStage to 4;
								Return;
							Else
								Set nCount to 0;
								Return;
							Endif
						Endif
					ElseIf ( nStage == 4 )
						If (nCount == 0)
							SetGhost 0;
							Set nCount to 1;
							Return;
						Elseif (nCount == 1)
							If (GetAnimAction != 13)
								SetActorValue Aggression 2;
								AddToFaction NVDLC04TunnelerFaction 1;
								RemoveScriptPackage;
								Set nCount to 0;			
								Set nStage to 0;
								Set bTeleport to 0;																				; We're done here. Now the script will begin checking again the Variable04 conditional.
							EndIf
						EndIf
					EndIf
				Endif
			Endif
		Endif
	Endif

END ; GameMode

BEGIN OnDeath

	If ( IsKiller Player == 0 ) && ( IsKiller NVDLC04EDEREF== 0 ) && ( IsKiller NVDLC04UlyssesREF == 0 )
		KillActor Player 0;
	EndIf

END

BEGIN OnLoad

	; This initializes our variables and only runs once.
	If (bInitialized == 0)

		Set rLinkA to GetLinkedRef;
		Set rLinkB to rLinkA.GetLinkedRef;		
		Set bInitialized to 1;

	EndIf

END ; OnLoad


BEGIN OnStartCombat Player

	; When the tunneler first starts combat with the player, initiate his sneak attack sequence.
	If ( bRetreat == 0 ) && ( GetActorValue Confidence != 0 )

		Set bRetreat to 1;
		AddScriptPackage NVDLC04TunnelerFleePackage;

	EndIf

END


BEGIN GameMode

	If ( GetDead == 1 )
		Return;
	EndIf

	; Running this off a character variable so we can use this in a generic form. Also need to have it set our variable since the value gets cleared when we disable.
	If ( GetActorValue Variable04 == 1 )

		Set bTeleport to 1;
		SetActorValue Variable04 0;

	EndIf

	If ( bTeleport == 1 )		;Only run the main gamemode block once we're ready		

		If ( nStage == 0 )

			;Make the NPC Unkillable
			;SetActorRefEssential 1;	
			SetGhost 1;
			SetActorValue Aggression 0;
			RemoveFromFaction NVDLC04TunnelerFaction;
			StopCombat Player;	

			; Play Effects
			; Set rEffectA to rLinkA.PlaceAtMe DLC04ShovelDirtExplosion 1;

			; Play Submerge Animation
			; PlayIdle Burrow;
			AddScriptPackage NVDLC04TunnelerSubmergePackage;

			Set fTimer to 2.0;
			; Go to next Stage
			Set nStage to 1;

		ElseIf ( nStage == 1 )

			; Have a time delay
			If ( fTimer > 0 )
				Set fTimer to fTimer - GetSecondsPassed;
			Else
				; Disable the NPC & Effects
				SetActorAlpha 0;
				; Disable;
				; rEffectA.Disable;
				; rEffectA.MarkForDelete;

				; Set the next time duration (for how long he stays tunneling)
				Set fTimer to 4.0;

				; Go to next Stage
				Set nStage to 2;

			EndIf

		ElseIf ( nStage == 2 )

			; Have a time delay
			If ( fTimer > 0 )

				Set fTimer to fTimer - GetSecondsPassed;

			Else

				MoveTo rLinkB;

				; Play Effects
				; Set rEffectB to rLinkB.PlaceAtMe DLC04ShovelDirtExplosion 1;

				; Enable the NPC
				SetActorAlpha 1;
				; Enable;

				; Set the next time duration
				Set fTimer to 3.0;

				; Go to next Stage
				Set nStage to 3;

			EndIf

		ElseIf ( nStage == 3 )

			; Play Emerge Animation
			AddScriptPackage NVDLC04TunnelerEmergePackage;
			; PlayIdle Emerge;

			; Set the next time duration
			Set fTimer to 4.0;

			; Go to next Stage
			Set nStage to 4;

		ElseIf ( nStage == 4 )

			; Have a time delay
			If (fTimer > 0)

				Set fTimer to fTimer - GetSecondsPassed;

			Else

				; Disable the Effects
				; rEffectB.Disable;
				; rEffectB.MarkForDelete;

				;Make the NPC killable
				;SetActorRefEssential 0;	
				SetGhost 0;
				SetActorValue Aggression 2;
				AddToFaction NVDLC04TunnelerFaction 1;
				RemoveScriptPackage;

				; Clear variables
				Set nStage to 0;

				;We're done here, set this gamemode block to not run.
				Set bTeleport to 0;

			EndIf

		EndIf

	EndIf

END ; GameMode

scn NVDLC04Road02AmbushTriggerSCRIPT


; Enables trailer Deathclaw.
; JSH 06/20/11


BEGIN OnTrigger Player

	PlaySound NPCDeathClawScriptLand;
	NVDLC04Road02OverpassDeathclawREF.Enable;
	Disable;
