Scn	NVDLC04TunnelCodeSupportSCRIPT

; This script controls the scripted scenes in Collapsed Overpass Tunnel. It will handle several scenes, and creature spawning, accounted by the var nScene.

; J.S. 3/11/11


Short	bPlayerInterference	; 1 = Player has carried out some action that would normally disrupt the flow of the scene in question.

									; These vars are still not used - they are here for support purpose, in case they become necessary during Alpha polishing.
Short	bFirstWave				; 1 = First wave of tunnelers has spawned.
Short	bSecondWave			; 1 = Second wave of tunnelers has spawned.
Short	bThirdWave				; 1 = Third wave of tunnelers has spawned.

Int		nScene					; 1 - 5 = Large collection of events, each is discretely considered a "scene."
Int		nCount					; Operational integer.
Int		nState					; Operational integer - defines larger segments than nCount.
Float	fTimer					; Timer Float.


BEGIN GameMode

	If (GetDisabled == 1)
		Return;
	Else
		If (NVDLC04TunnelBox.nScene < 4)
			Return;
		Else
			If (nScene == 4)																												; In this scene (4), we don't worry about timing any special reveals. The reveals have already been done above.
				If (NVDLC04TunnelBox.bFinale == 1)																					; This var is one once players cross the finale trigger, which puts them between three burrowing holes (gives chance to plant mines in one).
					Set fTimer to (fTimer + GetSecondsPassed);
					If (nState == 0)																											; For nState 0, we simply prepare the scene, by playing a familiar (by now), scare sound, 2 secs after the finale trigger is crossed.
						If (nCount == 0)
							If (fTimer >= 2)
								NVDLC04TunnelTunnelerWave02BruteREF.PlaySound NPCSporeCarrierScream;
								Set nState to 1;
								Return;
							Endif
						Endif
					Elseif (nState == 1)																									; As in scene 3, we simply proceed to enable, play idle, run timer, set Ghost 0/aggression 2, and enable the next tunneler in line.
						If (nCount == 0)																										; This sequence repeats with three tunnelers, which make wave02, and then starts scene 5, which spawns wave03 (6 tunnelers total).
							NVDLC04TunnelTunnelerWave02BruteREF.Enable;
							Set nCount to 1;
							Return;
						Elseif (nCount == 1)
							NVDLC04TunnelTunnelerWave02BruteREF.PlayIdle Emerge;
							NVDLC04TunnelTunnelerWave02BruteREF.SAA 0;
							Set nCount to 2;
							Return;
						Elseif (nCount == 2)
							If (NVDLC04TunnelTunnelerWave02BruteREF.GetAnimAction == 13)
								NVDLC04TunnelTunnelerWave02BruteREF.SAA 1;
								NVDLC04TunnelTunnelerWave02BruteREF.SetGhost 0;
								Set fTimer to 0;
								Set nCount to 3;
								Return;
							Else
								Set nCount to 1;
								Return;
							Endif
						Elseif (nCount == 3)
							If (fTimer >= 1)
								NVDLC04TunnelTunnelerWave02AmbREF.Enable;
								NVDLC04TunnelTunnelerWave02BruteREF.SetAV Aggression 2;
								Set nCount to 4;
								Return;
							Endif
						Elseif (nCount == 4)
							NVDLC04TunnelTunnelerWave02AmbREF.PlayIdle Emerge;
							NVDLC04TunnelTunnelerWave02AmbREF.SAA 0;
							Set fTimer to 0;
							Set nCount to 5;
							Return;
						Elseif (nCount == 5)
							If (NVDLC04TunnelTunnelerWave02AmbREF.GetAnimAction == 13)
								NVDLC04TunnelTunnelerWave02AmbREF.SAA 1;
								NVDLC04TunnelTunnelerWave02AmbREF.SetGhost 0;
								Set fTimer to 0;
								Set nCount to 6;
								Return;
							Else
								Set nCount to 4;
								Return;
							Endif
						Elseif (nCount == 6)
							If (fTimer >= 1)
								NVDLC04TunnelTunnelerWave02VenREF.Enable;
								NVDLC04TunnelTunnelerWave02AmbREF.SetAV Aggression 2;
								NVDLC04TunnelTunnelerWave02AmbREF.Playsound NPCSporeCarrierAimVox;
								Set nCount to 7;
								Return;
							Endif
						Elseif (nCount == 7)
							NVDLC04TunnelTunnelerWave02VenREF.PlayIdle Emerge;
							NVDLC04TunnelTunnelerWave02VenREF.SAA 0;
							Set nCount to 8;
							Return;
						Elseif (nCount == 8)
							If (NVDLC04TunnelTunnelerWave02VenREF.GetAnimAction == 13)
								NVDLC04TunnelTunnelerWave02VenREF.SAA 1;
								NVDLC04TunnelTunnelerWave02VenREF.SetGhost 0;
								Set fTimer to 0;
								Set fTimer to 0;
								Set nCount to 9;
								Return;
							Else
								Set nCount to 7;
								Return;
							Endif
						Elseif (nCount == 9)
							If (fTimer >= 1)
								NVDLC04TunnelTunnelerWave02VenREF.SetAV Aggression 2;
								Set nCount to 0;
								Set fTimer to 0;
								Set nState to 2;																								; We reset vars, and move to nState 2, where we prepare the next tunnelers.
								Return;
							Endif
						Endif
					Elseif (nState == 2)																									; Here, we prepare the next wave of tunnelers, wave03, which is to spawn immediately after wave02.
						NVDLC04TunnelTunnelerWave03VenREF.SetGhost 1;
						NVDLC04TunnelTunnelerWave03VenREF.SetAV Aggression 0;
						NVDLC04TunnelTunnelerWave03AmbREF.SetGhost 1;
						NVDLC04TunnelTunnelerWave03AmbREF.SetAV Aggression 0;
						NVDLC04TunnelTunnelerWave03BruteREF.SetGhost 1;
						NVDLC04TunnelTunnelerWave03BruteREF.SetAV Aggression 0;
						Set nState to 0;
						Set nScene to 5;																									; We move the scene to 5.
						Return;
					Endif
				Endif
			Elseif (nScene == 5)																											; This scene is identical to scene 4, but spawning wave03 tunnelers instead.
				If (NVDLC04TunnelBox.bFinale == 1)
					Set fTimer to (fTimer + GetSecondsPassed);
					If (nState == 0)
						If (nCount == 0)
							If (NVDLC04TunnelTunnelerWave02BruteREF.GetDead == 1 && (NVDLC04TunnelTunnelerWave02AmbREF.GetDead == 1 || NVDLC04TunnelTunnelerWave02VenREF.GetDead == 1))
								NVDLC04TunnelTunnelerWave03BruteREF.PlaySound NPCSporeCarrierScream;
								Set nState to 1;
								Return;
							Elseif (NVDLC04TunnelTunnelerWave02AmbREF.GetDead == 1 && (NVDLC04TunnelTunnelerWave02BruteREF.GetDead == 1 || NVDLC04TunnelTunnelerWave02VenREF.GetDead == 1))
								NVDLC04TunnelTunnelerWave03BruteREF.PlaySound NPCSporeCarrierScream;
								Set nState to 1;
								Return;
							Elseif (NVDLC04TunnelTunnelerWave02VenREF.GetDead == 1 && (NVDLC04TunnelTunnelerWave02BruteREF.GetDead == 1 || NVDLC04TunnelTunnelerWave02AmbREF.GetDead == 1))
								NVDLC04TunnelTunnelerWave03BruteREF.PlaySound NPCSporeCarrierScream;
								Set nState to 1;
								Return;
							Endif
						Endif
					Elseif (nState == 1)
						If (nCount == 0)																										; This sequence repeats with three tunnelers, which make wave02, and then starts scene 5, which spawns wave03 (6 tunnelers total).
							NVDLC04TunnelTunnelerWave03BruteREF.Enable;
							Set nCount to 1;
							Return;
						Elseif (nCount == 1)
							NVDLC04TunnelTunnelerWave03BruteREF.PlayIdle Emerge;
							NVDLC04TunnelTunnelerWave03BruteREF.SAA 0;
							Set nCount to 2;
							Return;
						Elseif (nCount == 2)
							If (NVDLC04TunnelTunnelerWave03BruteREF.GetAnimAction == 13)
								NVDLC04TunnelTunnelerWave03BruteREF.SAA 1;
								NVDLC04TunnelTunnelerWave03BruteREF.SetGhost 0;
								Set fTimer to 0;
								Set nCount to 3;
								Return;
							Else
								Set nCount to 1;
								Return;
							Endif
						Elseif (nCount == 3)
							If (fTimer >= 1)
								NVDLC04TunnelTunnelerWave03AmbREF.Enable;
								NVDLC04TunnelTunnelerWave03BruteREF.SetAV Aggression 2;
								Set nCount to 4;
								Return;
							Endif
						Elseif (nCount == 4)
							NVDLC04TunnelTunnelerWave03AmbREF.PlayIdle Emerge;
							NVDLC04TunnelTunnelerWave03AmbREF.SAA 0;
							Set fTimer to 0;
							Set nCount to 5;
							Return;
						Elseif (nCount == 5)
							If (NVDLC04TunnelTunnelerWave03AmbREF.GetAnimAction == 13)
								NVDLC04TunnelTunnelerWave03AmbREF.SAA 1;
								NVDLC04TunnelTunnelerWave03AmbREF.SetGhost 0;
								Set fTimer to 0;
								Set nCount to 6;
								Return;
							Else
								Set nCount to 4;
								Return;
							Endif
						Elseif (nCount == 6)
							If (fTimer >= 1)
								NVDLC04TunnelTunnelerWave03VenREF.Enable;
								NVDLC04TunnelTunnelerWave03AmbREF.SetAV Aggression 2;
								NVDLC04TunnelTunnelerWave03AmbREF.Playsound NPCSporeCarrierAimVox;
								Set nCount to 7;
								Return;
							Endif
						Elseif (nCount == 7)
							NVDLC04TunnelTunnelerWave03VenREF.PlayIdle Emerge;
							NVDLC04TunnelTunnelerWave02VenREF.SAA 0;
							Set nCount to 8;
							Return;
						Elseif (nCount == 8)
							If (NVDLC04TunnelTunnelerWave03VenREF.GetAnimAction == 13)
								NVDLC04TunnelTunnelerWave03VenREF.SAA 1;
								NVDLC04TunnelTunnelerWave03VenREF.SetGhost 0;
								Set fTimer to 0;
								Set fTimer to 0;
								Set nCount to 9;
								Return;
							Else
								Set nCount to 7;
								Return;
							Endif
						Elseif (nCount == 9)
							If (fTimer >= 1)
								NVDLC04TunnelTunnelerWave03VenREF.SetAV Aggression 2;
								Set nCount to 0;
								Set fTimer to 0;
								Set nState to 2;																								; We reset vars, and move to nState 2, where we prepare the next tunnelers.
								Return;
							Endif
						Endif
					Elseif (nState == 2)
						Set fTimer to 0;
						Set nState to 0;
						Set nScene to 6;																									; In case that we want to run a sixth scene, or prepare the level for when players return to it after beating DLC04.
						Set NVDLC04TunnelBox.nScene to 6
						Set NVDLC04TunnelBox.fRespawnTime to (GameDaysPassed + 3)
						Set NVDLC04TunnelBox.bRespawn to 1;
						Disable;
						MarkForDelete;
						Return;
					Endif
				Endif
			Endif
		Endif
	Endif

END

