scn NVDLC04VenomousTunnelerAmbusherSCRIPT


; Duplicate of script NVDLC04TunnelerAmbusherSCRIPT, but with a modified death for Venomous Tunnelers
; CES 5/31/11
; CES 7/13/11 - Added set restrained and resets on fTimer to get the jitter out of the burrow animations.


Ref rEffectA;					; Reference for the effect that play at location A
Ref rEffectB;				; Reference for the effect that play at location B
Ref rLinkA;					; Reference for location A (Digging Hole)
Ref rLinkB;					; Reference for location B (Emergence Hole)

Short bTeleport;			; Variable to track the teleportation process
Short nStage;				; Tracks which stage of the process we are in
								; Stage 0 - Initial effects and animation at dig hole.
								; Stage 1 - Delay for effects, then disable NPC and Effects.
								; Stage 2 - Delay for tunneling time, then effects and animation at emergence hole.
								; Stage 3 - Delay for effects, then disable effects and cleanup.
Short	nCount				;

Short bInitialized;			; Tracks initializing settings

Short bRetreat;				; Tracks whether the tunneler has tried to flee after taking too much damage.

Float fTimer;				; Timer for delays.

Short bShaderLoaded	; 1 = Fancy shader FX loaded.


BEGIN OnDeath

	StopMagicShaderVisuals GlowingOneFXShader;

	PlaceAtMe NVDLC04TunnelerPoisonExplosion;
	;CastImmediateOnSelf NVDLC04TunnelerPoisonDeathAttack;

	If ( IsKiller Player == 0 ) && ( IsKiller NVDLC04EDEREF== 0 ) && ( IsKiller NVDLC04UlyssesREF == 0 )
		KillActor Player 0;
	EndIf

END

BEGIN OnLoad

	; This initializes our variables and only runs once.
	If (bInitialized == 0)

		Set rLinkA to GetLinkedRef;
		Set rLinkB to rLinkA.GetLinkedRef;		
		Set bInitialized to 1;

	EndIf

END ; OnLoad


BEGIN OnStartCombat Player

	; When the tunneler first starts combat with the player, initiate his sneak attack sequence.
	If ( bRetreat == 0 ) && ( GetActorValue Confidence != 0 )

		Set bRetreat to 1;
		AddScriptPackage NVDLC04TunnelerFleePackage;

	EndIf

END


BEGIN GameMode

	If ( GetDead == 1 )
		Return;
	Else
		If (bInitialized == 0)
			Set rLinkA to GetLinkedRef;
			Set rLinkB to rLinkA.GetLinkedRef;
			Set bInitialized to 1;
		Else
			If (bRetreat == 1)																									; After starting combat, the tunneler runs for a digging hole and emerges elsewhere.
				If (bTeleport == 0)
					If (GetActorValue Variable04 == 1)																		; Running this off a character variable so we can use this in a generic form. Also need to have it set our variable since the value gets cleared when we disable.
						Set bTeleport to 1;
						SetActorValue Variable04 0;
						Return;
					Endif
				Else 																												; Only run the main gamemode block once we're ready		
					If ( nStage == 0 )
						If (nCount == 0)
							SetRestrained 1;
							SetGhost 1;																							; Make the tunneler immune to death.
							SetActorValue Aggression 0;
							RemoveFromFaction NVDLC04TunnelerFaction;
							StopCombat Player;
							Set nCount to 1;
							Return;
						Elseif (nCount == 1)
							RemoveScriptPackage;
							PlayIdle Burrow;
							Set nCount to 2;
							Return;
						Elseif (nCount == 2)
							If (GetAnimAction == 13)																			; Go to next Stage
								Set nStage to 1;
								Set nCount to 0;
								Return;
							Else
								Set nCount to 1;
								Return;
							Endif
						Endif
					ElseIf ( nStage == 1 )
						If (nCount == 0)
							Set fTimer to (fTimer + GetSecondsPassed);
							If (fTimer >= 1.3)																					; Have a time delay before disabling actor, and effects. This is handled by the end of the script-forced animation called above.
								SetActorAlpha 0;																				; Disable the tunneler.
								Disable;
								Set fTimer to 0;
								Set nCount to 1;
								Return;
							EndIf
						Elseif (nCount == 1)
							Set nCount to 0;
							Set nStage to 2;
							Return;
						Endif
					ElseIf ( nStage == 2 )
						If (nCount == 0)
							If (fTimer >= 4)																						; Have a time delay before moving the disabled tunneler to its emerge spot.
								MoveTo rLinkB;
								Set nCount to 1;
								Return;
							Else
								Set fTimer to (fTimer + GetSecondsPassed);
								Return;				
							EndIf
						Elseif (nCount == 1)
							Enable; 																								; Enable the tunneler.
							Set fTimer to 0;
							Set nCount to 0;
							Set nStage to 3;
						Endif
					ElseIf ( nStage == 3 )
						If (nCount == 0) 																						; Play Emerge Animation
							;AddScriptPackage NVDLC04TunnelerEmergePackage;
							SetRestrained 0;
							RemoveScriptPackage;
							PlayIdle Emerge;
							SetActorAlpha 0;
							Set nCount to 1;
							Return;
						Elseif (nCount == 1)
							If (GetAnimAction == 13)
								SetActorAlpha 1;
								Set nCount to 0;
								Set nStage to 4;
								Return;
							Else
								Set nCount to 0;
								Return;
							Endif
						Endif
					ElseIf ( nStage == 4 )
						If (nCount == 0)
							SetGhost 0;
							Set nCount to 1;
							Return;
						Elseif (nCount == 1)
							Set fTimer to (fTimer + GetSecondsPassed);
							If (fTimer >= 2.3)
								AddToFaction NVDLC04TunnelerFaction 1;
								SetActorValue Aggression 2;
								RemoveScriptPackage;
								Set fTimer to 0;
								Set nCount to 0;			
								Set nStage to 0;
								Set bTeleport to 0;																				; We're done here. Now the script will begin checking again the Variable04 conditional.
							EndIf
						EndIf
					EndIf
				Endif
			Endif
		Endif
	Endif

END ; GameMode

scn NVDLC04TunnelerPoisonDeathScript
; This script controls the death explosion for the venomous tunnelers. ; CES 05/31/11
BEGIN ScriptEffectStart
; Make spore carrier explode! PlaceAtMe NVDLC04TunnelerPoisonExplosion;
Kill Player 0;
END

SCN NVDLC04UlyssesTrigger4SCRIPT


; Script for Ulysses' final greeting through ED-E.
; TLS 05/20/2011
; TLS 06/02/2011 - Moved ED-E's removal from factions to OnTriggerEnter to stop him from detecting enemies and breaking the scene.
; TLS 06/06/2011 - Enabling Divide bad guys here to stop ED-E from chasing them.
; DLM 6/14/11 - Moved the enabling of the Marked Men to a different area of the Divide.


Short bUlysses;
Float fTimer;
Short bRunTimer;
Int		iStage;								;Stage number for the timer
Short	bDoOnce;							;Makes sure the trigger only goes off once
Short	bEDEFlyAway;						;Starts timer
Float	fTimerEDEFlyAway;				;timer to disable the fly away version of Ed-E



BEGIN OnTriggerEnter Player

	If (bUlysses == 0)
		NVDLC04EDEREF.MoveTo NVDLC04EDEUlysses4MarkerREF;
		NVDLC04EDEREF.AddScriptPackage NVDLC04EDEUlyssesCutIn04;
		Set bUlysses to 1;
		; Take ED-E away.
		Set NVDLC04DialogueEDE.bEDEHired to 0;
		Set NVDLC04DialogueEDE.bEDEFired to 1;
		; Remove companion perk
		NVDLC04EDEREF.RemovePerk CompanionSuite;

		; Clean up companion factions.
		NVDLC04EDEREF.RemoveFromFaction FollowerFaction;
		NVDLC04EDEREF.SetPlayerTeammate 0;

		; Set ED-E's Aggression to Unaggressive
		NVDLC04EDEREF.SetActorValue Aggression 0;
	EndIf

END


BEGIN GameMode

	If (bRunTimer == 1)
		Set fTimer to (fTimer + GetSecondsPassed)
		If (fTimer >= 0.25) && (NVDLC04EDEREF.GetDisabled == 0)
			Set NVDLC04EDEREF.bFlyAway to 0;
			NVDLC04EDEREF.SetGhost 0;

			;ShowMessage NVDLC04EDEGoneMessage;
			NVDLC04EDEREF.RemoveItem NVDLC04EDEWeapons 1;
			NVDLC04EDEREF.RemoveAllItems Player;
			NVDLC04EDEREF.AddItem NVDLC04EDEWeapons 1;

			; Move ED-E
			NVDLC04EDEREF.MoveTo NVDLC04EDECaptiveMarkerREF;

			; Restrain ED-E
			NVDLC04EDEREF.SetRestrained 1
			NVDLC04EDEREF.Disable;
			EDEFlyAwayREF.Enable;
			Player.RemoveScriptPackage NVDLC04PlayerNoSavePackage;
			;DivideWestMarkedMenSnipers01.Enable;
			;DivideWestDeathclaws.Enable;
		ElseIf (fTimer >= 1) && (bDoOnce == 0)
			SetStage NVDLC04MQ02a 100;
			Player.RemovePerk NVDLC04EnhancedSensors;
			ShowMessage NVDLC04EDEGoneMessage;
			ShowMessage FollowerMessagePerkEDERemove;
			Set bEDEFlyAway to 1;
			EDEFlyAwayREF.PlayGroup Forward 1;
			PlaySound NVDLC04QSTEdeFlyAway;
			Set bDoOnce to 1;
			NVDLC04EDEREF.RemoveItem NVDLC04EDEWeapons 999;
			NVDLC04EDEREF.RemoveAllItems Player;
			NVDLC04EDEREF.AddItem NVDLC04EDEWeapons 1;
			EnablePlayerControls;
		EndIf
	EndIf

	If (bEDEFlyAway == 1) && (fTimerEDEFlyAway >=6.5) && (iStage == 0)
		DivideFlareMarker07.FireWeapon NVDLC04WeapFlareGun;
		PlaySound NVDLC04WPNFlareGunFire3DDist;
		Set iStage to 1;
	ElseIf (bEDEFlyAway == 1) && (fTimerEDEFlyAway >=7) && (iStage == 1)
		DivideFlareMarker08.FireWeapon NVDLC04WeapFlareGun;
		PlaySound NVDLC04WPNFlareGunFire3DDist;
		Set iStage to 2;
	ElseIf (bEDEFlyAway == 1) && (fTimerEDEFlyAway >=7.5) && (iStage ==2)
		EDEFlyAwayREF.disable;
		Set iStage to 3;
	ElseIf (bEDEFlyAway == 1) && (fTimerEDEFlyAway >=8) && (iStage == 3)
		Disable;
		MarkForDelete;
	ElseIf (bEDEFlyAway == 1) && (fTimerEDEFlyAway < 9)
		Set fTimerEDEFlyAway to (fTimerEDEFlyAway + GetSecondsPassed)
	EndIf

END

Scn	NVDLC04Road01MMCamp01DetonatorOnTriggerSCRIPT

; This script runs on triggers, which turn a codebox var (NVDLC04Road01Camp01Box) to 1, signaling the okay to start ambush/flare scene.

; J.S. 5/24/11

Short	bDoOnce


BEGIN OnTriggerEnter Player

	If (NVDLC04Road01Camp01Box.bDetonator == 0)
		If (Player.GetItemCount NVDLC04WeapSonicDetonator > 0)	
			Set NVDLC04Road01Camp01Box.bDetonator to 1;
			Disable;
			MarkForDelete;
		Endif
	Else
		Disable;
		MarkForDelete;
	Endif

END

scn NVDLC04TempleMarkedMenQuestSCRIPT


; Script to handle spawning of the Marked Men during the Temple battle. Moved from NVDLC04UlyssesBattleCodeboxSCRIPT.
; JSH 05/24/11
; DLM 6/21/11 - Added a check for Ulysses being dead to spawn the marked men (in the event of a 1 hit kill on him)

Short bStartSpawn;						; 1 = Spawning of Wave 1 Marked Men has started.

Short iMarkedMenSpawnOnce02;
Short iMarkedMenSpawnR01;
Short iMarkedMenSpawnL02;
Short iDoOnceMarkedMenR01;
Short iDoOnceMarkedMenL02;
Short iDoOnceMarkedMenRU01;
Short iDoOnceMarkedMenLU02;
Short iMarkedMenSpawnRU01;
Short iMarkedMenSpawnLU02;

BEGIN GameMode

	If ( bStartSpawn == 0)																																; Marked Men spawn when Ulysses is at 75% health or dead
		If (NVDLC04UlyssesREF.GetDead == 1)	&& (UlyssesBattleCodebox.bUlyssesIsFriend == 0)
			Set iMarkedMenSpawnR01 to 1;
			Set iMarkedMenSpawnL02 to 1;
			Set bStartSpawn to 1;

		ElseIf (NVDLC04UlyssesREF.GetHealthPercentage <= 0.75) && (UlyssesBattleCodebox.bUlyssesIsFriend == 0)
			Set iMarkedMenSpawnR01 to 1;
			Set iMarkedMenSpawnL02 to 1;
			Set NVDLC04UlyssesCombatBarks.bCombatMarkedMenEnter to 1;
			Set bStartSpawn to 1;
		EndIf
	EndIf

	If (iMarkedMenSpawnR01 == 1) && (iDoOnceMarkedMenR01 == 0)																	;Indicates when the Marked Men can spawn
		If ( UlyssesBattleCodebox.bWeakPlayer == 1 )
			TempleBattleMMR01WeakREF.MoveTo TempleMMSpawn01;
			TempleBattleMMR01WeakREF.Enable;
		Else
			TempleBattleMMR01.MoveTo TempleMMSpawn01;
			TempleBattleMMR01.Enable;
		EndIf
		Set iDoOnceMarkedMenR01 to 1;
	EndIf

	If (iMarkedMenSpawnR01 == 1) && (iDoOnceMarkedMenR01 == 1) && ( (TempleBattleMMR01.GetDead == 1) || (TempleBattleMMR01WeakREF.GetDead == 1) )
		If ( UlyssesBattleCodebox.bWeakPlayer == 1 )
			TempleBattleMMR02WeakREF.MoveTo TempleMMSpawn01;
			TempleBattleMMR02WeakREF.Enable;
		Else
			TempleBattleMMR02.MoveTo TempleMMSpawn01;
			TempleBattleMMR02.Enable;
		EndIf
		Set UlyssesBattleCodebox.iMarkedMenDeadCount to (UlyssesBattleCodebox.iMarkedMenDeadCount +1);
		Set iDoOnceMarkedMenR01 to 2;
	EndIf

	If (iMarkedMenSpawnR01 == 1) && (iDoOnceMarkedMenR01 == 2) && ( (TempleBattleMMR02.GetDead == 1) || (TempleBattleMMR02WeakREF.GetDead == 1) )
		If ( UlyssesBattleCodebox.bWeakPlayer == 1 )
			TempleBattleMMR03WeakREF.MoveTo TempleMMSpawn01;
			TempleBattleMMR03WeakREF.Enable;
		Else
			TempleBattleMMR03.MoveTo TempleMMSpawn01;
			TempleBattleMMR03.Enable;
		EndIf
		Set UlyssesBattleCodebox.iMarkedMenDeadCount to (UlyssesBattleCodebox.iMarkedMenDeadCount +1);
		Set iDoOnceMarkedMenR01 to 3;
	EndIf

	If (iMarkedMenSpawnR01 == 1) && (iDoOnceMarkedMenR01 == 3) && ( (TempleBattleMMR03.GetDead == 1) || (TempleBattleMMR03WeakREF.GetDead == 1) )
		If ( UlyssesBattleCodebox.bWeakPlayer == 1 )
			TempleBattleMMR04WeakREF.MoveTo TempleMMSpawn01;
			TempleBattleMMR04WeakREF.Enable;
		Else
			TempleBattleMMR04.MoveTo TempleMMSpawn01;
			TempleBattleMMR04.Enable;
		EndIf
		Set UlyssesBattleCodebox.iMarkedMenDeadCount to (UlyssesBattleCodebox.iMarkedMenDeadCount +1);
		Set iDoOnceMarkedMenR01 to 4;
	EndIf

	If (iMarkedMenSpawnR01 == 1) && (iDoOnceMarkedMenR01 == 4) && ( (TempleBattleMMR04.GetDead == 1) || (TempleBattleMMR04WeakREF.GetDead == 1) )
		Set UlyssesBattleCodebox.iMarkedMenDeadCount to (UlyssesBattleCodebox.iMarkedMenDeadCount +1);
		Set iDoOnceMarkedMenR01 to 5;
	EndIf

	If (iMarkedMenSpawnL02 == 1) && (iDoOnceMarkedMenL02 == 0)
		If ( UlyssesBattleCodebox.bWeakPlayer == 1 )
			TempleBattleMML01WeakREF.MoveTo TempleMMSpawn02;
			TempleBattleMML01WeakREF.Enable;
		Else
			TempleBattleMML01.MoveTo TempleMMSpawn02;
			TempleBattleMML01.Enable;
		EndIf
		Set iDoOnceMarkedMenL02 to 1;
	EndIf

	If (iMarkedMenSpawnL02 == 1) && (iDoOnceMarkedMenL02 == 1) && ( (TempleBattleMML01.GetDead == 1) ||  (TempleBattleMML01WeakREF.GetDead == 1) )
		If ( UlyssesBattleCodebox.bWeakPlayer == 1 )
			TempleBattleMML02WeakREF.MoveTo TempleMMSpawn02;
			TempleBattleMML02WeakREF.Enable;
		Else
			TempleBattleMML02.MoveTo TempleMMSpawn02;
			TempleBattleMML02.Enable;
		EndIf
		Set UlyssesBattleCodebox.iMarkedMenDeadCount to (UlyssesBattleCodebox.iMarkedMenDeadCount +1);
		Set iDoOnceMarkedMenL02 to 2;
	EndIf

	If (iMarkedMenSpawnL02 == 1) && (iDoOnceMarkedMenL02 == 2) && ( (TempleBattleMML02.GetDead == 1) || (TempleBattleMML02WeakREF.GetDead == 1) )
		If ( UlyssesBattleCodebox.bWeakPlayer == 1 )
			TempleBattleMML03WeakREF.MoveTo TempleMMSpawn02;
			TempleBattleMML03WeakREF.Enable;
		Else
			TempleBattleMML03.MoveTo TempleMMSpawn02;
			TempleBattleMML03.Enable;
		EndIf
		Set UlyssesBattleCodebox.iMarkedMenDeadCount to (UlyssesBattleCodebox.iMarkedMenDeadCount +1);
		Set iDoOnceMarkedMenL02 to 3;
	EndIf

	If (iMarkedMenSpawnL02 == 1) && (iDoOnceMarkedMenL02 == 3) && ( (TempleBattleMML03.GetDead == 1) || (TempleBattleMML03WeakREF.GetDead == 1) )
		If ( UlyssesBattleCodebox.bWeakPlayer == 1 )
			TempleBattleMML04WeakREF.MoveTo TempleMMSpawn02;
			TempleBattleMML04WeakREF.Enable;
		Else
			TempleBattleMML04.MoveTo TempleMMSpawn02;
			TempleBattleMML04.Enable;
		EndIf
		Set UlyssesBattleCodebox.iMarkedMenDeadCount to (UlyssesBattleCodebox.iMarkedMenDeadCount +1);
		Set iDoOnceMarkedMenL02 to 4;
	EndIf

	If (iMarkedMenSpawnL02 == 1) && (iDoOnceMarkedMenL02 == 4) && ( (TempleBattleMML04.GetDead == 1) ||  (TempleBattleMML04WeakREF.GetDead == 1) )
		Set UlyssesBattleCodebox.iMarkedMenDeadCount to (UlyssesBattleCodebox.iMarkedMenDeadCount +1);
		Set iDoOnceMarkedMenL02 to 5;
	EndIf


;;;;;Marked Men throwing satchel charges
	If (UlyssesBattleCodebox.bMarkedMenInTrigger == 1) && (UlyssesBattleCodebox.bThrowChargeOnce == 0) && (UlyssesBattleCodebox.fChargeTimer >= 3)
		Set UlyssesBattleCodebox.iThrowCharge to (UlyssesBattleCodebox.iThrowCharge +1);
		If ( UlyssesBattleCodebox.bWeakPlayer == 1 )
			TempleBattleMMB01WeakREF.EvaluatePackage;
		Else
			TempleBattleMMB01.EvaluatePackage;
		EndIf
		Set UlyssesBattleCodebox.bThrowChargeOnce to 1;
	ElseIf (UlyssesBattleCodebox.bMarkedMenInTrigger == 1) && (UlyssesBattleCodebox.bThrowChargeOnce == 0) && (UlyssesBattleCodebox.fChargeTimer < 3)
		Set UlyssesBattleCodebox.fChargeTimer to (UlyssesBattleCodebox.fChargeTimer + GetSecondsPassed);
	EndIf	

	If (TempleBattleMMB01.GetDead == 1) || (TempleBattleMMB01WeakREF.GetDead == 1)
		Set UlyssesBattleCodebox.ithrowCharge to 1;
	EndIf	


;;;;;Secondary Wave of Marked Men Spawning

	If (UlyssesBattleCodebox.bUlyssesIsFriend == 1) && (iMarkedMenSpawnOnce02 == 0)											;Marked Men spawn when Ulysses is the player's friend
		Set iMarkedMenSpawnR01 to 1;
		Set iMarkedMenSpawnL02 to 1;																								
		Set iMarkedMenSpawnRU01 to 1;
		Set iMarkedMenSpawnLU02 to 1;
		Set NVDLC04UlyssesCombatBarks.bFriendMarkedMenEnter to 1;
		Set iMarkedMenSpawnOnce02 to 1;
	EndIf

	If (iMarkedMenSpawnRU01 == 1) && (iDoOnceMarkedMenRU01 == 0)																;Indicates when the Marked Men can spawn
		If ( UlyssesBattleCodebox.bWeakPlayer == 1 )
			TempleBattleMMB01WeakREF.MoveTo TempleMMSpawnU01;
			TempleBattleMMB01WeakREF.Enable;
		Else
			TempleBattleMMB01.MoveTo TempleMMSpawnU01;
			TempleBattleMMB01.Enable;
		EndIf
		Set iDoOnceMarkedMenRU01 to 1;
	EndIf

	If (iMarkedMenSpawnRU01 == 1) && (iDoOnceMarkedMenRU01 == 1) && ( (TempleBattleMMB01.GetDead == 1) ||  (TempleBattleMMB01WeakREF.GetDead == 1) )
		If ( UlyssesBattleCodebox.bWeakPlayer == 1 )
			TempleBattleMMRU01WeakREF.MoveTo TempleMMSpawnU01;
			TempleBattleMMRU01WeakREF.Enable;
		Else
			TempleBattleMMRU01.MoveTo TempleMMSpawnU01;
			TempleBattleMMRU01.Enable;
		EndIf
		Set UlyssesBattleCodebox.iThrowCharge to 1;
		Set UlyssesBattleCodebox.iMarkedMenDeadCount to (UlyssesBattleCodebox.iMarkedMenDeadCount +1);
		Set iDoOnceMarkedMenRU01 to 2;
	EndIf

	If (iMarkedMenSpawnRU01 == 1) && (iDoOnceMarkedMenRU01 == 2) && ( (TempleBattleMMRU01.GetDead == 1) || (TempleBattleMMRU01WeakREF.GetDead == 1) )
		If ( UlyssesBattleCodebox.bWeakPlayer == 1 )
			TempleBattleMMB02WeakREF.MoveTo TempleMMSpawnU01;
			TempleBattleMMB02WeakREF.Enable;
		Else
			TempleBattleMMB02.MoveTo TempleMMSpawnU01;
			TempleBattleMMB02.Enable;
		EndIf
		Set UlyssesBattleCodebox.iMarkedMenDeadCount to (UlyssesBattleCodebox.iMarkedMenDeadCount +1);
		Set iDoOnceMarkedMenRU01 to 3;
	EndIf

	If (iMarkedMenSpawnRU01 == 1) && (iDoOnceMarkedMenRU01 == 3) && ( (TempleBattleMMB02.GetDead == 1) || (TempleBattleMMB02WeakREF.GetDead == 1) )
		If ( UlyssesBattleCodebox.bWeakPlayer == 1 )
			TempleBattleMMRU02WeakREF.MoveTo TempleMMSpawnU01;
			TempleBattleMMRU02WeakREF.Enable;
		Else
			TempleBattleMMRU02.MoveTo TempleMMSpawnU01;
			TempleBattleMMRU02.Enable;
		EndIf
		Set UlyssesBattleCodebox.iMarkedMenDeadCount to (UlyssesBattleCodebox.iMarkedMenDeadCount +1);
		Set iDoOnceMarkedMenRU01 to 4;
	EndIf

	If (iMarkedMenSpawnRU01 == 1) && (iDoOnceMarkedMenRU01 == 4) && ( (TempleBattleMMRU02.GetDead == 1) || (TempleBattleMMRU02WeakREF.GetDead == 1) )
		Set UlyssesBattleCodebox.iMarkedMenDeadCount to (UlyssesBattleCodebox.iMarkedMenDeadCount +1);
		Set iDoOnceMarkedMenRU01 to 5;
	EndIf

	If (iMarkedMenSpawnLU02 == 1) && (iDoOnceMarkedMenLU02 == 0)
		If ( UlyssesBattleCodebox.bWeakPlayer == 1 )
			TempleBattleMMLU01WeakREF.MoveTo TempleMMSpawnU02;
			TempleBattleMMLU01WeakREF.Enable;
		Else
			TempleBattleMMLU01.MoveTo TempleMMSpawnU02;
			TempleBattleMMLU01.Enable;
		EndIf
		Set iDoOnceMarkedMenLU02 to 1;
	EndIf

	If (iMarkedMenSpawnLU02 == 1) && (iDoOnceMarkedMenLU02 == 1) && ( (TempleBattleMMLU01.GetDead == 1) || (TempleBattleMMLU01WeakREF.GetDead == 1) )
		If ( UlyssesBattleCodebox.bWeakPlayer == 1 )
			TempleBattleMMLU02WeakREF.MoveTo TempleMMSpawnU02;
			TempleBattleMMLU02WeakREF.Enable;
		Else
			TempleBattleMMLU02.MoveTo TempleMMSpawnU02;
			TempleBattleMMLU02.Enable;
		EndIf
		Set UlyssesBattleCodebox.iMarkedMenDeadCount to (UlyssesBattleCodebox.iMarkedMenDeadCount +1);
		Set iDoOnceMarkedMenLU02 to 2;
	EndIf

	If (iMarkedMenSpawnLU02 == 1) && (iDoOnceMarkedMenLU02 == 2) && ( (TempleBattleMMLU02.GetDead == 1) || (TempleBattleMMLU02WeakREF.GetDead == 1) )
		If ( UlyssesBattleCodebox.bWeakPlayer == 1 )
			TempleBattleMMLU03WeakREF.MoveTo TempleMMSpawnU02;
			TempleBattleMMLU03WeakREF.Enable;
		Else
			TempleBattleMMLU03.MoveTo TempleMMSpawnU02;
			TempleBattleMMLU03.Enable;
		EndIf
		Set UlyssesBattleCodebox.iMarkedMenDeadCount to (UlyssesBattleCodebox.iMarkedMenDeadCount +1);
		Set iDoOnceMarkedMenLU02 to 3;
	EndIf

	If (iMarkedMenSpawnLU02 == 1) && (iDoOnceMarkedMenLU02 == 3) && ( (TempleBattleMMLU03.GetDead == 1) || (TempleBattleMMLU03WeakREF.GetDead == 1) )
		If ( UlyssesBattleCodebox.bWeakPlayer == 1 )
			TempleBattleMMLU04WeakREF.MoveTo TempleMMSpawnU02;
			TempleBattleMMLU04WeakREF.Enable;
		Else
			TempleBattleMMLU04.MoveTo TempleMMSpawnU02;
			TempleBattleMMLU04.Enable;
		EndIf
		Set UlyssesBattleCodebox.iMarkedMenDeadCount to (UlyssesBattleCodebox.iMarkedMenDeadCount +1);
		Set iDoOnceMarkedMenLU02 to 4;
	EndIf

	If (iMarkedMenSpawnLU02 == 1) && (iDoOnceMarkedMenLU02 == 4) && ( (TempleBattleMMLU04.GetDead == 1) || (TempleBattleMMLU04WeakREF.GetDead == 1) )
		Set UlyssesBattleCodebox.iMarkedMenDeadCount to (UlyssesBattleCodebox.iMarkedMenDeadCount +1);
		Set iDoOnceMarkedMenLU02 to 5;
	EndIf

END

