Scn	NVDLC04Road01WomenBarracksCodeHolderSCRIPT

; Handles scripted scene (ambush) in the Women's Barracks secondary loc of Road01.
; J.S. 4/14/11


Short	bDoOnce
Int		nState
Float	fTimer

BEGIN OnTriggerEnter Player

	If (nState == 0)
		Set nState to 1;
		Return;
	Endif

END


BEGIN GameMode

	If (GetDisabled == 0)
		If (bDoOnce == 0)
			If (nState == 1)
				If (fTimer >= 1)
					NVDLC04Road01SecretDood01REF.Enable;
					Set nState to 2;
					Set fTimer to 0;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Elseif (nState == 2)
				If (fTimer >= 0.7)
					NVDLC04Road01SecretDood02REF.Enable;
					Set nState to 3;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Elseif (nState == 3)
				Set fTimer to 0;
				Set nState to 0;
				Set bDoOnce to 1;
				Disable;
				MarkForDelete;
			Endif
		Endif
	Endif

END

scn NVDLC04RedNightvisionBESCRIPT


Ref rSelf;


BEGIN ScriptEffectStart

	set rSelf to GetSelf;

	If (rSelf == Player)
		ApplyImageSpaceModifier NVDLC04RedNightvisionISFX;
		PlaySound OBJHappyMaker;
		player.setav Nighteye 1;
	EndIf
END


BEGIN ScriptEffectFinish

	set rSelf to GetSelf;

	If (rSelf == Player)

		RemoveImageSpaceModifier NVDLC04RedNightvisionISFX;
		If ( IsImageSpaceActive NVDLC04RedNightvisionFadeOutISFX == 0 )
			ApplyImageSpaceModifier NVDLC04RedNightvisionFadeOutISFX;
		Endif
		player.setav Nighteye 0

	EndIf

END

Scn	NVDLC04Silo01BlastDoorLeverSCRIPT

; Script that handles activation states of the lever activator tied to the blast door's EDE terminal in Silo01.
; J.S. 4/13/11.
; TLS 04/25/2011 - Duplicated objective scripting for Find ED-E objective.


Short	bDoOnce		; 1 = Lever has been used, and remains in its final position thereafter.
Int		nState		; 0 = Lever is retracted (tandem console has not been opened by EDE); 1 = Lever is rising; 2 = Lever is extended, and ready for use; 3 = Lever has been used.
						; these states correspond, respectively, to the following animation groups on the lever: SpecialIdle, Forward, Backward, Left.


BEGIN OnActivate

	If (IsActionRef Player == 1)
		If (bDoOnce == 0)
			If (NVDLC04DialogueEDE.bEDEHired == 0) && (nState < 2)
				ShowMessage NVDLC04EDEMessage;
				If (GetStage NVDLC04MQ01a < 20)
					SetStage NVDLC04MQ01a 20;
				EndIf
			Elseif (nState == 2)
				If (NVDLC04Silo01RevealBox.bAccess == 1)
					If (NVDLC04Silo01RevealBox.bRevealed == 0)
						SetDestroyed 1;
						NVDLC04SiloBlastDoorControlsREF.SetDestroyed 1;
						NVDLC04EDETerminalSilo01ButtonREF.PlayGroup Left 0;
						NVDLC04SiloBDControlsLightREF.Disable;
						PlaySound3D DRSCitadelMainGateOpen;
						NVDLC04Silo01MaintenanceBot01REF.Enable;
						NVDLC04Silo01MaintenanceBot02REF.Enable;
						NVDLC04Silo01WelcomeDoorREF.Unlock;
						SetObjectiveCompleted NVDLC04MQ01a 35 1;
						Set NVDLC04Silo01RevealBox.bRevealed to 1;
					Endif
					NVDLC04SiloBlastDoorControlsREF.Playgroup Right 0;
					NVDLC04SiloWallWindowREF.Playgroup Left 0;
					Set NVDLC04SiloBlastDoorControlsREF.mode to 1;
					Set nState to 3;
					Set bDoOnce to 1;
					Return;
				Endif
			Elseif (nState == 3)
				ShowMessage NVDLC04EDELeverOnMSG;
			Endif
		Endif
	Endif

END

scn NVDLC04Road01MusicScript

; Script plays Music for walking past First Warhead
; AJD 4/12/2011
; J.S. 4/13/11 - Added GameMode block, and timer, to control the return of normal music after the reveal sounds play.


Int		nState
Float	fTimer

Begin OnTriggerEnter Player

	If (nState == 0)
		PlayMusic NVDLC04TransitionStinger;
		PlaySound musNVDLC04WarheardReveal;
		Set nState to 1;
	Endif

End


BEGIN GameMode

	If (nState == 1)
		Set fTimer to (fTimer + GetSecondsPassed);
		If (fTimer >= 16)
			Set nState to 2;
			Return;
		Else
			PlayMusic NVDLC04TransitionStinger;
		Endif
	Elseif (nState == 2)
		Disable;
		MarkForDelete;
	Endif

END

