scn NVDLC04NukedBlastSCRIPT


; applies NVDLC04NukeTransISFX effect on entering the various impact sites across DLC4
; JRG 04/08/2011 - Adding ISFX, as well as TOD stuff


Short bActive;

BEGIN OnTriggerEnter Player

	If (bActive == 0)
		If (GameHour < 20) && (GameHour > 8)
			ApplyImageSpaceModifier NVDLC04NukeTransDayISFX;
			Set bActive to 1;
		Elseif (GameHour > 20) || (GameHour < 8)
			ApplyImageSpaceModifier NVDLC04NukeTransNightISFX;
			Set bActive to 1;
		Endif
	Endif


END

SCN NVDLC04EDETempleRevealCodeHolderSCRIPT


; Script for FX on ED-E's second reveal.
; TLS 04/07/2011
; TLS 04/22/2011 - Added variable to switch off Open Pod 2 option.
; TLS 06/02/2011 - Resetting ED-E's Aggression level.


Float fTimer;
Short nStage;
Ref rExplosion;
Short bPodOpen;


BEGIN GameMode

	If (GetDisabled == 1)
		Return;
	Else
		If (nStage == 0)
			NVDLC04EDEREF.PlaySound AMBHooverTrbnRmSteamOS;
			Set fTimer to 1;
			Set nStage to 1;
		ElseIf (nStage == 1)
			If (fTimer >= 0)
				Set fTimer to (fTimer - GetSecondsPassed);
			Else
				NVDLC04EDETempleFXREF.Enable;
				NVDLC04EDEREF.PlaySound AMBHooverTrbnRmSteamOS;
				Set nStage to 2;
				Set fTimer to 2;
				Return;
			EndIf
		ElseIf (nStage == 2)
			If (fTimer >= 0)
				Set fTimer to (fTimer - GetSecondsPassed);
			Else
				NVDLC04EDEREF.PlaySound AMBHooverTrbnRmSteamOS;
				EDERescuePod.PlayGroup Forward 1;
				NVDLC04EDETempleRevealLightBeamREF.Enable;
				Set nStage to 3;
				Set fTimer to 1;
				Return;
			EndIf
		ElseIf (nStage == 3)
			If (fTimer >= 0)
				Set fTimer to (fTimer - GetSecondsPassed);
			Else
				Set rExplosion to NVDLC04EDETempleStaticREF.PlaceAtMe NVDLC04EDEReleaseExplosion;
				rExplosion.SetScale 0.35;
				Set nStage to 4;
				Set fTimer to 0.5;
				Return;
			EndIf;
		ElseIf (nStage == 4);
			If (fTimer >= 0)
				Set fTimer to (fTimer - GetsecondsPassed);
			Else
				NVDLC04EDEREF.Enable;
				NVDLC04EDETempleStaticREF.Disable;
				EDERescuePodCol.Disable;
				SetStage NVDLC04MQ03a 20;
				NVDLC04EDETempleFXREF.Disable;
				NVDLC04EDETempleRevealLightBeamREF.Disable;
				NVDLC04EDEREF.SetRestrained 0;
				NVDLC04EDEREF.AddToFaction RobotFaction 1;
				Set nStage to 5;
				Set fTimer to 4;
				Return;
			EndIf
		ElseIf (nStage == 5)
			If (fTimer >= 0)
				Set fTimer to (fTimer - GetSecondsPassed);
			Else
				If (NVDLC04EDEREF.bTempleGreet == 0)
					NVDLC04EDEREF.AddScriptPackage NVDLC04EDELog09PACKAGE;
				EndIf
				NVDLC04EDEREF.SetActorValue Aggression 1;
				Disable;
				MarkForDelete;
			EndIf
		EndIf
	EndIf

END

