SCN NVDLC04UlyssesCombatBarksSCRIPT


; Codebox for Ulysses combat barks
; DM 4/7/11
; DM 4/11/11 - Changed to a quest script
; CES 07/28/11 - Modified the bark timer to only allow a new bark if Ulysses isn't talking.


INT		iEyebotKillCounter01;
INT		iEyebotKillCounter02;
INT		iMarkedMenKIllCounter;
INT		iCombatTauntCounter;
INT		iFriendTauntCounter;
FLOAT	fBarkTimer;
FLOAT	fTauntTimer;
SHORT	bCanBark;
SHORT	bCombatEyebotDestroyed01;		
SHORT	bCombatEyebotDestroyed02;		
SHORT	bCombatEyebotsAllDestroyed;		
SHORT	bCombatEyebotsDisabled01;		
SHORT	bCombatEyebotsDisabled02;		
SHORT	bCombatEyebotsHostile01;			
SHORT	bCombatEyebotsHostile02;			
SHORT	bCombatMarkedMenAllDead;		
SHORT	bCombatMarkedMenEnter;			
SHORT	bCombatMarkedMenKilled01;		
SHORT	bCombatMarkedMenKilled02;		
SHORT	bCombatTaunt01;						
SHORT	bCombatTaunt02;						
SHORT	bCombatTaunt03;						
SHORT	bFriendMarkedMenAllDead;			
SHORT	bFriendMarkedMenEnter;			
SHORT	bFriendMarkedMenHalfDead;		
SHORT	bFriendMMUsesStealthboy;
SHORT	bFriendMMWithSatchelCharge;
SHORT	bFriendSentryDies01;				
SHORT	bFriendSentryDies02;				
SHORT	bFriendTaunt01;						
SHORT	bFriendTaunt02;						
SHORT	bFriendTaunt03;						
SHORT	bDoOnceCombatMMAllDie;
SHORT	bDoOnceFriendMMHalfDead;
SHORT	bDoOnceFriendSentryDies01;
SHORT	bDoOnceFriendSentryDies02;


BEGIN GameMode


;;;;;Ulysses Bark Timer
	If (bCanBark == 0) && (fBarkTimer >= 5) && (NVDLC04UlyssesREF.IsTalking == 0)
		Set bCanBark to 1;
		Set fBarkTimer to 0;
	ElseIf (bCanBark == 0) && (fBarkTimer < 5)
		Set fBarkTimer to (fBarkTimer + GetSecondsPassed);
	EndIf


;;;;;Ulysses is fighting against the player barks
	If (bCanBark == 1) && (bCombatEyebotDestroyed01 == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesCombatEyebotDestroyed01;
		Set bCombatEyebotDestroyed01 to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bCombatEyebotDestroyed02 == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesCombatEyebotDestroyed02;
		Set bCombatEyebotDestroyed02 to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bCombatEyebotsAllDestroyed == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesCombatEyebotsAllDestroyed;
		Set bCombatEyebotsAllDestroyed to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bCombatEyebotsDisabled01 == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesCombatEyebotsDisabled01;
		Set bCombatEyebotsDisabled01 to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bCombatEyebotsDisabled02 == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesCombatEyebotsDisabled02;
		Set bCombatEyebotsDisabled02 to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bCombatEyebotsHostile01 == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesCombatEyebotsHostile01;
		Set bCombatEyebotsHostile01 to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bCombatEyebotsHostile02 == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesCombatEyebotsHostile02;
		Set bCombatEyebotsHostile02 to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bCombatMarkedMenAllDead == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesCombatMarkedMenAllDead;
		Set bCombatMarkedMenAllDead to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bCombatMarkedMenEnter == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesCombatMarkedMenEnter;
		Set bCombatMarkedMenEnter to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bCombatMarkedMenKilled01 == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesCombatMarkedMenKilled01;
		Set bCombatMarkedMenKilled01 to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bCombatMarkedMenKilled02 == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesCombatMarkedMenKilled02;
		Set bCombatMarkedMenKilled02 to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bCombatTaunt01 == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesCombatTaunt01;
		Set bCombatTaunt01 to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bCombatTaunt02 == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesCombatTaunt02;
		Set bCombatTaunt02 to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bCombatTaunt03 == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesCombatTaunt03;
		Set bCombatTaunt03 to 0;
		Set bCanBark to 0;
	EndIf


;;;;;Ulysses is fighting with the player barks
	If (bCanBark == 1) && (bFriendMarkedMenAllDead == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesFriendMarkedMenAllDead;
		Set bFriendMarkedMenAllDead to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bFriendMarkedMenEnter == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesFriendMarkedMenEnter;
		Set bFriendMarkedMenEnter to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bFriendMarkedMenHalfDead == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesFriendMarkedMenHalfDead;
		Set bFriendMarkedMenHalfDead to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bFriendMMUsesStealthboy == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesFriendMMUsesStealthboy;
		Set bFriendMMUsesStealthboy to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bFriendMMWithSatchelCharge == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesFriendMMWithSatchelCharge;
		Set bFriendMMWithSatchelCharge to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bFriendSentryDies01 == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesFriendSentryDies01;
		Set bFriendSentryDies01 to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bFriendSentryDies02 == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesFriendSentryDies02;
		Set bFriendSentryDies02 to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bFriendTaunt01 == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesFriendTaunt01;
		Set bFriendTaunt01 to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bFriendTaunt02 == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesFriendTaunt02;
		Set bFriendTaunt02 to 0;
		Set bCanBark to 0;
	EndIf

	If (bCanBark == 1) && (bFriendTaunt03 == 1)
		NVDLC04UlyssesREF.Say NVDLC04UlyssesFriendTaunt03;
		Set bFriendTaunt03 to 0;
		Set bCanBark to 0;
	EndIf


;;;;;Setting the variables for each of Ulysses barks

;;;;;Variables being set in UlyssesBattleCodebox
;bCombatEyebotsDisabled01
;bCombatEyebotsDisabled02
;bCombatEyebotsHostile01
;bCombatEyebotsHostile02
;bCombatMarkedMenEnter
;bFriendMarkedMenAllDead
;bFriendMarkedMenEnter
;bFriendMMWithSatchelCharge

;;;;;Variable being set in the script NVDLC04TempleMarkedMenHunterSCRIPT
;bFriendMMUsesStealthboy


;;;;;Ulysses is fighting against the player

	If (UlyssesEyebotR01.GetDead == 1) && (iEyebotKillCounter01 == 0)						
		Set bCombatEyebotDestroyed01 to 1;
		Set iEyebotKillCounter01 to 1;
	EndIf

	If (UlyssesEyebotR02.GetDead == 1) && (iEyebotKillCounter01 == 1)
		Set bCombatEyebotDestroyed01 to 1;
		Set iEyebotKillCounter01 to 2;
	EndIf

	If (UlyssesEyebotR03.GetDead == 1) && (iEyebotKillCounter01 == 2)
		Set bCombatEyebotDestroyed01 to 1;
		Set iEyebotKillCounter01 to 3;
	EndIf

	If (UlyssesEyebotR04.GetDead == 1) && (iEyebotKillCounter01 == 3)
		Set iEyebotKillCounter01 to 4;
	EndIf

	If (UlyssesEyebotL01.GetDead == 1) && (iEyebotKillCounter02 == 0)
		Set bCombatEyebotDestroyed02 to 1;
		Set iEyebotKillCounter02 to 1;
	EndIf

	If (UlyssesEyebotL02.GetDead == 1) && (iEyebotKillCounter02 == 1)
		Set bCombatEyebotDestroyed02 to 1;
		Set iEyebotKillCounter02 to 2;
	EndIf

	If (UlyssesEyebotL03.GetDead == 1) && (iEyebotKillCounter02 == 2)
		Set bCombatEyebotDestroyed02 to 1;
		Set iEyebotKillCounter02 to 3;
	EndIf

	If (UlyssesEyebotL04.GetDead == 1) && (iEyebotKillCounter02 == 3)
		Set iEyebotKillCounter02 to 4;
	EndIf

	If (iEyebotKillCounter01 == 4) && (iEyebotKillCounter02 == 4)								
		Set bCombatEyebotsAllDestroyed to 1;
		Set iEyebotKillCounter01 to 5;
		Set iEyebotKillCounter02 to 5;
	EndIf

	If (UlyssesBattleCodebox.iMarkedMenDeadCount == 8) && (UlyssesBattleCodebox.bUlyssesIsFriend == 0) && (bDoOnceCombatMMAllDie == 0)
		Set bCombatMarkedMenAllDead to 1;
		Set bDoOnceCombatMMAllDie to 1;
	EndIf

	If (UlyssesBattleCodebox.iMarkedMenDeadCount == 2) && (iMarkedMenKillCounter == 0) && (UlyssesBattleCodebox.bUlyssesIsFriend == 0)
		Set bCombatMarkedMenKilled01 to 1;
		Set iMarkedMenKillCounter to 1;
	EndIf

	If (UlyssesBattleCodebox.iMarkedMenDeadCount == 4) && (iMarkedMenKillCounter == 1) && (UlyssesBattleCodebox.bUlyssesIsFriend == 0)
		Set bCombatMarkedMenKilled02 to 1;
		Set iMarkedMenKillCounter to 2;
	EndIf

	If (UlyssesBattleCodebox.iMarkedMenDeadCount == 6) && (iMarkedMenKillCounter == 2) && (UlyssesBattleCodebox.bUlyssesIsFriend == 0)
		Set bCombatMarkedMenKilled01 to 1;
		Set iMarkedMenKillCounter to 3;
	EndIf

	If (UlyssesBattleCodebox.iMarkedMenDeadCount == 7) && (iMarkedMenKillCounter == 3) && (UlyssesBattleCodebox.bUlyssesIsFriend == 0)
		Set bCombatMarkedMenKilled02 to 1;
		Set iMarkedMenKillCounter to 4;
	EndIf

	If (bCanBark == 1) && (iCombatTauntCounter == 0) && (fTauntTimer >= 10) && (UlyssesBattleCodebox.bUlyssesIsFriend == 0)
		Set bCombatTaunt01 to 1;
		Set iCombatTauntCounter to 1;
		Set fBarkTimer to 0;
		Set fTauntTimer to 0;
	ElseIf (bCanBark == 1) && (iCombatTauntCounter == 1) && (fTauntTimer >= 10) && (UlyssesBattleCodebox.bUlyssesIsFriend == 0)
		Set bCombatTaunt02 to 1;
		Set iCombatTauntCounter to 2;
		Set fBarkTimer to 0;
		Set fTauntTimer to 0;
	ElseIf (bCanBark == 1) && (iCombatTauntCounter == 2) && (fTauntTimer >= 10) && (UlyssesBattleCodebox.bUlyssesIsFriend == 0)
		Set bCombatTaunt03 to 1;
		Set iCombatTauntCounter to 0;
		Set fBarkTimer to 0;
		Set fTauntTimer to 0;
	ElseIf (bCanBark == 1) && (fTauntTimer < 10) && (UlyssesBattleCodebox.bUlyssesIsFriend == 0)
		Set fTauntTimer to (fTauntTimer + GetSecondsPassed);
	EndIf


;;;;;Ulysses is fighting with the player

	If (UlyssesBattleCodebox.iMarkedMenDeadCount == 8) && (UlyssesBattleCodebox.bUlyssesIsFriend == 1) && (bDoOnceFriendMMHalfDead == 0)
		Set bFriendMarkedMenHalfDead to 1;
		Set bDoOnceFriendMMHalfDead to 1;
	EndIf

	If (UlyssesSentry01.GetDead == 1) && (bDoOnceFriendSentryDies01 == 0) && (UlyssesBattleCodebox.bUlyssesIsFriend == 1)
		Set bFriendSentryDies01 to 1;
		Set bDoOnceFriendSentryDies01 to 1;
	EndIf

	If (UlyssesSentry02.GetDead == 1) && (bDoOnceFriendSentryDies02 == 0) && (UlyssesBattleCodebox.bUlyssesIsFriend == 1)
		Set bFriendSentryDies02 to 1;
		Set bDoOnceFriendSentryDies02 to 1;
	EndIf

	If (bCanBark == 1) && (iFriendTauntCounter == 0) && (fTauntTimer >= 10) && (UlyssesBattleCodebox.bUlyssesIsFriend == 1)
		Set bFriendTaunt01 to 1;
		Set iFriendTauntCounter to 1;
		Set fBarkTimer to 0;
		Set fTauntTimer to 0;
	ElseIf (bCanBark == 1) && (iFriendTauntCounter == 1) && (fTauntTimer >= 10) && (UlyssesBattleCodebox.bUlyssesIsFriend == 1)
		Set bFriendTaunt02 to 1;
		Set iFriendTauntCounter to 2;
		Set fBarkTimer to 0;
		Set fTauntTimer to 0;
	ElseIf (bCanBark == 1) && (iFriendTauntCounter == 2) && (fTauntTimer >= 10) && (UlyssesBattleCodebox.bUlyssesIsFriend == 1)
		Set bFriendTaunt03 to 1;
		Set iFriendTauntCounter to 0;
		Set fBarkTimer to 0;
		Set fTauntTimer to 0;
	ElseIf (bCanBark == 1) && (fTauntTimer < 10) && (UlyssesBattleCodebox.bUlyssesIsFriend == 1)
		Set fTauntTimer to (fTauntTimer + GetSecondsPassed);
	EndIf

END

SCN NVDLC04DivideBuildingFallSCRIPT


;The falling building sequence
;DM 2/24/11


FLOAT	fTimer;							;Timer
SHORT	bBuildingFall;					;Building and rockslide initiator
INT		iStage;							;Stage the sequence is on


BEGIN GameMode

	If (bBuildingFall == 1) && (iStage == 0) && (fTimer  >= 1)
		Player.PlaceAtMe NVDLC04DivideRumbleNoSound;
		Set iStage to 1;
	ElseIf (bBuildingFall == 1) && (iStage == 1) && (fTimer  >= 1.5)
		Player.PlaceAtMe NVDLC04DivideRumbleNoSound;
		Set iStage to 2;
	ElseIf (bBuildingFall == 1) && (iStage == 2) && (fTimer  >= 2)
		Player.PlaceAtMe NVDLC04DivideRumbleNoSound;
		Set iStage to 3;
	ElseIf (bBuildingFall == 1) && (iStage == 3) && (fTimer  >= 2.5)
		Player.PlaceAtMe NVDLC04DivideRumbleNoSound;
		Set iStage to 4;
	ElseIf (bBuildingFall == 1) && (iStage == 4) && (fTimer  >= 3.2)
		DivideBldgRockslide01REF.PlayGroup Forward 1;
		Player.PlaceAtMe NVDLC04DivideRumbleNoSound;
		Set iStage to 5;
	ElseIf (bBuildingFall == 1) && (iStage == 5) && (fTimer  >= 4)
		DivideBldgRockslide03REF.PlayGroup Forward 1;
		PlaySound NVDLC04QSTBuildingCollapse;
		Player.PlaceAtMe NVDLC04DivideRumbleNoSound;
		Set iStage to 6;
	ElseIf (bBuildingFall == 1) && (iStage == 6) && (fTimer  >= 5)
		DivideBldgRockslide02REF.PlayGroup Forward 1;
		Player.PlaceAtMe NVDLC04DivideRumbleNoSound;
		Set iStage to 7;
	ElseIf	(bBuildingFall == 1) && (iStage == 7) && (fTimer  >= 6)
		DivideBuildingFallenREF.PlayGroup Backward 1;
		Player.PlaceAtMe NVDLC04DivideRumbleNoSound;
		Set iStage to 8;
	ElseIf	(bBuildingFall == 1) && (iStage == 8) && (fTimer  >= 6.5)
		Player.PlaceAtMe NVDLC04DivideRumbleNoSound;
		Set iStage to 9;
	ElseIf	(bBuildingFall == 1) && (iStage == 9) && (fTimer  >= 9.5)
		Player.PlaceAtMe NVDLC04DivideRumbleNoSound;
		BuildingCollapseRocks.Enable;
		SetStage NVDLC04MQ02 30;
		Set iStage to 10;
	ElseIf	(bBuildingFall == 1) && (fTimer < 10)
		Set fTimer to (fTimer + GetSecondsPassed);
	EndIf

END

SCN	NVDLC04EDEFlyAwayTriggerSCRIPT


;Set the variable to have the false Ed-E fly away
; DM 4/5/11
; TLS 04/06/2011 - Moving quest updates to here.
; TLS 04/07/2011 - More quest stuff here.
; TLS 04/13/2011 - Adding ED-E's stuff back to the player.
; DM 4/19/11 - Adding flares
; TLS 05/02/2011 - Adding new pop-up.


Int		iStage;								;Stage number for the timer
Short	bDoOnce;							;Makes sure the trigger only goes off once
Short	bEDEFlyAway;						;Starts timer
Float	fTimerEDEFlyAway;				;timer to disable the fly away version of Ed-E


BEGIN OnTriggerEnter Player

	If (bDoOnce == 0)
		SetStage NVDLC04MQ02a 100;
		Player.RemovePerk NVDLC04EnhancedSensors;
		ShowMessage NVDLC04EDEGoneMessage;
		ShowMessage FollowerMessagePerkEDERemove;
		Set bEDEFlyAway to 1;
		EDEFlyAwayREF.PlayGroup Forward 1;
		PlaySound NVDLC04QSTEdeFlyAway;
		Set bDoOnce to 1;
		NVDLC04EDEREF.RemoveItem NVDLC04EDEWeapons 999;
		NVDLC04EDEREF.RemoveAllItems Player;
		NVDLC04EDEREF.AddItem NVDLC04EDEWeapons 1;
	EndIf

END


BEGIN GameMode

	If (bEDEFlyAway == 1) && (fTimerEDEFlyAway >=6.5) && (iStage == 0)
		DivideFlareMarker07.FireWeapon NVDLC04WeapFlareGun;
		PlaySound NVDLC04WPNFlareGunFire3DDist;
		Set iStage to 1;
	ElseIf (bEDEFlyAway == 1) && (fTimerEDEFlyAway >=7) && (iStage == 1)
		DivideFlareMarker08.FireWeapon NVDLC04WeapFlareGun;
		PlaySound NVDLC04WPNFlareGunFire3DDist;
		Set iStage to 2;
	ElseIf (bEDEFlyAway == 1) && (fTimerEDEFlyAway >=7.5) && (iStage ==2)
		EDEFlyAwayREF.disable;
		Set iStage to 3;
	ElseIf (bEDEFlyAway == 1) && (fTimerEDEFlyAway >=8) && (iStage == 3)
		Disable;
		MarkForDelete;
	ElseIf (bEDEFlyAway == 1) && (fTimerEDEFlyAway < 9)
		Set fTimerEDEFlyAway to (fTimerEDEFlyAway + GetSecondsPassed)
	EndIf

END

SCN NVDLC04MQ04SCRIPT


; Script to control final perk award.
; TLS 04/04/2011


Short bMessage;					; Used to trigger the awarding of the final perk.
Short nButton;


BEGIN GameMode

	If (bMessage == 1)
			If (NVDLC04MQ03b.nMissileTarget == 1)
				Player.AddPerk NVDLC04BearSlayerPerk;
				Set bMessage to 0;
				StopQuest NVDLC04MQ04;
			ElseIf (NVDLC04MQ03b.nMissileTarget == 2)
				Player.AddPerk NVDLC04ScourgeOfTheEastPerk;
				Set bMessage to 0;
				StopQuest NVDLC04MQ04;
			ElseIf (NVDLC04MQ03b.nMissileTarget == 3)
				Player.AddPerk NVDLC04DeadMansBurdenPerk;
				Set bMessage to 0;
				StopQuest NVDLC04MQ04;
			EndIf
	EndIf

END


BEGIN MenuMode 1001

	If (GetStage NVDLC04MQ04 == 100)
		Set bMessage to 1;
	EndIf

END

SCN NVDLC04CompletionTriggerSCRIPT


; Script to complete final quest of DLC04.
; TLS 04/04/2011
; CES 04/13/11 - Added in stuff to control the nuke explosions
; TLS 04/14/2011 - Added timer for quest completion
; OMP 04/22/2011 - Moved the scripting that cleans up the Ending Slide sequence from NVDLC04EndingMarkerTriggerSCRIPT.
; TLS 04/25/2011 - Added DisableFastTravel flag.
; JSH 05/06/11 - Marked Laser Detonator as non-quest object so player can chuck it away, if he wants. But who would want to? It's neat.
; TLS 05/17/2011 - Got rid of previous addition. You will never be free of the Detonator. NEVER.
; DLM 5/31/11 - Added disabling of animated ulysses rocket and enabling of a static version for if the player leaves before the smoke clears.
; DLM 7/13/11 - Moved the temple entrance disabling to this script.


Short bRunTimer;
Float fTimer;
Short bNukeOnce;

BEGIN OnTriggerEnter Player

	; Give control back to the player
	SetInCharGen 0;																			; Allow the player to level up
	Player.RemoveScriptPackage NVDLC04PlayerNoSavePackage;				; Allow player to save the game
	EnablePlayerControls;																	; Restore control to the player
	PlayMusic EndTransitionStinger;
	TempleExit.Disable;																		;Disables the current doors and swaps them with standard versions
	TempleEntrance.Disable;

	StopQuest NVDLC04Ending;

	; This should be the same as Travis has below, but I don't care about which one firing, just fire it.
	If (bNukeOnce == 0) && ((GetStage NVDLC04MQ03b == 30) || (GetStage NVDLC04MQ04 == 20))

		Set bNukeOnce to 1;
		EnableFastTravel 0;

		;Turn off coyotes nearby.
		NVDLC04CanyonWreckageCoyoteMarker.Disable;

		;Check variable to see which Nuke we should show.
		If (NVDLC04MQ03b.nMissileTarget == 1)
			;NCR got Nuked
			NVDLC04Long15NukeExplosionREF.PlayGroup Forward 1;
			ApplyImageSpaceModifier NVDLC04WastelandNukeISFX;
			ForceWeather NVDLC04WastelandClear;
			PlaySound NVDLC04QSTCompletionNuke;
			UlyssesRocketREF.Disable;
			UlyssesRocketStaticREF.Enable;

		ElseIf (NVDLC04MQ03b.nMissileTarget == 2)
			;Caesar's Legion got Nuked
			NVDLC04LegionNukeExplosionREF.PlayGroup Forward 1;
			ApplyImageSpaceModifier NVDLC04WastelandNukeISFX;
			ForceWeather NVDLC04WastelandClear;
			PlaySound NVDLC04QSTCompletionNuke;
			UlyssesRocketREF.Disable;
			UlyssesRocketStaticREF.Enable;

		ElseIf (NVDLC04MQ03b.nMissileTarget == 3)
			;Everybody got Nuked
			NVDLC04LegionNukeExplosionREF.PlayGroup Forward 1;
			NVDLC04Long15NukeExplosionREF.PlayGroup Forward 1;
			ApplyImageSpaceModifier NVDLC04WastelandNukeISFX;
			ForceWeather NVDLC04WastelandClear;
			PlaySound NVDLC04QSTCompletionNuke;
			UlyssesRocketREF.Disable;
			UlyssesRocketStaticREF.Enable;

		EndIf

		; Start timer if there's a nuke explosion, otherwise start a shorter timer
		If (NVDLC04MQ03b.nMissileTarget == 0)
			If (bRunTimer == 0)
				Set fTimer to 3;
				Set bRunTimer to 1;
			EndIf
		Else
			If (bRunTimer == 0)
				Set fTimer to 18;                                                    
				Set bRunTimer to 1;
			EndIf
		EndIf
	EndIf

END


BEGIN GameMode

	If (bRunTimer == 0)
		Return;
	Else
		If (fTimer >= 0)
			Set fTimer to (fTimer - GetSecondsPassed);
		Else
			;This is Travis' stuff
			If (GetStage NVDLC04MQ03b == 30)
				SetStage NVDLC04MQ03b 100;
			ElseIf (GetStage NVDLC04MQ04 == 20)
				SetStage NVDLC04MQ04 100;
			EndIf

			ReleaseWeatherOverride;
			EnableFastTravel 1;
			Set bRunTimer to 0;
			Disable;
			MarkForDelete;
		EndIf
	EndIf

END

SCN NVDLC04NukedLegionBoatSCRIPT


; Boat that takes the player to and from the nuked Legion camp
; TLS 04/04/2011


Ref rLinkedRef;


BEGIN OnActivate

	If (IsActionRef Player)
		Set rLinkedRef to GetLinkedRef;
