	EndIf

END

ScriptName NVDLC04AchievementsSCRIPT


; Tracks progress towards achievements in NVDLC04.
; JSH 04/01/2011
; OMP 04/19/2011 - Moved the IncrementScriptedChallenge call for Ulysses Odyssey to the audio log trigger.
; TLS 06/02/2011 - Adding setup for Nostalgia


Short nEDECATED;						; EDE's backstory progress.
Short nWarheadHunter;					; How many warheads the player has detonated.
Short nMostLikelyTo;						; Player's past history references progress.
Short nNostalgia;							; Number of Divide journal entries.
Short nUlyssesOdyssey					; Ulysses' history progress.

Short bGainedEDECATED;
Short bGainedWarheadHunter;
Short bGainedMostLikelyTo;
Short bGainedNostalgia;
Short bGainedUlyssesOdyssey;

Short bTrooperNote01;
Short bTrooperNote02;
Short bTrooperNote03;
Short bApartmentNote;
Short bGovtNote;
Short bJournal01;
Short bJournal02;
Short bJournal03;
Short bWaterPlantNote;

BEGIN GameMode

; --------------------- N O S T A L G I A ------------------------
; Icrements challenge when player finds loose notes. Terminal-based notes are handled in the scripting for the terminal.

	If (GetHasNote NVDLC04AudioLogTrooperNote01 == 1) && (bTrooperNote01 == 0)
		IncrementScriptedChallenge NVDLC04ChallengeNostalgia;
		Set bTrooperNote01 to 1;
	EndIf

	If (GetHasNote NVDLC04AudioLogTrooperNote02 == 1) && (bTrooperNote02 == 0)
		IncrementScriptedChallenge NVDLC04ChallengeNostalgia;
		Set bTrooperNote02 to 1;
	EndIf

	If (GetHasNote NVDLC04AudioLogTrooperNote03 == 1) && (bTrooperNote03 == 0)
		IncrementScriptedChallenge NVDLC04ChallengeNostalgia;
		Set bTrooperNote03 to 1;
	EndIf

	If (GetHasNote NVDLC04DivideJournal01 == 1) && (bJournal01 == 0)
		IncrementScriptedChallenge NVDLC04ChallengeNostalgia;
		Set bJournal01 to 1;
	EndIf

	If (GetHasNote NVDLC04DivideJournal02 == 1) && (bJournal02 == 0)
		IncrementScriptedChallenge NVDLC04ChallengeNostalgia;
		Set bJournal02 to 1;
	EndIf

	If (GetHasNote NVDLC04DivideJournal03 == 1) && (bJournal03 == 0)
		IncrementScriptedChallenge NVDLC04ChallengeNostalgia;
		Set bJournal03 to 1;
	EndIf

	If (GetHasNote NVDLC04DivideJournal01 == 1) && (bJournal01 == 0)
		IncrementScriptedChallenge NVDLC04ChallengeNostalgia;
		Set bJournal01 to 1;
	EndIf



	; OBSOLETE
	;If ( nEDECATED == 5 ) && ( bGainedEDECATED == 0 )
	;	IncrementScriptedChallenge NVDLC04AchievementEDECATED;
	;	Set bGainedEDECATED to 1;
	;EndIf

	;If ( nMostLikelyTo == 5 ) && ( bGainedMostLikelyTo == 0 )
	;	IncrementScriptedChallenge NVDLC04ChallengeMostLikelyTo;
	;	Set bGainedMostLikelyTo to 1;
	;EndIf

	;If ( nNostalgia == 5 ) && ( bGainedNostalgia == 0 )
	;	IncrementScriptedChallenge NVDLC04ChallengeNostalgia;
	;	Set bGainedNostalgia to 1;
	;EndIf

	;If ( nUlyssesOdyssey == 6 ) && ( bGainedUlyssesOdyssey == 0 )
	;	Set bGainedUlyssesOdyssey to 1;
	;EndIf

END

SCN NVDLC04EDEMissileLaunchTriggerSCRIPT


; Script to trigger ED-E's activation of the missile launch
; TLS 03/31/2011
; TLS 05/11/2011 - Tweaked to get console anims playing right.
; TLS 05/31/2011 - Removing ED-E waiting check.
; J.S. 7/12/11 - Added call, and check, to disable the CSA on the launch controls' lever, in preparation for its activation sequence.


Float fTimer;
Short nTimer;
Short	bLeverOffOnce;	; J.S. 7/12/11 New var for CSA check.


BEGIN OnTriggerEnter Player

	If (NVDLC04DialogueEDE.bEDEHired == 1)
		If (NVDLC04EDEREF.GetDistance Player >= 550)
			NVDLC04EDEREF.MoveTo NVDLC04EDELog05MarkerREF;
		EndIf
		If (nTimer < 1)
			NVDLC04EDEREF.AddScriptPackage NVDLC04EDEActivateMissileLaunch;
			NVDLC04MissileLaunchTerminalREF.PlaySound OBJComputerTerminalForward;
			NVDLC04MissileLaunchTerminalREF.PlayGroup Forward 0;
			NVDLC04LaunchCollisionREF.Enable;
			Set nTimer to 1;
		EndIf
	EndIf

	If (bLeverOffOnce == 0)
		NVDLC04EDETerminalLeverLaunch01REF.SetDestroyed 1;								; J.S. 7/12/11 The lever is now set to Destroyed 1, which means it does not display a CSA.
		Set bLeverOffOnce to 1;
	Endif	

END


BEGIN GameMode

	If (nTimer == 0)
		Return;
	ElseIf (nTimer == 1)
		If (fTimer <= 1)
			Set fTimer to (fTimer + GetSecondsPassed);
		Else
			NVDLC04MissileLaunchTerminalREF.PlayGroup Backward 0;
			Set nTimer to 2;
			Set fTimer to 0;
			Return;
		EndIf
	ElseIf (nTimer == 2)
		If (fTimer <= 15 && NVDLC04EDERef.GetDistance NVDLC04EDEMissileLaunchEDEMarkerREF >= 96)
			Set fTimer to (fTimer + GetSecondsPassed);
		Else
			NVDLC04EDEREF.UseWeapon NVDLC04WeapEDEUnlockZapper NVDLC04EDEREF NVDLC04MissileLaunchTarget2REF 1 0 0 1 1;
			Set nTimer to 3;
			Set fTimer to 0;
			Return;
		EndIf
	ElseIf (nTimer == 3)
		If (fTimer <= 1)
			Set fTimer to (fTimer + GetSecondsPassed);
		Else
			NVDLC04MissileLaunchTerminalREF.PlayGroup Backward 0;
			Set NVDLC04MissileLaunchTerminalREF.fTimer to 4;
			Set NVDLC04MissileLaunchTerminalREF.nTimerStage to 1;
			Set nTimer to 4;
			Set fTimer to 0;
			Return;
		EndIf
	ElseIf (nTimer == 4)
		If (fTimer <= 2)
			Set fTimer to (fTimer + GetSecondsPassed);
		Else
			NVDLC04MissileLaunchTerminalREF.PlaySound OBJComputerTerminalArc;
			Set nTimer to 5;
			Set fTimer to 0;
			Return;
		EndIf
	ElseIf (nTimer == 5)
		If (fTimer <= 8)
			Set fTimer to (fTimer + GetSecondsPassed);
		Else
			NVDLC04EDERef.ResetAI;
			Set nTimer to 6;
			Set fTimer to 0;
			Disable;
			MarkForDelete;
			Return;
		EndIf
	EndIf

END

scn NVDLC04RadioTowerStartSCRIPT

; controls the start and timing of the Vista blinking radio towers
; JRG 03/31/2011 - Adding scripting for initial timing and start of radio towers

float fTimer;
short bActive;
short nStage

BEGIN OnTriggerEnter Player

	if (bActive == 0)
		set bActive to 1;
		set nStage to 0;
		disable
	Endif

END

BEGIN GameMode

	if (bActive == 1);

		set fTimer to (fTimer + GetSecondsPassed);

		If (fTimer >= 0) && (nStage == 0);
			NVDLC04RadioTower01REF.playgroup forward 1;
			set nStage to 1;
		Endif

;		If (fTimer >= 1) && (nStage == 1);
;			NVDLC04RadioTower02REF.playgroup forward 1;
;			set nStage to 2;
;		Endif

		If (fTimer >= 1) && (nStage == 1);
			NVDLC04RadioTower03REF.playgroup forward 1;
			set nStage to 2;
		Endif

;		If (fTimer >= 3) && (nStage == 3);
;			NVDLC04RadioTower04REF.playgroup forward 1;
;			set nStage to 4;
;		Endif

		If (fTimer >= 2) && (nStage == 2);
			NVDLC04RadioTower05REF.playgroup forward 1;
			set nStage to 3;
		Endif
	EndIf

END

Scn	NVDLC04Road01MMCamp03WarheadFireworksSCRIPT

;
; J.S. 3/28/11

Short	bDoOnce
Int		nCount
Float	fTimer

BEGIN GameMode

	If (GetDisabled == 1)
		MarkForDelete;
		Return;
	Else
		If (bDoOnce == 0)
			If (nCount == 0)
				If (NVDLC04Road01Camp03rWarhead01REF.GetDisabled == 1)
					NVDLC04Road01Camp03rWarhead01REF.PlaceAtMe NVDLC04WarheadNukeExplosion;
					Set nCount to 1;
					Return;
				Endif
			Elseif (nCount == 1)
				If (fTimer >= 0.8)
					NVDLC04Road01Camp03rWarhead01REF.PlaceAtMe 40mmGrenadeExplosion;
					Set fTimer to 0;
					Set nCount to 2;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Elseif (nCount == 2)
				If (fTimer >= 1.2)
					NVDLC04Road01Camp03rWarhead01REF.PlaceAtMe BusNukeExplosion;
					Set fTimer to 0;
					Set nCount to 0;
					Set bDoOnce to 1;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Endif
		Else
			Disable;
			Return;
		Endif
	Endif

END

SCN NVDLC04EDEUpgradeSCRIPT


; Script to increment Camarader-E perk when the player finds an upgrade.
; TLS 03/25/2011
; TLS 03/30/2011 - Added pop-up message.
; TLS 04/04/2011 - Added scripting for sequential upgrade messages.
; TLS 04/06/2011 - Simplified scripting for sequential upgrade messages.
; TLS 04/12/2011 - Incrementing ED-Ecated Challenge


BEGIN OnAdd Player

	Player.AddPerk NVDLC04CamaraderEPerk;
	If (NVDLC04GenericPerkQuest.nCamaraderERank == 0)
		Set NVDLC04GenericPerkQuest.nCamaraderERank to 1;
		Player.AddPerk NVDLC04CamaraderECompanionPerk 1;
		ShowMessage NVDLC04EDEUpgradeMessage;
	ElseIf (NVDLC04GenericPerkQuest.nCamaraderERank == 1)
		Set NVDLC04GenericPerkQuest.nCamaraderERank to 2;
		Player.AddPerk NVDLC04CamaraderECompanionPerk 1;
		ShowMessage NVDLC04EDEUpgrade02Message;
	ElseIf (NVDLC04GenericPerkQuest.nCamaraderERank == 2)
		Set NVDLC04GenericPerkQuest.nCamaraderERank to 3;
		Player.AddPerk NVDLC04CamaraderECompanionPerk 1;
		ShowMessage NVDLC04EDEUpgrade03Message;
	ElseIf (NVDLC04GenericPerkQuest.nCamaraderERank == 3)
		Set NVDLC04GenericPerkQuest.nCamaraderERank to 4;
		ShowMessage NVDLC04EDEUpgrade04Message;
	ElseIf (NVDLC04GenericPerkQuest.nCamaraderERank == 4)
		Set NVDLC04GenericPerkQuest.nCamaraderERank to 5;
		ShowMessage NVDLC04EDEUpgrade05Message;
	EndIf

	IncrementScriptedChallenge NVDLC04AchievementEDECated;


END

scn NVDLC04MissileExplosionTimerSCRIPT


; Handles the timing of the missile explosion after it's launched from Silo 02.
; JSH 03/25/11

Short bInitialize		; Initial setup for variables.
Float fTimer;			; Time-holding variable.
Ref rExplosion;			; Reference to hold explosion.


BEGIN GameMode

	If ( bInitialize == 0 )
		SetInChargen 1;
		Set fTimer to 33;
		Set bInitialize to 1;
		Return;
	EndIf

	If ( fTimer > 0 )
		Set fTimer to ( fTimer - GetSecondsPassed )
		Return;
	Else
		SetInChargen 0;
		Set rExplosion to NVDLC04MissileExplosionMarker.PlaceAtMe NVDLC04MissileExplosion;
		rExplosion.setscale .7
		ApplyImageSpaceModifier MegatonNukeISFX;
		StopQuest NVDLC04MissileExplosionTimer;
		PlaySound NVDLC04QSTMissileLaunchPt3
	EndIf

END

SCN NVDLC04Silo02ExplosionTriggerSCRIPT


;Causes explosions to go off
;DM 3/24/11


REF	Explosion


BEGIN OnTriggerEnter Player

	Set Explosion to GetLinkedRef;
	Explosion.PlaceAtMe NVDLC04Silo02Explosion;
	Disable;
	MarkForDelete;

END

SCN NVDLC04TempleKillTriggerSCRIPT


;Kills or moves anything that falls into a hole.
;DM 3/24/11
;DM 4/7/11 - Added conditions for ED-E and if Ulysses is your friend to move them to the start of the level instead of kill them.


SHORT 	bPlayerDoOnce;
SHORT 	bUlyssesDoOnce;
REF		KilledActor;


BEGIN OnTrigger

	If (GetActionRef == Player) && (bPlayerDoOnce == 0)
		Player.Kill;
		ApplyImageSpaceModifier FadeToBlack9sISFX;
		Set bPlayerDoOnce to 1;
	ElseIf (GetActionRef == NVDLC04UlyssesREF) && (UlyssesBattleCodebox.bUlyssesIsFriend == 0) && (bUlyssesDoOnce == 0)
		NVDLC04UlyssesREF.Kill;
		UlyssesFootlocker.Enable;
		NVDLC04UlyssesREF.RemoveAllItems UlyssesFootlocker;
		Set UlyssesBattleCodebox.bUlyssesHasFallen to 1;
		Set bUlyssesDoOnce to 1;	
	ElseIf (GetActionRef == NVDLC04UlyssesREF) && (UlyssesBattleCodebox.bUlyssesIsFriend == 1)
		NVDLC04UlyssesREF.MoveTo EdEMoveToMarker;
	ElseIf (GetActionRef == NVDLC04EDEREF)
		NVDLC04EDEREF.MoveTo EdEMoveToMarker;	
	ElseIf (GetActionRef != Player) && (GetActionRef != NVDLC04UlyssesREF) && (GetActionRef != NVDLC04EDEREF)
		Set KilledActor to GetActionRef;
		KilledActor.Kill Player;
	EndIf

END

scn NVDLC04Road02TunnelerTriggerSCRIPT


; Causes the tunnelers in the underpass to spawn.
; JSH 03/24/11


BEGIN OnTrigger Player

	NVDLC04Road02JunctionDeathclaw01REF.Enable;
	NVDLC04Road02JunctionDeathclaw02REF.Enable;

	NVDLC04Road02TunnelerParentREF.Enable;
	Disable;

END

scn NVDLC04UlyssesLootTriggerSCRIPT


; Handles loot transfer if Ulysses pulls an Emperor and ends up in a bottomless pit.
; JSH 03/23/11
; DM 3/24/11 - Added conditional for message pop up
; TLS 04/04/2011 - Piggybacking on this trigger to give the player the Lonesome Road Perk
; TLS 04/06/2011 - Moving quest completion stuff here.
; TLS 05/12/2011 - Fixed stage quest gets set to.
; TLS 05/31/2011 - Teleporting ED-E in with the player in case the player tried to leave ED-E behind.


Short bDoOnce;


BEGIN OnTriggerEnter Player

	If (GetStage NVDLC04MQ03a < 30)
		SetStage NVDLC04MQ03a 30;
	EndIf

	If (GetObjectiveCompleted NVDLC04MQ03a 20 == 0) && (bDoOnce == 0)
		ShowMessage NVDLC04LonesomeRoadPerkMSG;
		Player.AddPerk NVDLC04LonesomeRoadPerk;
		Set bDoOnce to 1;
	EndIf

	If (UlyssesBattleCodebox.bUlyssesHasFallen == 1)
		ShowMessage NVDLC04UlyssesPackageMSG;
		Disable;
	EndIf

	If (NVDLC04EDEREF.GetInCell NVDLC04DivideUlyssesTemple != 1) && (NVDLC04EDEREF.GetDisabled == 0)
		NVDLC04EDEREF.MoveTo EdEMoveToMarker;
		Set NVDLC04EDEREF.Waiting to 0;
	EndIf

END

SCN NVDLC04EDEFinaleQuestSCRIPT


; Controller for the final sequence wherein ED-E sacrifices himself to stop the missile launch.
; TLS 03/23/2011
; TLS 03/28/2011 - Tweaking sequence
; TLS 03/29/2011 - Adding Anim states
; TLS 03/30/2011 - Added removal of ED-E companion perk.
; TLS 03/31/2011 - Tweaked timing and replaced SGTM slomo with something less hacky.
; TLS 04/01/2011 - Added anim for missile retracting.
; TLS 04/13/2011 - Moved ED-E firing so player can't interact with him during the sequence.
; DLM 6/30/11 - Set ED-E to Ghost to keep the player from knocking him unconscious during the sequence.


Short bRunTimer;
Short nStage;
Float fTimer;
Float fEDEZPos;
Ref rExplosion;



BEGIN GameMode

	If (bRunTimer == 1)
		If (fTimer >= 0)
			Set fTimer to (fTimer - GetSecondsPassed);
		ElseIf (nStage == 0)
			NVDLC04EDEREF.Say NVDLC04DialogueEDENVDLC04EDETopic063;
			TempleLaunchControl.SetDestroyed 1;
			NVDLC04FinaleBlockerREF.Enable;
			NVDLC04EDEREF.UseWeapon NVDLC04WeapEDEUnlockZapper NVDLC04EDEREF NVDLC04FinaleEDETargetMarkerREF 25 0 0 1 0;
			Set NVDLC04EDEFinaleQuest.nStage to 1;
			Set NVDLC04EDEFinaleQuest.fTimer to 6;
			PlayMusic NVDLC04TransitionStinger;
			PlaySound musNVDLC04DeactivateNuke;
			NVDLC04EDEREF.SetGhost 1;
			Return;
		ElseIf (nStage == 1)
			NVDLC04EDEREF.CastImmediateOnSelf FlamerCritEffect;
			Set UlyssesBattleCodebox.bTempleCollapse to 1;
			Set nStage to 2;
			Set fTimer to 8;
			Return;
		ElseIf (nStage == 2)
			NVDLC04EDEREF.CastImmediateOnSelf FlamerCritEffect;
			Set rExplosion to NVDLC04EDEREF.PlaceAtMe NVDLC04EDESparkExplosion 1;
			Set fEDEZPos to (NVDLC04EDEREF.GetPos Z + 150);
			rExplosion.SetPos Z fEDEZPos;
			;NVDLC04EDEREF.CastImmediateOnSelf DLC03TrapFieldShockEffect;
			Set NVDLC04EDEFinaleQuest.nStage to 3;
			Set NVDLC04EDEFinaleQuest.fTimer to 5;
			Return;
		ElseIf (nStage == 3)
			NVDLC04EDEREF.CastImmediateOnSelf FlamerCritEffect;
			NVDLC04EDEREF.CastImmediateOnSelf DLC03TrapFieldShockEffect;
			Set NVDLC04EDEFinaleQuest.nStage to 4;
			Set NVDLC04EDEFinaleQuest.fTimer to 5;
			Return;
		ElseIf (nStage == 4)
			NVDLC04EDEREF.CastImmediateOnSelf FlamerCritEffect;
			Set rExplosion to NVDLC04EDEREF.PlaceAtMe NVDLC04EDESparkExplosion 1;
			Set fEDEZPos to (NVDLC04EDEREF.GetPos Z + 160);
			rExplosion.SetPos Z fEDEZPos;
			NVDLC04EDEREF.CastImmediateOnSelf DLC03TrapFieldShockEffect;
			NVDLC04EDEREF.PlayIdle SpecialIdleExplode;
			Set NVDLC04EDEFinaleQuest.nStage to 5;
			Set NVDLC04EDEFinaleQuest.fTimer to 2.75;
			Player.CastImmediateOnSelf NVDLC04SloMoEffect;
			NVDLC04EDEREF.Say Death;
			Return;
		ElseIf (nStage == 5)
			Set rExplosion to NVDLC04EDEREF.PlaceAtMe NVDLC04EDEZapExplosion 1;
			Set fEDEZPos to (NVDLC04EDEREF.GetPos Z + 150);
			rExplosion.SetPos Z fEDEZPos;
			Set NVDLC04EDEFinaleQuest.nStage to 6;
			Set NVDLC04EDEFinaleQuest.fTimer to 1;
			Return;
		ElseIf (nStage == 6)
			NVDLC04EDEREF.SetGhost 0;
			SetEssential NVDLC04EDE 0;
			Set fTimer to 0.1;
			Set nStage to 7;
			Return;
		ElseIf (nStage == 7)
			Set NVDLC04DialogueEDE.bEDEHired to 0;
			Set NVDLC04DialogueEDE.bEDEFired to 1;
			; Clean up companion factions.
			NVDLC04EDEREF.RemoveFromFaction FollowerFaction;
			NVDLC04EDEREF.SetPlayerTeammate 0;
			NVDLC04EDEREF.AddToFaction RobotFaction 1;

			NVDLC04EDEREF.KillActor NVDLC04EDEREF 0;
			Set fTimer to 1.5;
			Set nStage to 8;
			Return;
		ElseIf (nStage == 8)
			NVDLC04FinaleBlockerREF.Disable;
			TempleLaunchControl.PlayGroup Left 1;
			UlyssesRocketREF.PlayGroup Backward 1;
			; Remove companion perk
			NVDLC04EDEREF.RemovePerk CompanionSuite;
			Player.RemovePerk NVDLC04EnhancedSensors;
			ShowMessage FollowerMessagePerkEDERemove;
			SetObjectiveCompleted NVDLC04MQ03b 25 1;
			SetStage NVDLC04MQ03b 20;
			StopQuest NVDLC04EDEFinaleQuest;
		EndIf
	EndIf

END

Scn	NVDLC04TunnelDeathclawRushTriggerSCRIPT

; This trigger volume script kickstarts the tunnel's deathclaw run to its death. This is done to prevent players from getting too close to the deathclaw's death scene.
; J.S. 03/22/11


BEGIN OnTriggerEnter Player

	If (NVDLC04TunnelMoleRat01REF.GetDistance NVDLC04TunnelMoleRat01Point02REF <= 512 && NVDLC04TunnelMoleRat01REF.GetDead != 1)
		If (NVDLC04TunnelBox.bRunPackage != 1)																				; If the scripted scene's codebox has not yet registered that this deathclaw must change to a "run" package.
			NVDLC04TunnelMoleRat01REF.AddScriptPackage NVDLC04PackageTunnelMoleRat01PatrolToDeath02;	; We give the deathclaw this run package.
			Set NVDLC04TunnelBox.bRunPackage to 1;																		; And update the variable in the codebox.
			If (NVDLC04TunnelBox.nState == 3)
				Set NVDLC04TunnelBox.nCount to 0;
				Set NVDLC04TunnelBox.fTimer to 0;
			Endif
			Set NVDLC04TunnelBox.bPlayerInterference to 1;
		Endif
	Else
		If (NVDLC04TunnelMoleRat01REF.GetDead != 1 && NVDLC04TunnelMoleRat01REF.GetAV Aggression != 2)	; Only if the deathclaw's still alive, though.
			NVDLC04TunnelMoleRat01REF.SetAV Aggression 2;
			NVDLC04TunnelMoleRat01REF.StartCombat Player;
			Set NVDLC04TunnelBox.nCount to 0;
			Set NVDLC04TunnelBox.nState to 0;
			Set NVDLC04TunnelBox.fTimer to 0;
			Set NVDLC04TunnelBox.nScene to 1;
		Endif
	Endif

	Set NVDLC04TunnelTunnelerWave01VenREF.bRetreat to 1;
	Set NVDLC04TunnelTunnelerWave01AmbREF.bRetreat to 1;
	Set NVDLC04TunnelTunnelerWave01BruteREF.bRetreat to 1;

	Set NVDLC04TunnelTunnelerWave02VenREF.bRetreat to 1;
	Set NVDLC04TunnelTunnelerWave02AmbREF.bRetreat to 1;
	Set NVDLC04TunnelTunnelerWave02BruteREF.bRetreat to 1;

	Disable;
	MarkForDelete;

END

SCN	NVDLC04UlyssesEyebotDeathSCRIPT


;Removes the body and replaces it with some spare parts
;DM 3/22/11
;DM 5/6/11 - Removed spare parts spawing.


BEGIN OnDeath

	PlaceAtMe EyebotExplosion;
	Disable;
	MarkForDelete;

END

SCN	NVDLC04TempleMarkedMenHunterSCRIPT


;Removes the body and replaces it with an ash pile
;Has the hunter use a stealth boy when the player gets close.
;DM 3/22/11
;DM 3/29/11 - Added explosion effect when the MM turn to ash
;DM 5/6/11 - Removing the ash piles and changing the explosion
;DM 5/31/11 - Swapped the Game Mode section of the script with the Scout version
; JS 8/1/11 - Reworked entire script so that the disintegration FX do not increase challenge. The FX code also does no longer apply even if the marked men were killed by enery weapons.
; Finally, I've removed the OnDeath block from the previous script, and incorporated any GameMode code (related to distance combat behaviors) into the new scripting structure.

SHORT DoOnce;		;Only add the grenade package once when the player is close
Short	nDead	; JS 8/1/11
Short	nCount	; JS 8/1/11
Float 	fTimer	; JS 8/1/11

BEGIN OnHit

	If (nDead == 0)
		If (GetAV Health <= 0 || GetDead == 1)
			If (IsInCriticalStage None != 1)
				Set nDead to 2;
				Return;
			Else
				Set nDead to 1;
				Return;
			Endif
		Endif
	Endif

END

BEGIN GameMode

	If (GetDisabled == 0)
		If (nDead == 2)
			Return;
		Elseif (nDead == 0)
			If (GetAV Health <= 0 || GetDead == 1)
				If (IsInCriticalStage None != 1)
					Set nDead to 2;
					Return;
				Else
					Set nDead to 1;
					PlaceAtMe MinePlasmaExplosion;
					Return;
				Endif
			Else
				If (GetDistance Player <= 756) && (DoOnce == 0)
					AddScriptPackage NVDLC04TempleMarkedMenThrowGrenade;
					Set DoOnce to 1;
				ElseIf (GetDistance Player > 756)
					Set DoOnce to 0;
				EndIf
			Endif
		Elseif (nDead == 1)
			If (nCount == 0)
				If (fTimer >= 2)
					PlaceAtMe MinePlasmaExplosion;
					Resurrect;
					Set nCount to 1;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Elseif (nCount == 1)
				SAA 0;
				Set nCount to 2;
				Return;
			Elseif (nCount == 2)
				KillActor NVDLC04UlyssesREF;
				Set fTimer to 0;
				Set nCount to 3;
				Return;
			Elseif (nCount == 3)
				If (fTimer >= 0.1)
					CIOS PlasmaEffect;
					Set nCount to 4;
					Set fTimer to 0;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Elseif (nCount == 4)
				If (fTimer >= 1)
					SAA 1;
					Set nDead to 2;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Endif
		Endif
	Endif

END

SCN	NVDLC04TempleMarkedMenScoutSCRIPT


;Removes the body and replaces it with an ash pile. 
;Has the scout throw a grenade at the player when they get close.
;DM 3/22/11
;DM 3/29/11 - Added explosion effect when the MM turn to ash
;DM 5/6/11 - Removing the ash piles and changing the explosion
;DM 5/31/11 - Swapped the Game Mode section of the script with the Hunter version
; JS 8/1/11 - Reworked entire script so that the disintegration FX do not increase challenge. The FX code also does no longer apply even if the marked men were killed by enery weapons.
; Finally, I've removed the OnDeath block from the previous script, and incorporated any GameMode code (related to distance combat behaviors) into the new scripting structure.


SHORT DoOnce;		;Only add the stealth package once when the player is close
Short	nDead	; JS 8/1/11
Short	nCount	; JS 8/1/11
Float 	fTimer	; JS 8/1/11


BEGIN OnHit

	If (nDead == 0)
		If (GetAV Health <= 0 || GetDead == 1)
			If (IsInCriticalStage None != 1)
				Set nDead to 2;
				Return;
			Else
				Set nDead to 1;
				Return;
			Endif
		Endif
	Endif

END

BEGIN GameMode

	If (GetDisabled == 0)
		If (nDead == 2)
			Return;
		Elseif (nDead == 0)
			If (GetAV Health <= 0 || GetDead == 1)
				If (IsInCriticalStage None != 1)
					Set nDead to 2;
					Return;
				Else
					Set nDead to 1;
					PlaceAtMe MinePlasmaExplosion;;
					Return;
				Endif
			Else
				If (GetDistance Player <= 256) && (DoOnce == 0)
					AddScriptPackage NVDLC04TempleMarkedMenStealthBoy;
					Set NVDLC04UlyssesCombatBarks.bFriendMMUsesStealthboy to 1;
					Set DoOnce to 1;
				Elseif (GetDistance Player > 256)
					Set DoOnce to 0;
				Endif
			Endif
		Elseif (nDead == 1)
			If (nCount == 0)
				If (fTimer >= 2)
					PlaceAtMe MinePlasmaExplosion;
					Resurrect;
					Set nCount to 1;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Elseif (nCount == 1)
				SAA 0;
				Set nCount to 2;
				Return;
			Elseif (nCount == 2)
				KillActor NVDLC04UlyssesREF;
				Set fTimer to 0;
				Set nCount to 3;
				Return;
			Elseif (nCount == 3)
				If (fTimer >= 0.1)
					CIOS PlasmaEffect;
					Set nCount to 4;
					Set fTimer to 0;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Elseif (nCount == 4)
				If (fTimer >= 1)
					SAA 1;
					Set nDead to 2;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Endif
		Endif
	Endif

END

