				Set nDead to 1;
				Return;
			Endif
		Endif
	Endif

END

BEGIN GameMode

	If (GetDisabled == 0)
		If (nDead == 2)
			Return;
		Elseif (nDead == 0)
			If (GetAV Health <= 0 || GetDead == 1)
				If (IsInCriticalStage None != 1)
					Set nDead to 2;
					Return;
				Else
