					Set nCount to 1;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Elseif (nCount == 1)
				SAA 0;
				Set nCount to 2;
				Return;
			Elseif (nCount == 2)
				KillActor NVDLC04UlyssesREF;
				Set fTimer to 0;
				Set nCount to 3;
				Return;
			Elseif (nCount == 3)
				If (fTimer >= 0.1)
					CIOS PlasmaEffect;
					Set nCount to 4;
					Set fTimer to 0;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Elseif (nCount == 4)
				If (fTimer >= 1)
					SAA 1;
					Set nDead to 2;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Endif
		Endif
	Endif

END

SCN	NVDLC04TempleMarkedMenMarauderSCRIPT


;Removes the body and replaces it with an ash pile
;DM 3/22/11
;DM 3/29/11 - Added explosion effect when the MM turn to ash
;DM 5/6/11 - Removing the ash piles and changing the explosion
; JS 8/1/11 - Reworked entire script so that the disintegration FX do not increase challenge. The FX code also does no longer apply even if the marked men were killed by enery weapons.
; Finally, I've removed the OnDeath block from the previous script, and incorporated any GameMode code (related to distance combat behaviors) into the new scripting structure.

Short	nDead	; JS 8/1/11
Short	nCount	; JS 8/1/11
Float 	fTimer	; JS 8/1/11


BEGIN OnHit

	If (nDead == 0)
		If (GetAV Health <= 0 || GetDead == 1)
			If (IsInCriticalStage None != 1)
				Set nDead to 2;
				Return;
			Else
				Set nDead to 1;
				Return;
			Endif
		Endif
	Endif

END

BEGIN GameMode

	If (GetDisabled == 0)
		If (nDead == 2)
			Return;
		Elseif (nDead == 0)
			If (GetAV Health <= 0 || GetDead == 1)
				If (IsInCriticalStage None != 1)
					Set nDead to 2;
					Return;
				Else
					Set nDead to 1;
					PlaceAtMe MinePlasmaExplosion;;
					Return;
				Endif
			Endif
		Elseif (nDead == 1)
			If (nCount == 0)
				If (fTimer >= 2)
					PlaceAtMe MinePlasmaExplosion;
					Resurrect;
					Set nCount to 1;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Elseif (nCount == 1)
				SAA 0;
				Set nCount to 2;
				Return;
			Elseif (nCount == 2)
				KillActor NVDLC04UlyssesREF;
				Set fTimer to 0;
				Set nCount to 3;
				Return;
			Elseif (nCount == 3)
				If (fTimer >= 0.1)
					CIOS PlasmaEffect;
					Set nCount to 4;
					Set fTimer to 0;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Elseif (nCount == 4)
				If (fTimer >= 1)
					SAA 1;
					Set nDead to 2;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Endif
		Endif
	Endif

END

SCN NVDLC04EDECommissaryAccessTriggerSCRIPT


; Script for ED-E to unlock commissaries for the player.
; TLS 03/22/2011
; TLS 03/29/2011 - Added AnimStates
; TLS 03/31/2011 - Removed glitchy audio.
; TLS 04/15/2011 - Adding scripting to make ED-E fly closer before activating.
; TLS 05/02/2011 - Adding DoOnce control to OnTriggerEnter
; TLS 05/19/2011 - Adding timers for anim states.
; CES 6/23/11 - Updated the add script package to work for each individual commissary so EdE will travel to the terminal, not the player.
; TLS 08/02/2011 - Added commissary 6.


Ref rLinkedRef;
Short bRunTimer;
Float fTimer;


BEGIN OnTriggerEnter Player

	Set rLinkedRef to GetLinkedRef;
	If (NVDLC04DialogueEDE.bEDEHired == 1) && (NVDLC04EDEREF.Waiting == 0)
		rLinkedRef.PlayGroup Forward 0;

		If (NVDLC04EDEREF.GetInCell NVDLC04Silo01 == 1)
			;Commissary 1 - Silo01
			NVDLC04EDEREF.AddScriptPackage NVDLC04EDEMoveToPlayerCommissary01;
		ElseIf (NVDLC04EDEREF.GetInCell NVDLC04SLHopeVilleMensBarracks == 1)
			;Commissary 2 - Hopeville Men's Barracks
			NVDLC04EDEREF.AddScriptPackage NVDLC04EDEMoveToPlayerCommissary02;
		ElseIf (NVDLC04EDEREF.GetInCell NVDLC04RoadHQ == 1)
			;Commissary 3 - Hopeville HQ
			NVDLC04EDEREF.AddScriptPackage NVDLC04EDEMoveToPlayerCommissary03;
		ElseIf (NVDLC04EDEREF.GetInCell NVDLC04Silo02 == 1)
			;Commissary 4 - Silo02
			NVDLC04EDEREF.AddScriptPackage NVDLC04EDEMoveToPlayerCommissary04;
		ElseIf (NVDLC04EDEREF.GetInCell NVDLC04DivideTempleMaint == 1)
			;Commissary 5 - Ulysses' Temple
			NVDLC04EDEREF.AddScriptPackage NVDLC04EDEMoveToPlayerCommissary05;
		ElseIf (Player.GetInWorldspace NVDLC04Road02World == 1)
			;Commissary 6 - High Road
			NVDLC04EDEREF.AddScriptPackage NVDLC04EDEMoveToPlayerCommissary06;
		Else
			;Fall back to just moving to the player if the above doesn't work.
			NVDLC04EDEREF.AddScriptPackage NVDLC04EDEMoveToPlayerCommissary;
		EndIf
		Disable;
		MarkForDelete;
	EndIf

END

Scn		NVDLC04Silo01Floor01AwakenBotTriggerSCRIPT

; This script awakens the first floor sentry bot upon crossing its associated trigger.

; J.S. 3/1/11

Short	bDoOnce				; 1 = bot enabled, and trigger is now ready for deletion.

BEGIN OnTriggerEnter Player

	If (bDoOnce == 0)													; Simple DoOnce check, to enable the robot of silo's first floor. We keep it disabled to help performance, and aggro, when players are turning the silo's power back on (bottom level).
		NVDLC04Silo01Floor01Bot01REF.Enable;
		Set bDoOnce to 1;
		Disable;
		MarkForDelete;
	Endif

END

SCN NVDLC04EDECommissarySCRIPT


; ED-E's special access panel to allow him to unlock barter points for the player.
; TLS 03/21/2011
; TLS 04/07/2011 - Complete optional commissary objective.
; TLS 05/19/2011 - Added timer to set animation states.
; CES 6/23/11 - Updated the add script package to work for each individual commissary so EdE will travel to the terminal, not the player.
; TLS 07/18/2011 - Removed obsolete code for weapon use.
; ABR 07/22/2011 - Added scripting to reset Vendor Container's Caps if they get to high to prevent Barter issues.
; TLS 08/02/2011 - Added commissary 6
; ABR 08/09/2011 - Fixed scripting to check the Number of Caps in the Repair Merchant as well as the Vendor Container.


Short bUnlocked;				;  Flag for whether ED-E has unlocked the terminal yet.
Short nDisplay;				; Flag for whether the Commissary message box is popped up - 1 = locked message, 2 = unlocked message.
Short nButton;					; Controller for button functionality.
Ref rLinkedRef;				; Reference used for Merchant/Repair NPC
Ref rSelf;						; Reference for telling ED-E what to shoot.
Short bRunTimer;
Float fTimer;
Int nNumCapsVendor;		; Number of Caps in the Commissary Vendor Container.
Int nNumCapsRepair;			; Number of Caps the Commissary Repair Merchant has.


BEGIN OnActivate

	If (IsActionREF Player == 1) && (bUnlocked == 0)
		Set nDisplay to 1;
		Set rSelf to GetSelf;
		ShowMessage NVDLC04EDECommissaryLockedMSG;
	ElseIf (IsActionREF Player == 1) && (bUnlocked == 1)
		Set nDisplay to 2;
		Set rLinkedRef to GetLinkedRef;
		ShowMessage NVDLC04EDECommissaryUnlockedMSG;
	EndIf

	; Reset Caps if the get to High.
	set nNumCapsVendor to NVDLC04VendorContainerCommissaryREF.getitemcount caps001
	set nNumCapsRepair to rLinkedRef.getitemcount caps001

	If ((nNumCapsVendor + nNumCapsRepair) > 6000) || ((nNumCapsVendor + nNumCapsRepair) < 200)
		NVDLC04VendorContainerCommissaryREF.removeitem caps001 nNumCapsVendor
		NVDLC04VendorContainerCommissaryREF.additem caps001 6000
		rLinkedRef.removeitem caps001 nNumCapsRepair
	EndIf

END

BEGIN OnHit NVDLC04EDEREF

	If (bUnlocked == 0)
		ShowMessage NVDLC04EDECommissaryMSG;
		PlaySound OBJComputerTerminalArc;
		PlayGroup Left 1;
		PlaySound OBJComputerTerminalForward;
		Set bUnlocked to 1;
		Set bRunTimer to 1;
		If (GetObjectiveCompleted NVDLC04MQ01a 25 == 0)
			SetObjectiveCompleted NVDLC04MQ01a 25 1;
		EndIf
		NVDLC04EDEREF.ResetAI;
	EndIf

END

BEGIN GameMode

	If (nDisplay == 1)
		Set nButton to GetButtonPressed
		; Cancel Option
		If (nButton == 0)
			Set nDisplay to 0;
		; Commissary Option
		ElseIf (nButton == 1)
			PlayGroup Forward 1;
			If (NVDLC04EDEREF.GetInCell NVDLC04Silo01 == 1)
				;Commissary 1 - Silo01
				NVDLC04EDEREF.AddScriptPackage NVDLC04EDEMoveToPlayerCommissary01;
			ElseIf (NVDLC04EDEREF.GetInCell NVDLC04SLHopeVilleMensBarracks == 1)
				;Commissary 2 - Hopeville Men's Barracks
				NVDLC04EDEREF.AddScriptPackage NVDLC04EDEMoveToPlayerCommissary02;
			ElseIf (NVDLC04EDEREF.GetInCell NVDLC04RoadHQ == 1)
				;Commissary 3 - Hopeville HQ
				NVDLC04EDEREF.AddScriptPackage NVDLC04EDEMoveToPlayerCommissary03;
			ElseIf (NVDLC04EDEREF.GetInCell NVDLC04Silo02 == 1)
				;Commissary 4 - Silo02
				NVDLC04EDEREF.AddScriptPackage NVDLC04EDEMoveToPlayerCommissary04;
			ElseIf (NVDLC04EDEREF.GetInCell NVDLC04DivideTempleMaint == 1)
				;Commissary 5 - Ulysses' Temple
				NVDLC04EDEREF.AddScriptPackage NVDLC04EDEMoveToPlayerCommissary05;
			ElseIf (Player.GetInWorldspace NVDLC04Road02World == 1)
				;Commissary 6 - High Road
				NVDLC04EDEREF.AddScriptPackage NVDLC04EDEMoveToPlayerCommissary06;
			Else
				;Fall back to just moving to the player if the above doesn't work.
				NVDLC04EDEREF.AddScriptPackage NVDLC04EDEMoveToPlayerCommissary;
			EndIf
		EndIf
	ElseIf (nDisplay == 2)
		Set nButton to GetButtonPressed
		; Cancel Option
		If (nButton == 0)
			Set nDisplay to 0;
		; Commissary Option
		ElseIf (nButton == 1)
			rLinkedRef.Activate Player 1;
			Set nDisplay to 0;
		; Repair Option
		ElseIf (nButton == 2)
			rLinkedRef.ShowRepairMenu;
			Set nDisplay to 0;
		EndIf
	EndIf

	If (bRunTimer == 1)
		If (fTimer >= 1)
			PlayGroup SpecialIdle 0;
			Set bRunTimer to 0;
		Else
			Set fTimer to (fTimer + GetSecondsPassed);
		EndIf
	EndIf


END

SCN NVDLC04HealUlyssesSCRIPT


;Heals Ulysses when he is hit by a projectile
;DM 3/21/11


BEGIN ScriptEffectStart

	RestoreActorValue PerceptionCondition 100;
	RestoreActorValue EnduranceCondition 100;
	RestoreActorValue LeftAttackCondition 100;
	RestoreActorValue RightAttackCondition 100;
	RestoreActorValue LeftMobilityCondition 100;
	RestoreActorValue RightMobilityCondition 100;
	RestoreActorValue Health 25;

END

BEGIN ScriptEffectUpdate

	RestoreActorValue PerceptionCondition 100;
	RestoreActorValue EnduranceCondition 100;
	RestoreActorValue LeftAttackCondition 100;
	RestoreActorValue RightAttackCondition 100;
	RestoreActorValue LeftMobilityCondition 100;
	RestoreActorValue RightMobilityCondition 100;
	RestoreActorValue Health 25;

END

BEGIN ScriptEffectFinish

	RestoreActorValue PerceptionCondition 100;
	RestoreActorValue EnduranceCondition 100;
	RestoreActorValue LeftAttackCondition 100;
	RestoreActorValue RightAttackCondition 100;
	RestoreActorValue LeftMobilityCondition 100;
	RestoreActorValue RightMobilityCondition 100;
	RestoreActorValue Health 25;

END

scn NVDLC04TrapLaserTripwireEmitterSCRIPT


; Activator script for the laser tripwire emitter.
; JSH 03/21/11


ref rBeam				; This is the laser beam reference. This emitter should be linked to the associated beam

Short bSkillPassed	; If the player has passed the skill check, look for button press when in the game mode block.
Short nButton			; button var for menus


BEGIN OnActivate

	If  ( IsActionRef Player == 1 )

		Set rBeam to GetLinkedRef;

		If ( rBeam.GetDisabled == 1 )
			ShowMessage NVDLC04LaserTripwireEmitterHarmlessMSG;
			SetDestroyed 1;
		Else
			ShowMessage NVDLC04LaserTripwireEmitterMSG;
			Set bSkillPassed to 1;
		Endif

	EndIf

END


BEGIN GameMode

	If ( bSkillPassed == 1	)										;don't run stuff in game mode unless recently activated 

		Set nButton to GetButtonPressed						;grab the button state, you don't know when this will be set

			If ( nButton > -1	)										;if a valid response is received start doing stuff

				If  ( nButton == 1 )
					ShowMessage TrapLaserDisabled;
					rBeam.Disable;
					rBeam.MarkForDelete;
					RewardXP 90;
					Set bSkillPassed to 0;						;stop running stuff 
					SetDestroyed 1;
				Else
					Set bSkillPassed to 0	;	
				EndIf

			EndIf

	EndIf

END

scn NVDLC04TrapLaserTripwireBeamSCRIPT


; Activator script for the laser tripwire beam.
; JSH 03/21/11


Ref rActionRef;							; Whoever's crossing the beam.
Ref rLinkedRef							; Linked missile launcher trap.


BEGIN OnTriggerEnter

	Set rLinkedRef to GetLinkedRef;
	Set rActionRef to GetActionRef;

	If ( rActionRef.HasPerk LightStep == 0 )
		PlaySound3D TRPTripwireLaser;
		rLinkedRef.FireWeapon WeapMissileLauncher;
		Disable;
		MarkForDelete;
	EndIf

END

