Scn	NVDLC04Silo01EDEReleaseCodeHolderSCRIPT

; Script to handle the scripted scene where EDE is released from its pod in Hopeville's Missile Silo interior.

; J.S. 3/19/11
; TLS 04/01/2011 - Added forcegreet to end of sequence.
; CES 07/12/77 - Added in script to make EdE ghost until he talks to you.

Short	bActive					; 1 = Player just unlocked EDE's pods.
Ref	rExp						; Ref of placeatme explosion when EDE is released.

Int		nState					; Operational variable, for large sequences.
Int		nCount					; Operational variable, for precise timings.
Float	fTimer					; Timer float.

BEGIN GameMode

	If (GetDisabled == 1)																										; If codebox is disabled, skip reading script.
		Return;
	Else																															; If codebox is enabled, continue with script.
		If (bActive == 1)																											; 
			If (nState == 0)																										;
				If (nCount == 0)
					If (fTimer >= 1)
						NVDLC04EDEREF.PlaySound AMBHooverTrbnRmSteamOS;
						NVDLC04EDEREF.PlaySound NVDLC04QSTEdeReveal;
						Set nCount to 1;
						Return;
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
					Endif
				Elseif (nCount == 1)
					If (fTimer >= 2)
						NVDLC04EDEREF.PlaySound AMBHooverTrbnRmSteamOS;
						NVDLC04Silo01EDERevealEffectsMarkerREF.Enable;
						Set fTimer to 0;
						Set nCount to 2;
						Return;
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
					Endif
				Elseif (nCount == 2)
					If (fTimer >= 3)
						NVDLC04EDEREF.PlaySound AMBHooverTrbnRmSteamOS;
						NVDLC04Silo01EDERevealEffects02MarkerREF.Enable;
						Set fTimer to 0;
						Set nCount to 3;
						Return;
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
					Endif
				Elseif (nCount == 3)
					If (fTimer >= 3)
						NVDLC04EDESpawnLightFX01REF.Enable;
						Set fTimer to 0;
						Set nCount to 4;
						Return;
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
					Endif
				Elseif (nCount == 4)
					If (fTimer >= 1)
						NVDLC04EDEREF.PlaySound AMBHooverTrbnRmSteamOS;
						NVDLC04EDEPodREF.PlayGroup Forward 0;
						Set fTimer to 0;
						Set nCount to 0;
						Set nState to 1;
						Return;
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
					Endif
				Endif
			Elseif (nState == 1)																								; 
				If (nCount == 0)
					If (fTimer >= 0.6)
						Set rExp to NVDLC04EDEStaticREF.PlaceAtMe NVDLC04EDEReleaseExplosion;
						rExp.SetScale 0.35;
						NVDLC04EDEOffLightREF.Disable;
						Set fTimer to 0;
						Set nCount to 1;
						Return;
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
					Endif
				Elseif (nCount == 1)																							; 
					If (fTimer >= 0.5)
						NVDLC04EDEStaticREF.Disable;
						NVDLC04EDEREF.Enable;
						NVDLC04EDEREF.SetGhost 1;
						NVDLC04EDEOnLightREF.Enable;
						NVDLC04Silo01EDERevealEffectsMarkerREF.Disable;
						NVDLC04Silo01EDERevealEffects02MarkerREF.Disable;
						NVDLC04EDESpawnLightFX01REF.Disable;
						Set fTimer to 0;
						Set nCount to 2;
						Return;
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
					Endif
				Elseif (nCount == 2)																							; 
					NVDLC04EDEREF.PlaySound OBJZAXConsoleVialForward
					Set fTimer to 0;
					Set nCount to 3;
					Return;
				Elseif (nCount == 3)	
					If (fTimer >= 4)
						If (NVDLC04EDEREF.GetTalkedToPC == 0)
							NVDLC04EDEREF.AddScriptPackage NVDLC04EDEForceGreetPACKAGE;			;
						Endif
						NVDLC04Silo01MaintRoomDoorREF.Unlock;
						Set fTimer to 0;
						Set nCount to 0;
						Set nState to 2;
						Return;
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
					Endif
				Endif
			Elseif (nState == 2)																								;
			Elseif (nState == 3)
			Endif
		Endif
	Endif

END

scn NVDLC04UlyssesGrenadeTriggerSCRIPT


; Causes Ulysses to toss some grenades at the position.
; JSH 03/18/11
; DM 3/31/11 - Added conditional that Ulysses is in combat with the player


Short bDoOnce;				; Trigger grenade volley only once.


BEGIN OnTrigger Player

	If ( bDoOnce == 0 ) && (NVDLC04UlyssesREF.IsInCombat == 1) && (UlyssesBattleCodebox.bUlyssesIsFriend == 0)

		If ( NVDLC04UlyssesREF.bPlasmaVolleyDone == 0 )
			NVDLC04UlyssesREF.AddItem WeapGrenadePlasma 3;
			NVDLC04UlyssesREF.AddScriptPackage NVDLC04UlyssesPlasmaGrenadeVolley;
			Set NVDLC04UlyssesREF.bPlasmaVolleyDone to 1;
		Else
			NVDLC04UlyssesREF.AddItem WeapNVGrenadeIncendiary 3;
			NVDLC04UlyssesREF.AddScriptPackage NVDLC04UlyssesFireGrenadeVolley;
		EndIf

		Set bDoOnce to 1;
		Disable;

	EndIf

END

scn NVDLC04GenericPerkQuestSCRIPT


; Created for scripted perk actions in NVDLC04
; ETB 3/18/11
; TLS 03/25/2011 	- Added scripting for ED-E upgrade notes 4 and 5.
; TLS 03/28/2011 	- Removed previous update - GameMode script isn't necessary.
; TLS 04/01/2011 	- Added variable for Karma-based perk and added scripting for Just Lucky I'm Alive & Irradiated Beauty.
; TLS 04/04/2011 	- Added message pop-up for Just Lucky I'm Alive and scripting for endgame perks.
; TLS 04/05/2011 	- Added Scripting for Roughin' It.
; TLS 04/06/2011 	- Added script to award player the Marked perk.
; ETB 04/06/2011	- Added scripting for Voracious Reader perk.
; ETB 04/09/2011 	- Added scripting for Roughin' It! Item.
; OMP 04/12/2011 - nBurnedBook07Count was being counted twice; fixed that.
; TLS 04/13/2011 - Adding variables for ED-E skill checks.
; TLS 05/10/2011 - Adding scripting for Red Glare upgrade achievement.
; TLS 05/20/2011 - Removing XP award from Voracious Reader.
; TLS 05/31/2011 - Adding scripting for Camarader-E hourly perk.
; TLS 06/15/2011 - Added autosave after assigning endgame perks.
; CES 08/03/11 - Updated how the Marked perk is given to the player. Works after killing 3 of the 4 named marked men instead of just the first guy.


; ---------- Item: Rushing Water Variables ------	\/
Short bRushingWaterPerkAdd						; 0 = Associated hidden perk not added yet 1 = Perk added (don't add again)
; ---------- Perk: Camarader-E Variables ---------	\/
Short bCamaraderEWeapRepairON
Short bCamaraderEArmorRepairON
Short bCamaraderEMakeEnergyCellsON
Short bCamaraderEDailyWeapRepair			; 0 = Repair available 1 = Repair used for the day
Short bCamaraderEDailyArmorRepair			; 0 = Repair available 1 = Repair used for the day
Short bCamaraderEHourlyEnergyCells			; 0 = Energy Cells available 1 = Energy Cells used for the hour
Short nCamaraderERank;
Short nExplosivesCheck;							; 1 = Flamer Fuel check passed, 2 = Satchel Charges check passed.
Short nRepairCheck;									; 1 = Weapon repair optimization check passed, 2 = Extra ammo check passed
; -------	Perk: Roughin' It! Variables --------------	\/
Short bFirstPickedUp									; [NOTE: This variable is for the item (NVDLC04RoughinItBedrollKit) not the perk.]
Short bLetsSleep										; <<<
Short bPause
; -------	Perk: Voracious Reader Variables ----	\/
Short nBurnedBook08Count 
Short nBurnedBook07Count 
Short nBurnedBook06Count 
Short nBurnedBook05Count 
Short nBurnedBook04Count 
Short nBurnedBook03Count 
Short nBurnedBook02Count 
Short nBurnedBook01Count 
Short nBurnedBookTotal 
Short nBookCount 
Short bBlankMagFirstPickup						; Used to display a message the first time the Player gets a Blank Magazine. Used in the script "NVDLC04BlankMagazineSCRIPT"
; -------	Perk: Marked Variables ----	\/
Short nMarkedCount									; Counts the number of named marked men killed.
Short bMarkedBlister
Short bMarkedBonesaw
Short bMarkedBlade
Short bMarkedBeast

Short nKarmaAbs;										; Absolute value of player's Karma
Short nKarma;											; Player's actual karma

Short bPlayerInCombat;								; Used to simulate an OnCombatEnd block

Short bPlayerSleep;									; Used to check whether the player has been sleeping as opposed to just waiting.

Short nEndgamePerk;								; Used as a DoOnce check for the four endgame perks

Short bAlphaStrike;
Short bAutoLauncher;
Short bFarSighting;
; -------	Perk: Camarader-E Variables ----	\/
Short bCamaraderEDoOnce;



BEGIN GameMode

	; Award Marked Perk when player kills three named marked men.
	If (NVDLC04Road01BossREF.GetDead == 1) && (bMarkedBlister == 0)
		Set bMarkedBlister to 1;
		Set nMarkedCount to nMarkedCount + 1;
	EndIf

	If (NVDLC04BeastREF.GetDead == 1) && (bMarkedBeast == 0)
		Set bMarkedBeast to 1;
		Set nMarkedCount to nMarkedCount + 1;
	EndIf

	If (NVDLC04BonesawREF.GetDead == 1) && (bMarkedBonesaw == 0)
		Set bMarkedBonesaw to 1;
		Set nMarkedCount to nMarkedCount + 1;
	EndIf

	If (BladeREF.GetDead == 1) && (bMarkedBlade == 0)
		Set bMarkedBlade to 1;
		Set nMarkedCount to nMarkedCount + 1;
	EndIf

	If (Player.HasPerk NVDLC04MarkedPerk == 0) && (nMarkedCount >= 3)
		Player.AddPerk NVDLC04MarkedPerk;
		ShowMessage NVDLC04MarkedPerkMSG;
	EndIf

; ----------------------------------------------------	C H A L L E N G E ---------------------------------------------	\/

	If (Player.GetItemCount ModNVDLC04RedGlareAlphaStrike >= 1) && (bAlphaStrike == 0)
		IncrementScriptedChallenge NVDLC04AchievementRocketsRedGlare;
		Set bAlphaStrike to 1;
	EndIf

	If (Player.GetItemCount ModNVDLC04RedGlareAutoLauncher >= 1) && (bAutoLauncher == 0)
		IncrementScriptedChallenge NVDLC04AchievementRocketsRedGlare;
		Set bAutoLauncher to 1;
	EndIf

	If (Player.GetItemCount ModNVDLC04RedGlareFarSighting >= 1) && (bFarSighting == 0)
		IncrementScriptedChallenge NVDLC04AchievementRocketsRedGlare;
		Set bFarSighting to 1;
	EndIf

; ----------------------------------------------------	P E R K S ----------------------------------------------------------	\/
	; Voracious Reader routine
	If ( Player.HasPerk NVDLC04VoraciousReaderPerk == 1 )
		If ( Player.GetItemCount NVDLC04BooksBurnedLIST >= 1 )
				Set nBurnedBook08Count to Player.GetItemCount BurnedBook08;
				Set nBurnedBook07Count to Player.GetItemCount BurnedBook07;
				Set nBurnedBook06Count to Player.GetItemCount BurnedBook06;
				Set nBurnedBook05Count to Player.GetItemCount BurnedBook05;
				Set nBurnedBook04Count to Player.GetItemCount BurnedBook04;
				Set nBurnedBook03Count to Player.GetItemCount BurnedBook03;
				Set nBurnedBook02Count to Player.GetItemCount BurnedBook02;
				Set nBurnedBook01Count to Player.GetItemCount BurnedBook01;
				Set nBurnedBookTotal to ( nBurnedBook08Count + nBurnedBook07Count  + nBurnedBook06Count + nBurnedBook05Count + nBurnedBook04Count + nBurnedBook03Count + nBurnedBook02Count + nBurnedBook01Count );
				Set nBookCount to nBurnedBookTotal;
				Player.RemoveItem BurnedBook08 nBurnedBook08Count;
				Player.RemoveItem BurnedBook07 nBurnedBook07Count;
				Player.RemoveItem BurnedBook06 nBurnedBook06Count;
				Player.RemoveItem BurnedBook05 nBurnedBook05Count;
				Player.RemoveItem BurnedBook04 nBurnedBook04Count;
				Player.RemoveItem BurnedBook03 nBurnedBook03Count;
				Player.RemoveItem BurnedBook02 nBurnedBook02Count;
				Player.RemoveItem BurnedBook01 nBurnedBook01Count;
				Player.AddItem NVDLC04BlankMagazineMISC nBookCount;
		Endif
	Endif

	; Endgame Perk effect
	If (nEndgamePerk == 1)
		Set nEndgamePerk to 2;
		Autosave;
	EndIf

	; IIrradiated Beauty/Roughin' It effect
	If (bPlayerSleep == 1)
		; Irradiated Beauty
		If (Player.HasPerk NVDLC04IrradiatedBeautyPerk == 1)
			Player.CastImmediateOnSelf NVDLC04IrradiatedBeautyPerkEffect;
		EndIf
		; Roughin' It
		If (Player.HasPerk NVDLC04RoughinItPerk == 1) && (Player.IsInInterior == 0)
			Player.CastImmediateOnSelf WellRestedSpell;
		EndIf
		Set bPlayerSleep to 0;
	EndIf	

	; Just Lucky I'm Alive effect
	If (Player.HasPerk NVDLC04JustLuckyImAlivePerk == 0)
		Return;
	Else
		If (Player.IsInCombat == 1) && (bPlayerInCombat == 0)
			Set bPlayerInCombat to 1;
			Return;
		ElseIf (Player.IsInCombat == 0) && (bPlayerInCombat == 1)
			If (Player.GetHealthPercentage <= 0.25)
				If (Player.IsSpellTarget NVDLC04JustLuckyImAlivePerkEffect == 0)
					Player.CastImmediateOnSelf NVDLC04JustLuckyImAlivePerkEffect;
					ShowMessage NVDLC04JustLuckyImAlivePerkMSG;
				EndIf
			EndIf
			Set bPlayerInCombat to 0;
		EndIf
	EndIf

END


BEGIN MenuMode 1012

	; Irradiated Beauty/Roughin' It functionality
	If (IsPCSleeping == 1) && (bPlayerSleep == 0)
		Set bPlayerSleep to 1;
	EndIf

END


BEGIN MenuMode 1048

	; Endgame Perk UI checks
	If (Player.HasPerk NVDLC04BearSlayerPerk == 1) || (Player.HasPerk NVDLC04DeadMansBurdenPerk == 1) || (Player.HasPerk NVDLC04ScourgeOfTheEastPerk == 1) || (Player.HasPerk NVDLC04DivideSurvivorPerk == 1)
		If (nEndgamePerk == 0)
			Set nEndgamePerk to 1;
		EndIf
	EndIf

END

scn NVDLC04BowieKnifeVATSSCRIPT

; VATS Attack "Dirt-Nap" (attached to Bowie Knife)
; Inflicts Fatigue damage on the target to temporarily knock it out.
; ETB 3/17/11

Ref BowieKnifeTarget



BEGIN ScriptEffectStart

	Set BowieKnifeTarget to GetSelf;

	If ( Player.IsSneaking )
		If ( BowieKnifeTarget != PlayerRef ) && ( BowieKnifeTarget.GetIsCreatureType 6 != 1 )
			BowieKnifeTarget.DamageAV Fatigue 1000;
			ShowMessage NVDLC04BowieKnifeVATSHitMSG;
		Else
			BowieKnifeTarget.DamageAV Health 15;
			ShowMessage NVDLC04BowieKnifeVATSNoBloodMSG;
		EndIf
	Else
		ShowMessage NVDLC04BowieKnifeVATSFailedMSG;
	Endif

END; scripteffectstart

