SCN NVDLC04TempleLaunchTerminalSCRIPT


; Script for the terminal that controls the post-boss-fight missile sequence.
; TLS 03/16/2011
; TLS 03/22/2011 - Added OnHit functions for ED-E's finale scene
; TLS 03/28/2011 - Removed OnHit functions for ED-E's finale scene; handled by timer now.
; DM 3/29/11 - Added Set UlyssesBattleCodebox.bTempleCollapse to 1 which causes the temple to start shaking.
; TLS 04/04/2011 - Fixed conditional on Nuke All option.
; TLS 04/12/2011 - Added SetDestroyed call.
; TLS 05/20/2011 - Set console so ED-E log only fires the first time the player asks for his help.
; TLS 07/22/2011 - Added lever pull to animation sequence.


Short nButton;					; Player's option selection in message box.
Short nMessage;				; Message box is currently displayed: 0 = no message, 1 = main menu, 2 = No abort message, 
									; 3 = NCR confirm message, 4 = Caesar confirm message, 5 = Both confirm message.
Short bRunTimer;
Short nStage;
Float fTimer;
Short bDoOnce;

Short bEDEDoOnce;


BEGIN OnActivate

	If (IsActionRef Player == 1) && (GetStage NVDLC04MQ03b == 10)
		ShowMessage NVDLC04TempleLaunchMSG;
		Set nMessage to 1;
	Else 
		ShowMessage MQ05CantUseNowMessage;
	EndIf

END


BEGIN GameMode

	If (bRunTimer == 1)
		If (nStage == 0)
			If (fTimer < 1)
				Set fTimer to (fTimer + GetSecondsPassed);
			ElseIf (fTimer >= 1)
				NVDLC04EDETempleTerminalButtonREF.Playgroup Left 0;
				Set fTimer to fTimer + GetSecondsPassed;
				Set nStage to 1;
			EndIf
		ElseIf (nStage == 1)
			If (fTimer < 2)
				Set fTimer to (fTimer + GetSecondsPassed);
			ElseIf (fTimer >= 2)
				UlyssesRocketREF.PlayGroup Left 1;
				PlaySound NVDLC04QSTUlyssesFire;
				Set UlyssesBattleCodebox.bTempleCollapse to 1;
				Set bRunTimer to 0;
			EndIf
		EndIf
	EndIf

	If (nMessage == 0)
		Return;
	ElseIf (nMessage == 1)
		Set nButton to GetButtonPressed
		If (nButton == 0)
			Set nMessage to 0;
			Return;
		ElseIf (nButton == 1)
			Set nMessage to 2;
			ShowMessage NVDLC04TempleLaunchCodesMSG;
			Return;
		ElseIf (nButton == 2)
			Set nMessage to 3;
			ShowMessage NVDLC04TempleNukeAllConfirmMSG;
			Return;
		ElseIf (nButton == 3)
			Set nMessage to 4;
			ShowMessage NVDLC04TempleCaesarConfirmMSG;
			Return;
		ElseIf (nButton == 4)
			Set nMessage to 5;
			ShowMessage NVDLC04TempleNCRConfirmMSG;
			Return;
		EndIf
	ElseIf (nMessage == 2)
		Set nButton to GetButtonPressed;
		If (nButton == 0)
			ShowMessage NVDLC04TempleLaunchMSG;
			Set nMessage to 1;
		ElseIf (nButton == 1)
			NVDLC04EDEREF.RestoreAV Health 100;
			NVDLC04EDEREF.MoveTo NVDLC04EDEFinaleMarkerREF;
			If (bEDEDoOnce == 0)
				NVDLC04EDEREF.AddScriptPackage NVDLC04EDELog10PACKAGE;
				Set bEDEDoOnce to 1;
			Else
				NVDLC04EDEREF.RemoveItem NVDLC04EDEWeapons 1;
				NVDLC04EDEREF.RemoveAllItems Player;
				NVDLC04EDEREF.AddItem NVDLC04EDEWeapons 1;
				NVDLC04EDEREF.SetDestroyed 1;
				NVDLC04EDEREF.AddScriptPackage NVDLC04EDEActivateFinale;
				TempleLaunchControl.PlayGroup Forward 1;
				TempleLaunchControl.SetDestroyed 1;

				Set NVDLC04DialogueEDE.bEDEHired to 0;
				Set NVDLC04DialogueEDE.bEDEFired to 1;
				; Clean up companion factions.
				NVDLC04EDEREF.RemoveFromFaction FollowerFaction;
				NVDLC04EDEREF.SetPlayerTeammate 0;
				NVDLC04EDEREF.AddToFaction RobotFaction 1;
			EndIf
			Set nMessage to 0;
			Return;
		EndIf
	ElseIf (nMessage == 3)
		Set nButton to GetButtonPressed;
		If (nButton == 0)
			ShowMessage NVDLC04TempleLaunchMSG;
			Set nMessage to 1;
		ElseIf (nButton == 1)
			Set bRunTimer to 1;
			NVDLC04EDETempleTerminalButtonREF.Playgroup Forward 0;
			SetStage NVDLC04MQ03b 255;
			SetStage NVDLC04MQ04 10;
			Set NVDLC04MQ03b.nMissileTarget to 3;
			Set nMessage to 0;
			SetDestroyed 1;
			Return;
		EndIf
	ElseIf (nMessage == 4)
		Set nButton to GetButtonPressed;
		If (nButton == 0)
			ShowMessage NVDLC04TempleLaunchMSG;
			Set nMessage to 1;
		ElseIf (nButton == 1)
			Set bRunTimer to 1;
			NVDLC04EDETempleTerminalButtonREF.Playgroup Forward 0;
			SetStage NVDLC04MQ03b 255;
			SetStage NVDLC04MQ04 10;
			Set NVDLC04MQ03b.nMissileTarget to 2;
			Set nMessage to 0;
			SetDestroyed 1;
			Return;
		EndIf
	EndIf
	ElseIf (nMessage == 5)
		Set nButton to GetButtonPressed;
		If (nButton == 0)
			ShowMessage NVDLC04TempleLaunchMSG;
			Set nMessage to 1;
		ElseIf (nButton == 1)
			Set bRunTimer to 1;
			NVDLC04EDETempleTerminalButtonREF.Playgroup Forward 0;
			SetStage NVDLC04MQ03b 255;
			SetStage NVDLC04MQ04 10;
			Set NVDLC04MQ03b.nMissileTarget to 1;
			PlaySound NVDLC04QSTUlyssesFire;
			SetDestroyed 1;
			Return;
		EndIf
	EndIf

END

Scn	NVDLC04Silo02ElevatorTunnelerSCRIPT

; This script runs on the tunnelers used for the Silo02 Elevator Lift sequence. We want to detect certain player actions, and adjust accordingly the behavior/state of the tunnelers, to prevent errors in this delicate setup.
; J.S. 3/11/11

Short	bDoOnce			; 1 = Players have done something to the tunneler that requires us to force events, behaviors, or state changes, on the tunneler.
Short	nDismember	; 1 = We've had to obliterate this tunneler (hits the bottom of the elevator, for example).
Short	bDead			; 1 = We've recognized this tunneler as dead.
Short	bNoAggro		; 1 = Tunneler is set to non aggressive.
Short	bAnimSet		; 1 = Tunneler is set to be attacked, and attack, after its anim has played out.
Float	fTimer			; Timer Float.

BEGIN OnHit Player

	If (bDoOnce == 0)																	; If players hit the tunneler.
		If (GetDead != 1)																; Only if the tunneler's still alive, though.
			If (GetAnimAction == 13)													; And the tunneler is in mid animation state (action 13 specifies script-forced anims). In this case, the anims are called by the silo2elevator's scene codebox.
				If (IsInCombat != 1)													; And the tunneler is not currently in combat. 
					SetRestrained 0;
					KillActor Player;													; We kill the tunneler, with dismemberment, and XP gain, included. This case occurs only when tunnelers are hit by the player during their climbing (ceiling, and fence) anims.
					;ShowWarning "Killed by script."
					Set bDoOnce to 1;
				Endif
			Endif
		Endif
	Endif

END

BEGIN GameMode

	If (nDismember == 0)
		If (GetDisabled != 1)															;
			If (GetDead == 1)
				If (bDead == 0)
					If (NVDLC04ElevatorBox.bRidePrep == 1)
						Set NVDLC04ElevatorBox.nDeadTunnelers to (NVDLC04ElevatorBox.nDeadTunnelers + 1);
					Endif
					Set bDead to 1;
				Endif
				If (GetDistance Player >= 768 || GetPos Z <= 1600)
					;KillActor NVDLC04UlyssesREF 3;
					;KillActor NVDLC04UlyssesREF 5;
					;KillActor NVDLC04UlyssesREF 0;
					Set nDismember to 1;
					Return;
				Endif
			Else
				If (bAnimSet == 0)
					If (GetIsGhost == 1)
						If (bNoAggro == 0)
							SetAV Aggression 0;
							Set bNoAggro to 1;
							Return;
						Endif
					Else
						If (bNoAggro == 1)
							If (GetAnimAction != 13)
								Dispel NVDLC04TunnelerLiftDT;
								SetAV Aggression 2;
								SetRestrained 0;
								StartCombat Player;
								Set bNoAggro to 0;
								Set bAnimSet to 1;
								Return;
							Endif
						Endif
					Endif
				Else
					If (GetPos Z <= 1792)
						SetRestrained 0;
						KillActor NVDLC04UlyssesREF 3;
						KillActor NVDLC04UlyssesREF 5;
						KillActor NVDLC04UlyssesREF 0;
						Set nDismember to 1;
						Return;
					Endif
				Endif
			Endif
		Endif
	Elseif (nDismember == 1)
		If (fTimer >= 3)
			Disable;
			Set nDismember to 2;
			Return;
		Else
			Set fTimer to (fTimer + GetSecondsPassed);
		Endif
	Endif

END

SCN NVDLC04Silo02EntranceTriggerSCRIPT


; Turns on the Klaxon Lights
; DM 3/16/11
; DM 3/24/11 - Added Klaxon Lights
; DM 5/23/11 - Removed the unconscious sentry as it's no longer needed
; TLS 05/31/2011 - Added scripting to teleport ED-E to the player if the player left him waiting in the Silo Elevator.

SHORT bDoOnce;		;Do this once


BEGIN OnTriggerEnter Player

	If (NVDLC04EDEREF.Waiting == 1) && (NVDLC04EDEREF.GetInCell NVDLC04Silo2Elevator == 1)
		NVDLC04EDEREF.MoveTo NVDLC04Silo02EDEMarkerREF;
		Set NVDLC04EDEREF.Waiting to 0;
		NVDLC04EDEREF.ResetAI;
	EndIf

END


BEGIN GameMode

	If (bDoOnce == 0)
		Set Silo02Klaxon01.State to 1;
		Set Silo02Klaxon02.State to 1;
		Set Silo02Klaxon03.State to 1;
		Set Silo02KlaxonLight01.State to 1;
		Set Silo02KlaxonLight02.State to 1;
		Set Silo02KlaxonLight03.State to 1;
		Set bDoOnce to 1;
	EndIf

END

Scn	NVDLC04Silo02ElevatorCodeHolderSCRIPT

; This script controls the scripted scenes in the Silo02 Elevator ride. It will handle several scenes, and creature spawning, accounted by the var nScene.
; This script has several structural points where no actions are taken yet - the frame is such that it will allow quick work on it, if necessary, during polish phase (alpha).

; J.S. 3/15/11

Ref	rFire01
Ref	rFire02
Ref	rFire03
Ref	rFire04
Ref	rFire05
Ref	rTun01
Ref	rTun02
Ref	rTun03
Ref	rTun04
Ref	rTun05
Ref	rTun06

Short	bInit						; 1 = Initial operations that run once.
Short	bElevatorOn			; 1 = Elevator Activator has been triggered.
Short	bRideStart				; 1 = 
Short	bRideEnd				; 1 = 
Short	bRidePrep				; 1 = 
Short	bFinale					; 1 = 

Short	bPlayerInterference	; 1 = Player has carried out some action that would normally disrupt the flow of the scene in question.

Int		nDeadTunnelers		; Value corresponds to the number of killed tunnelers - set on the tunnelers' script (proper to the elevator sequence).
Int		nScene					; 1 - 3 = Large collection of events, each is discretely considered a "scene."
Int		nCount					; Operational integer.
Int		nState					; Operational integer - defines larger segments than nCount.
Float	fTimer					; Timer Float.


BEGIN GameMode

	SetAllVisible 1;

	If (GetDisabled == 1)																														; If the codebox is disabled, skip all code.
		Return;
	Else																																			; If the codebox is enabled, continue.
		If (bInit == 0)																															; Init check.
			Set rFire01 to NVDLC04Silo02ElevatorFire01REF;
			Set rFire02 to NVDLC04Silo02ElevatorFire02REF;
			Set rFire03 to NVDLC04Silo02ElevatorFire03REF;
			;Set rFire04 to NVDLC04Silo02ElevatorFire04REF;
			;Set rFire05 to NVDLC04Silo02ElevatorFire05REF;
			Set rTun01 to NVDLC04ElevTunnelerWave01AmbREF;
			Set rTun02 to NVDLC04ElevTunnelerWave01BruteREF;
			Set rTun03 to NVDLC04ElevTunnelerWave01VenREF;
			Set rTun04 to NVDLC04ElevTunnelerWave02AmbREF;
			Set rTun05 to NVDLC04ElevTunnelerWave02BruteREF;
			Set rTun06 to NVDLC04ElevTunnelerWave02VenREF;

			rTun01.SetAV Aggression 0;
			rTun02.SetAV Aggression 0;
			rTun03.SetAV Aggression 0;
			rTun04.SetAV Aggression 0;
			rTun05.SetAV Aggression 0;
			rTun06.SetAV Aggression 0;

			rTun01.SetGhost 1;
			rTun02.SetGhost 1;
			rTun03.SetGhost 1;
			rTun04.SetGhost 1;
			rTun05.SetGhost 1;
			rTun06.SetGhost 1;

			NVDLC04ElevTunnelerVictim02REF.SetGhost 1;
			NVDLC04ElevTunnelerVictim03REF.SetGhost 1;

			NVDLC04Silo02ElevatorAlarm01REF.Enable;

			Set bInit to 1;
			Return;
		Else																																		; When init conditions are set, start with scene 0.
			If (nScene == 0)
				If (bPlayerInterference == 0)																									; If players have not interfered with the scene, continue normally.
					If (bRideStart == 0)
						If (nState == 0)																												;
							Set fTimer to (fTimer + GetSecondsPassed);
							If (nCount == 0)
								If (fTimer >= 2)
									NVDLC04Silo02ElevatorAlarm01REF.PlaySound NPCSporeCarrierHissVox;
									Set fTimer to 0;
									Set nCount to 1;
									Return;
								Endif
							Elseif (nCount == 1)
								If (fTimer >= 1)
									NVDLC04ElevTunnelerVictim02REF.Enable;
									Set fTimer to 0;
									Set nCount to 2;
									Return;
								Endif
							Elseif (nCount == 2)
								NVDLC04ElevTunnelerVictim02REF.SetPos X 272;
								NVDLC04ElevTunnelerVictim02REF.SetPos Y 3458;
								NVDLC04ElevTunnelerVictim02REF.SetPos Z 2071;
								Set nCount to 3;
								Return;
							Elseif (nCount == 3)
								NVDLC04ElevTunnelerVictim02REF.PlayIdle SpecialIdleClimbbipAlt;
								Set nCount to 4;
								Return;
							Elseif (nCount == 4)
								If (NVDLC04ElevTunnelerVictim02REF.GetAnimAction == 13)
									NVDLC04ElevTunnelerVictim02REF.PlaySound NVDLC04NPCSporeCarrierFenceClimbAlt;
									NVDLC04ElevTunnelerVictim02REF.SetGhost 0;
									Set fTimer to 0;
									Set nCount to 0;
									Set nState to 1;																										; We've reset operational var, and move to state 1.
									Return;
								Else
									Set nCount to 3;
									Return;
								Endif
							Endif
						Elseif (nState == 1)																										; 
							If (nCount == 0)
								If (fTimer >= 2)
									NVDLC04ElevTunnelerVictim03REF.Enable;
									NVDLC04Silo02ElevatorAlarm01REF.PlaySound NPCSporeCarrierScream;
									Set fTimer to 0;
									Set nCount to 1;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
									Return;
								Endif
							Elseif (nCount == 1)
									NVDLC04ElevTunnelerVictim03REF.SAA 0;
									Set nCount to 2;
									Return;
							Elseif (nCount == 2)
									NVDLC04ElevTunnelerVictim03REF.PlayIdle SpecialIdleClimbbip;
									Set nCount to 3;
									Return;
							Elseif (nCount == 3)
								If (NVDLC04ElevTunnelerVictim03REF.GetAnimAction == 13)
									NVDLC04ElevTunnelerVictim03REF.PlaySound NVDLC04NPCSporeCarrierFenceClimb;
									Set nCount to 4;
									Return;
								Else
									Set nCount to 2;
									Return;
								Endif
							Elseif (nCount == 4)
								NVDLC04ElevTunnelerVictim03REF.SetGhost 0;
								NVDLC04ElevTunnelerVictim03REF.SAA 1;
								NVDLC04ElevTunnelerVictim03REF.SetAngle Z 90;
								If (bElevatorOn == 1)
									Set bRideStart to 1;
									Set NVDLC04Silo2ElevatorControlsREF.bActive to 1;
									Return;
								Endif
								Set nCount to 5;
								Return;
							Elseif (nCount == 5)
								Set fTimer to (fTimer + GetSecondsPassed);
								If (fTimer >= 4)
									NVDLC04Silo02ElevatorAlarm01REF.PlaySound NPCSporeCarrierHissVox;
									;NVDLC04ElevTunnelerVictim01REF.SMS Flames01;
									;NVDLC04SiloVictimFireLightREF.Disable;
									Set nCount to 0;
									Set fTimer to 0;
									Set nState to 2;																									; We've reset operational vars, and move to state 1.
								Else
									If (bElevatorOn == 1)
										Set bRideStart to 1;
										Set NVDLC04Silo2ElevatorControlsREF.bActive to 1;
										Return;
									Endif
								Endif
							Endif
						Elseif (nState == 2)																										; 
							If (bElevatorOn == 1)
								Set bRideStart to 1;
								Set NVDLC04Silo2ElevatorControlsREF.bActive to 1;
								Return;
							Endif
						Endif
					Else
						If (bRidePrep == 0)
							NVDLC04SiloElevatorStartCollision02REF.PlaySound NPCSporeCarrierScream;
							rTun01.Enable;
							rTun02.Enable;
							rTun03.Enable;
							Set nCount to 0;
							Set nState to 0;
							Set fTimer to 0;
							Set bRidePrep to 1;
							Return;
						Else
							If (nState == 0)
								Set fTimer to (fTimer + GetSecondsPassed);
								If (nCount == 0)
									If (fTimer >= 1.3)																									; Run the 2 sec timer.
										NVDLC04Silo02ElevatorFireVictimREF.PlaceAtMe NVDLC04Silo02ElevatorExplosionSpecial;
										NVDLC04ElevTunnelerVictim01REF.Enable;
										Set fTimer to 0;
										Set nCount to 1;
										Return;
									Endif
								Elseif (nCount == 1)
									If (fTimer >= 0.1)	
										NVDLC04ElevTunnelerVictim01REF.SetPos X -87;
										NVDLC04ElevTunnelerVictim01REF.SetPos Y 3395;
										NVDLC04ElevTunnelerVictim01REF.SetPos Z 2383;
										Set fTimer to 0;
										Set nCount to 2;
										Return;
									Endif
								Elseif (nCount == 2)
									If (fTimer >= 0.2)																									; Run the 2 sec timer.
										NVDLC04Silo02ElevatorFireVictimREF.PlaceAtMe NVDLC04Silo02ElevatorExplosionSpecial;
										Set fTimer to 0;
										Set nCount to 3;
										Return;
									Endif
								Elseif (nCount == 3)
									If (fTimer >= 0.2)																									; Run the 2 sec timer.
										NVDLC04ElevTunnelerVictim01REF.KillActor NVDLC04UlyssesREF;
										NVDLC04ElevTunnelerVictim01REF.PMS Flames01;
										Set nCount to 4;
										Return;
									Endif
								Elseif (nCount == 4)
									If (fTimer >= 0.4)
										rTun01.CIOS NVDLC04TunnelerLiftDT;
										rTun02.CIOS NVDLC04TunnelerLiftDT;
										rTun03.CIOS NVDLC04TunnelerLiftDT;
										Set nCount to 5;
										Set fTimer to 0;
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Elseif (nCount == 5)
									rTun01.Enable;
									Set nCount to 6;
									Return;
								Elseif (nCount == 6)
									;rTun01.MoveTo NVDLC04Silo02ElevatorAlarm01REF;
									;ShowWarning "Moved In Front."
									If (IsPS3 == 1 || IsXBox == 1)
										rTun01.PlayIdle SpecialIdleClimbElevator;
									Endif
									;rTun01.SetAngle Z 90;
									rTun01.SetAngle Z 0;
									rTun01.SAA 0;
									rTun01.SetAV Aggression 0;
									Set nCount to 7;
									Return;
								Elseif (nCount == 7)
									;rTun01.MoveTo NVDLC04ElevTunnelerPoint03REF;
									rTun01.MoveTo NVDLC04ElevTunnelerPoint02REF;
									Set nCount to 8;
									Return;
								Elseif (nCount == 8)
									If (fTimer >= 0.2)
										;rTun01.SetAngle Z 90;
										If (rTun01.GetAnimAction != 13)
											;rTun01.PlayIdle SpecialIdleClimbBipAlt ;SpecialIdleClimbBipAlt ;SpecialIdleClimbElevator;
											rTun01.PlayIdle SpecialIdleClimbElevator ;SpecialIdleClimbBipAlt ;SpecialIdleClimbElevator;
										Else
											Set nCount to 9;	
										Endif
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Elseif (nCount == 9)
									If (fTimer >= 0.3)
										rTun01.SetRestrained 1;
										;rTun01.SetAngle Z 90;
										rTun01.SetAngle Z 0;
										rTun01.SetPos X 142
										rTun01.SetPos Y 3670
										rTun01.SetPos Z 2064;
										rTun01.SAA 1;
										rTun01.SetGhost 0;
										Set nCount to 10;
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Elseif (nCount == 10)
									If (fTimer >= 5)
										Set nCount to 0;
										Set fTimer to 0;
										Set nState to 1;
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Endif
							Elseif (nState == 1)
								If (nCount == 0)
									If (fTimer >= 5)
										NVDLC04ElevTunnelerVictim01REF.SMS Flames01;
										NVDLC04SiloVictimFireLightREF.Disable;
										Set nCount to 1;
										Set fTimer to 0;
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Elseif (nCount == 1)
									rTun02.Enable;
									Set nCount to 2;
									Return;
								Elseif (nCount == 2)
									;rTun02.MoveTo NVDLC04Silo02ElevatorAlarm01REF;
									rTun02.SetAngle Z 270;
									rTun02.SAA 0;
									rTun02.SetAV Aggression 0;
									Set nCount to 3;
									Return;
								Elseif (nCount == 3)
									rTun02.MoveTo NVDLC04ElevTunnelerPoint04REF;
									;rTun01.AddScriptPackage NVDLC04PackageSilo02TunnelerTravelLure;
									Set nCount to 4;
									Return;
								Elseif (nCount == 4)
									If (fTimer >= 0.2)
										rTun02.SetAngle Z 270;
										If (rTun02.GetAnimAction != 13)
											rTun02.PlayIdle SpecialIdleClimbBip ;SpecialIdleClimbElevator;
										Else
											Set nCount to 5;
										Endif
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Elseif (nCount == 5)
									If (fTimer >= 0.5)
										rTun02.SetRestrained 1;
										;rTun02.SetPos Y 3670
										rTun02.SetAngle Z 270;
										rTun02.SAA 1;
										rTun02.SetGhost 0;
										Set nCount to 6;
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Elseif (nCount == 6)
									If (fTimer >= 3)
										Set nCount to 0;
										Set fTimer to 0;
										Set nState to 2;
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Endif
							Elseif (nState == 2)
								If (nCount == 0)
									If (fTimer >= 5)
										;PlayISFX
										Set nCount to 1;
										Set fTimer to 0;
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Elseif (nCount == 1)
									rTun03.Enable;
									Set nCount to 2;
									Return;
								Elseif (nCount == 2)
									;rTun03.MoveTo NVDLC04Silo02ElevatorAlarm01REF;
									rTun03.SetAngle Z 90;
									rTun03.SAA 0;
									rTun03.SetAV Aggression 0;
									Set nCount to 3;
									Return;
								Elseif (nCount == 3)
									rTun03.MoveTo NVDLC04ElevTunnelerPoint03REF;
									Set nCount to 4;
									Return;
								Elseif (nCount == 4)
									If (fTimer >= 0.2)
										rTun03.SetAngle Z 90;
										If (rTun03.GetAnimAction != 13)
											rTun03.PlayIdle SpecialIdleClimbBipAlt;
											rTun03.PlaySound NVDLC04NPCSporeCarrierFenceClimbAlt;
										Else
											Set nCount to 5;
										Endif
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Elseif (nCount == 5)
									If (fTimer >= 0.5)
										rTun03.SetRestrained 1;
										rTun03.SetAngle Z 90;
										rTun03.SAA 1;
										rTun03.SetGhost 0;
										Set nCount to 6;
										Return;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Elseif (nCount == 6)
									If (fTimer >= 3)
										NVDLC04Silo02ElevatorAlarm01REF.PlaySound NPCSporeCarrierScream;
										Set nCount to 0;
										Set fTimer to 0;
										Set nState to 0;
										Set nScene to 1;
									Else
										Set fTimer to (fTimer + GetSecondsPassed);
									Endif
								Endif
							Endif
						Endif
					Endif
				Endif
			Elseif (nScene == 1)																												; 
				If (bPlayerInterference == 0)																									; 
					If (nState == 0)																												; 
						If (nDeadTunnelers >= 1)
							If (nCount == 0)
								If (fTimer >= 10)
									;PlayISFX
									NVDLC04ElevTunnelerVictim01REF.Disable;
									NVDLC04Silo02ElevatorAlarm01REF.PlaySound NPCSporeCarrierHissVox;
									Set nCount to 1;
									Set fTimer to 0;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Elseif (nCount == 1)
								rTun04.Enable;
								Set nCount to 2;
								Return;
							Elseif (nCount == 2)
								rTun04.CIOS NVDLC04TunnelerLiftDT;
								rTun04.SAA 0;
								rTun04.SetAV Aggression 0;
								Set nCount to 3;
								Return;
							Elseif (nCount == 3)
								If (NVDLC04Silo2ElevatorControlsREF.fTimer < 60)
									rTun04.MoveTo NVDLC04ElevTunnelerPoint03REF;
								Else
									rTun04.MoveTo NVDLC04ElevTunnelerPoint03REF;
								Endif
								Set nCount to 4;
								Return;
							Elseif (nCount == 4)
								If (fTimer >= 0.2)
									If (rTun04.GetAnimAction != 13)
										If (NVDLC04Silo2ElevatorControlsREF.fTimer < 60)
											rTun04.PlayIdle SpecialIdleClimbBipAlt; SpecialIdleClimbElevator;
										Else
											rTun04.PlayIdle SpecialIdleClimbBipAlt;SpecialIdleClimbBip;
											rTun04.PlaySound NVDLC04NPCSporeCarrierFenceClimb;
										Endif
									Else
										Set nCount to 5;
									Endif
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Elseif (nCount == 5)
								If (fTimer >= 0.5)
									If (NVDLC04Silo2ElevatorControlsREF.fTimer < 60)
										rTun04.SetAngle Z 90; 0
										;rTun04.SetPos Y 3670
									Else
										rTun04.SetAngle Z 90;
									Endif
									rTun04.SetRestrained 1;
									rTun04.SAA 1;
									rTun04.SetGhost 0;
									Set nCount to 6;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Elseif (nCount == 6)
								If (fTimer >= 3)
									Set nCount to 0;
									Set fTimer to 0;
									Set nState to 1;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Endif
						Endif
					Elseif (nState == 1)																										;
						If (nDeadTunnelers >= 2)
							If (nCount == 0)
								If (fTimer >= 1)
									;PlayISFX
									Set nCount to 1;
									Set fTimer to 0;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Elseif (nCount == 1)
								rTun05.Enable;
								Set nCount to 2;
								Return;
							Elseif (nCount == 2)
								rTun05.CIOS NVDLC04TunnelerLiftDT;
								rTun05.SAA 0;
								rTun05.SetAV Aggression 0;
								Set nCount to 3;
								Return;
							Elseif (nCount == 3)
								rTun05.MoveTo NVDLC04ElevTunnelerPoint04REF;
								Set nCount to 4;
								Return;
							Elseif (nCount == 4)
								If (fTimer >= 0.2)
									If (rTun05.GetAnimAction != 13)
										rTun05.PlayIdle SpecialIdleClimbBipAlt;
										rTun05.PlaySound NVDLC04NPCSporeCarrierFenceClimbAlt;
									Else
										Set nCount to 5;
									Endif
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Elseif (nCount == 5)
								If (fTimer >= 0.5)
									rTun05.SetRestrained 1;
									rTun05.SetAngle Z 270;
									rTun05.SAA 1;
									rTun05.SetGhost 0;
									Set nCount to 6;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Elseif (nCount == 6)
								If (fTimer >= 3)
									Set nCount to 0;
									Set fTimer to 0;
									Set nState to 2;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Endif
						Endif
					Elseif (nState == 2)
						If (nDeadTunnelers >= 3 && NVDLC04Silo2ElevatorControlsREF.fTimer > 42)
							If (nCount == 0)
								If (fTimer >= 1)
									;PlayISFX
									Set nCount to 1;
									Set fTimer to 0;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Elseif (nCount == 1)
								rTun06.Enable;
								Set nCount to 2;
								Return;
							Elseif (nCount == 2)
								rTun06.CIOS NVDLC04TunnelerLiftDT;
								rTun06.SAA 0;
								rTun06.SetAV Aggression 0;
								Set nCount to 3;
								Return;
							Elseif (nCount == 3)
								If (NVDLC04Silo2ElevatorControlsREF.fTimer < 60)
									;rTun06.MoveTo NVDLC04ElevTunnelerPoint03REF; 2
									rTun06.MoveTo NVDLC04ElevTunnelerPoint02REF; 2
								Else
									rTun06.MoveTo NVDLC04ElevTunnelerPoint03REF;
								Endif
								Set nCount to 4;
								Return;
							Elseif (nCount == 4)
								If (fTimer >= 0.2)
									If (rTun06.GetAnimAction != 13)
										If (NVDLC04Silo2ElevatorControlsREF.fTimer < 60)
											;rTun06.PlayIdle SpecialIdleClimbBipAlt ;SpecialIdleClimbElevator;
											rTun06.PlayIdle SpecialIdleClimbElevator ;SpecialIdleClimbElevator;
										Else
											rTun06.PlayIdle SpecialIdleClimbBipAlt ;SpecialIdleClimbBipAlt;
											rTun06.PlaySound NVDLC04NPCSporeCarrierFenceClimbAlt;
										Endif
									Else
										Set nCount to 5;
									Endif
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Elseif (nCount == 5)
								If (fTimer >= 0.3)
									If (NVDLC04Silo2ElevatorControlsREF.fTimer < 60)
										rTun06.SetPos Y 3670
										;rTun06.SetAngle Z 90; 0
									Else
										rTun06.SetAngle Z 90;
									Endif
									rTun06.SetRestrained 1;
									rTun06.SAA 1;
									rTun06.SetGhost 0;
									Set nCount to 6;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Elseif (nCount == 6)
								If (fTimer >= 3)
									Set nCount to 0;
									Set fTimer to 0;
									Set nState to 3;
									Return;
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
								Endif
							Endif
						Endif
					Endif
				Endif
			Endif
		Endif
	Endif

END

Scn	NVDLC04Silo02ElevatorExplosionsCodeHolderSCRIPT

; This script controls the randomized explosions that occur during Silo02's elevator ride, after firing the nuke at the end of Road02.
; This script has several structural points where no actions are taken yet - the frame is such that it will allow quick work on it, if necessary, during polish phase (alpha).

; J.S. 3/15/11

Ref	rFire01
Ref	rFire02
Ref	rFire03
Ref	rFire04
Ref	rFire05
Ref	rFire06

Short	bInit						; 1 = Initial operations that run once.
Short	bEnd
Short	bMid

Int		nCount					; Operational integer.
Int		nState					; Operational integer - defines larger segments than nCount.
Float	fTimer					; Timer Float.
Float	fRandomTimer			; Random Timer Float.
Float	fRandom					; Random 0 - 99 float.


BEGIN GameMode

	If (GetDisabled == 1)																														; If the codebox is disabled, skip all code.
		Return;
	Else																																			; If the codebox is enabled, continue.	
		If (bInit == 0)
			Set rFire01 to NVDLC04Silo02ElevatorFire01REF;
			Set rFire02 to NVDLC04Silo02ElevatorFire02REF;
			Set rFire03 to NVDLC04Silo02ElevatorFire03REF;
			Set rFire04 to NVDLC04Silo02ElevatorFire01bREF;
			Set rFire05 to NVDLC04Silo02ElevatorFire02bREF;
			Set rFire06 to NVDLC04Silo02ElevatorFire03bREF;
			Set bInit to 1;
			Return;
		Else
			If (NVDLC04ElevatorBox.bRideStart == 0)
				If (nState == 0)
					If (nCount == 0)
						If (fTimer >= 4)
							Set fRandom to GetRandomPercent;
							If (fRandom >= 60)
								rFire01.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
								Set fTimer to 0;
								Set nCount to 1;
								Return;
							Elseif (fRandom >= 30)
								rFire02.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
								Set fTimer to 0;
								Set nCount to 1;
								Return;
							Else
								rFire03.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
								Set fTimer to 0;
								Set nCount to 1;
								Return;
							Endif 
						Else
							Set fTimer to (fTimer + GetSecondsPassed);
							Return
						Endif
					Elseif (nCount == 1)
						Set fRandomTimer to ((GetRandomPercent + 10) / 20);
						Set nCount to 0;
						Set nState to 1;
						Return;
					Endif
				Elseif (nState == 1)
					If (nCount == 0)
						If (fTimer >= fRandomTimer)
							If (fRandom >= 60)
								rFire01.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
								Set fTimer to 0;
								Set nCount to 1;
								Return;
							Elseif (fRandom >= 30)
								rFire02.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
								Set fTimer to 0;
								Set nCount to 1;
								Return;
							Else
								rFire03.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
								Set fTimer to 0;
								Set nCount to 1;
								Return;
							Endif
						Else
							Set fTimer to (fTimer + GetSecondsPassed);
							Return
						Endif
					Elseif (nCount == 1)
						Set fRandomTimer to ((GetRandomPercent + 10) / 20);
						Set nCount to 0;
						Return;
					Endif
				Endif
			Elseif (NVDLC04ElevatorBox.bRideStart == 1 && NVDLC04ElevatorBox.bRideEnd == 0)
				If (bMid == 0)
					Set nCount to 0;
					Set nState to 0;
					Set fTimer to 0;
					Set fRandom to 0;
					Set fRandomTimer to 0;
					Set bMid to 1;
				Else
					If (nState == 0)
						If (nCount == 0)
							If (fTimer >= fRandomTimer)
								Set fRandom to GetRandomPercent;
								If (fRandom >= 75)
									rFire01.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
									Set fTimer to 0;
									Set nCount to 1;
									Return;
								Elseif (fRandom >= 55)
									rFire02.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
									Set fTimer to 0;
									Set nCount to 1;
									Return;
								ElseIf (fRandom >= 40)
									rFire03.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
									Set fTimer to 0;
									Set nCount to 1;
									Return;
								Elseif (fRandom >= 30)
									rFire04.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
									Set fTimer to 0;
									Set nCount to 1;
									Return;
								Elseif (fRandom >= 20)
									rFire05.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
									Set fTimer to 0;
									Set nCount to 1;
									Return;
								Else
									rFire06.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
									Set fTimer to 0;
									Set nCount to 1;
									Return;
								Endif 
							Else
								Set fTimer to (fTimer + GetSecondsPassed);
								Return
							Endif
						Elseif (nCount == 1)
							If (GetRandomPercent <= 60)
								Set fRandomTimer to ((GetRandomPercent + 10) / 20);
							Else
								Set fRandomTimer to ((GetRandomPercent + 10) / 50);
							Endif
							Set nCount to 0;
							Return;
						Endif
					Endif
				Endif
			Elseif (NVDLC04ElevatorBox.bRideEnd == 1)
				;If (NVDLC04MQ == finished)
					If (bEnd == 0)
						Set nCount to 0;
						Set nState to 0;
						Set fTimer to 0;
						Set fRandom to 0;
						Set fRandomTimer to 0;
						Set bEnd to 1;
					Else
						If (nState == 0)
							If (nCount == 0)
								If (fTimer >= fRandomTimer)
									Set fRandom to GetRandomPercent;
									If (fRandom >= 60)
										rFire04.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
										Set fTimer to 0;
										Set nCount to 1;
										Return;
									Elseif (fRandom >= 30)
										rFire05.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
										Set fTimer to 0;
										Set nCount to 1;
										Return;
									Else
										rFire06.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
										Set fTimer to 0;
										Set nCount to 1;
										Return;
									Endif 
								Else
									Set fTimer to (fTimer + GetSecondsPassed);
									Return
								Endif
							Elseif (nCount == 1)
								If (GetRandomPercent <= 60)
									Set fRandomTimer to ((GetRandomPercent + 10) / 20);
								Else
									Set fRandomTimer to ((GetRandomPercent + 10) / 50);
								Endif
								Set nCount to 0;
								Return;
							Endif
						Endif
					Endif
				;Else
				;	Disable;
				;	Return;
				;Endif
			Endif
		Endif
	Endif

END

