SCN NVDLC04EDEAudioLog03TriggerSCRIPT


; Force greet trigger for ED-E Audio Log 03.
; TLS 03/15/2011
; TLS 03/17/2011 - Added teleport for bulletproofing
; TLS 03/25/2011 - Added conditional for later replays.
; TLS 04/06/2011 - Replaced MoveToMarkerWithFade with MoveTo, as the former seems to prevent dialogue firing right.
; TLS 05/31/2011 - Removing ED-E waiting check.
; J.S. 7/13/11 - Added GameMode block, and variables, to create a mini scripted scene out of this dialogue, since it's now moved to the start of RoadTunnel.

Short	bDoOnce
Int		nCount
Float	fTimer

BEGIN OnTriggerEnter Player

	If (NVDLC04DialogueEDE.bEDEHired == 1)
		Set NVDLC04DialogueEDE.nAudioLog03 to 1;
	EndIf

END

BEGIN GameMode

	If (GetDisabled != 1)
		If (bDoOnce == 0)
			If (NVDLC04DialogueEDE.nAudioLog03 == 1)
				If (nCount == 0)
					If (fTimer >= 0.1)
						NVDLC04EDEREF.MoveTo NVDLC04EDELog03MarkerREF;
						Set fTimer to 0;
						Set nCount to 1;
						Return;
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
						Return;
					Endif
				Elseif (nCount == 1)
					If (fTimer >= 0.8)
						NVDLC04EDEREF.AddScriptPackage NVDLC04EDELog03PACKAGE;
						Set fTimer to 0;
						Set nCount to 2;
						Return;
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
						Return;
					Endif
				Elseif (nCount == 2)
					If (fTimer >= 1)
						If (NVDLC04EDEREF.GetCurrentAIPackage != 6 || NVDLC04EDEREF.GetCurrentAIPackage != 34)
							NVDLC04EDEREF.AddScriptPackage NVDLC04PackageEDERoadTunnelStartFacing;
							Set bDoOnce to 1;
							Set fTimer to 0;
							Set nCount to 0;
							Return;
						Endif
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
						Return;
					Endif
				Endif
			Endif
		Else
			Disable;
			MarkForDelete;
		Endif
	Endif

END

SCN NVDLC04TempleEyebotControl02SCRIPT


; Eyebot control panel script
; DM 3/15/11
; DM 3/18/11 - Updated to use a cascading menu system instead of a double check.

INT	iButton		;Tracks the button presses for the current menu
INT	iMenu		;Tracks the current menu screen


BEGIN OnActivate

	If (IsActionRef Player == 1) && (UlyssesBattleCodebox.bEyebotTerminal == 1)
		ShowMessage NVDLC04TempleEyebotControlMsgL00;
	Else 
		ShowMessage MQ05CantUseNowMessage;
	EndIf

END


BEGIN GameMode

	Set iButton to GetButtonPressed;
	If (iButton == 0)
		Set iMenu to 0;
	EndIf

	If (iMenu == 0)																			;NVDLC04TempleEyebotControlMsgL00
		If (iButton == 1)
			ShowMessage NVDLC04TempleEyebotControlMsgL01;
			Set iMenu to 1;
		ElseIf (iButton == 2)	
			ShowMessage NVDLC04TempleEyebotControlMsgL02;
			Set iMenu to 2;
		EndIf
	ElseIf (iMenu == 1)																	;NVDLC04TempleEyebotControlMsgL01
		If (iButton == 1)
			ShowMessage NVDLC04TempleEyebotControlMsgL04;
			Set UlyssesBattleCodebox.bEyebotDisabledL02 to 1;
			Set iMenu to 4;
		ElseIf (iButton == 2)
			ShowMessage NVDLC04TempleEyebotControlMsgL02;
			Set iMenu to 2;
		EndIf
	ElseIf (iMenu == 2)																	;NVDLC04TempleEyebotControlMsgL02
		If (iButton == 1)
			ShowMessage NVDLC04TempleEyebotControlMsgL03;
			Set UlyssesBattleCodebox.bEyebotDisabledL02 to 1;
			Set iMenu to 3;
		ElseIf (iButton == 2)
			ShowMessage NVDLC04TempleEyebotControlMsgL01;
			Set iMenu to 1;
		EndIf
	ElseIf (iMenu == 3)																	;NVDLC04TempleEyebotControlMsgL03
		If (iButton == 1)
			Set UlyssesBattleCodebox.bEyebotTurnedL02 to 1;
			Set UlyssesBattleCodebox.bEyebotDisabledL02 to 0;
		EndIf
	ElseIf (iMenu == 4)																	;NVDLC04TempleEyebotControlMsgL04
		If (iButton == 1)
			Set UlyssesBattleCodebox.bEyebotTurnedL02 to 1;
			Set UlyssesBattleCodebox.bEyebotDisabledL02 to 0;
		EndIf
	EndIf

END

