Scn	NVDLC04Road01StartCodeHolderSCRIPT

; This script runs the intro scene for Road01, the first warhead reveal, and any other events that need GameMode code for the early parts of Road01 (Hopeville). It also controls the enabled state of spawns in the first half of Road01.

; J.S. 3/7/11

Short	bInit						; 1 = Initial operations that run once.
Short	bEDEMoved			; 1 = EDE's been moved into position when coming from Silo01.
Short	bUlyssesConvo		; 1 = Convo with Ulysses, via EDE, has been initiated.
Short	bWarheadFound		; 1 = First warhead is found, right outside the Silo01's bunker.
Short	bPlayerInStartArea	; 1 = If player's hanging out near the start area codebox.
Short	bHQEnc					; 1 = HQ's spawns are active.
Short	bCamp01Enc			; 1 = Camp01's spawns are active.
Short	bRespawn				; 1 = Spawns are active, and will be respawned after three days from the last time in which their encounter flag was set to active (bHQEnc, and bCamp01Enc).

Int		nCount
Int		nState
Float	fTimer
Float	fRespawnTime			; Holds value of Game Day at which encounters are set to respawn, contrasted to GameDaysPassed.

BEGIN OnTriggerEnter Player									; This OnTriggerEnter event cannot occur by players crossing this trigger because it's outside the game space. Instead, it's called by other scripts, or this script itself, in the event that
																		; we want to clean up the waves of encounters for performance reasons.
	If (bHQEnc == 1)												; If HQ's spawns are active, then we disable them and return the var to 0.
		NVDLC04Road01HQGuard01REF.Disable;
		NVDLC04Road01HQGuard02REF.Disable;
		Set bHQEnc to 0;
	Endif

END



BEGIN GameMode

	If (GetDisabled == 1)																										; If the codebox is disabled, skip all code.
		Return;
	Else																															; If the codebox is enabled, continue.
		If (bInit == 0)																											; Init check, we artificially reduce the first two marked men's perception, to aid in initial stealth approach to HQ.	
			NVDLC04Road01HQGuard01REF.SetAV Perception 1;
			NVDLC04Road01HQGuard02REF.SetAV Perception 1;
			NVDLC04EDEREF.MoveTo NVDLC04EDEHopevilleMarkerREF;										; We move EDE into the correct spot.
			Set bEDEMoved to 1;																								; We set the pertinent vars to 1.
			Set bPlayerInStartArea to 1;
			Set bHQEnc to 1;
			Set bInit to 1;
			Return;
		Else
			If (nState == 0)																										; In nState 0 we're going to check for EDE being moved properly, and then run a timer, and player pos check.
				If (bEDEMoved == 1)																							; We do this to prevent players from missing EDE's Ulysses convo if they rush ahead right after arriving to Road01.
					If (fTimer >= 0 || Player.GetPos X >= -3500)															; The timer leaves some time to check out the new area, and makes it feel more natural.
						NVDLC04EDEREF.MoveTo NVDLC04Road01StartEDEConvo;
						Set bEDEMoved to 1;
						NVDLC04EDERef.AddScriptPackage NVDLC04PackageEDERoad01StartConvo;		; This AI package makes EDE start convo with the Ulysses "cut-1."
						Set fTimer to 0;
						Set nState to 1;
						Return;
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
					Endif
				Else																												; Just in case that EDE's not moved yet, we put it in place - to be damn sure.
					Return;
				Endif
			Elseif (nState == 1)																								; These states are here in case we want to add some scripted events at the start of Road01, right after Ulysses convo.
				Set nState to 2;
				Return;
			Elseif (nState == 2)
				If (nCount == 0)
					If (fTimer >= 1)
						Set nCount to 1;
						Return;
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
					Endif		
				Elseif (nCount == 1)
					If (fTimer >= 1)
						Set nCount to 2;
						Return;
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
					Endif		
				Elseif (nCount == 2)
					Set fTimer to 0;
					Set nCount to 0;
					Set nState to 3;
					Return;
				Endif
			Elseif (nState == 3)																								; We quicly pass to nState 3, for now, which enables HQ's guards, if bHQEnc is 0. It will only be 0 when players have explored near Camp02.
				If (bHQEnc == 0)																								; If HQ's encounters are deactivated, we'll run distance checks to see if they need to be activated again.
					If (Player.GetDistance NVDLC04Road01HQMarkerRef <= 2560)
						If (nCount == 0)
							NVDLC04Road01HQGuard01REF.Enable;
							Set nCount to 1;
							Return;
						Elseif (nCount == 1)
							NVDLC04Road01HQGuard02REF.Enable;
							Set nCount to 0;
							If (NVDLC04Road01HQGuard01REF.GetDisabled != 1)
								NVDLC04Road01MidBox.EnterTrigger Player;											; If we've activated HQ's spawns, we make sure to deactivate those handled by Road01's Middle Area codebox (Camp02, and Camp03).
								Set bHQEnc to 1;																				; We set the HQ encounter var to 1 (active spawns).
								Set fRespawnTime to (GameDaysPassed + 3);
								Set bRespawn to 1;
							Endif
							Return;
						Endif
					Endif
				Endif
				If (bCamp01Enc == 0)																							; As with the HQ spawns, we do the same with Camp01's guards.
					If (Player.GetDistance NVDLC04Road01Camp01MarkerRef <= 3200)
						If (nCount == 0)
							NVDLC04Road01MMCamp01Scout01REF.Enable;
							Set nCount to 1;
							Return;
						Elseif (nCount == 1)
							NVDLC04Road01MMCamp01Guard01REF.Enable;
							Set nCount to 0;
							If (NVDLC04Road01MMCamp01Scout01REF.GetDisabled != 1)
								NVDLC04Road01MidBox.EnterTrigger Player;
								Set bCamp01Enc to 1;
								Set fRespawnTime to (GameDaysPassed + 3);
								Set bRespawn to 1;
							Endif
							Return;
						Endif
					Endif
				Endif
				If (bRespawn == 1)
					If (fRespawnTime <= GameDaysPassed)
						If (Player.GetDistance NVDLC04Road01HQMarkerRef >= 2500)
							NVDLC04Road01HQGuard01REF.Resurrect;
							NVDLC04Road01HQGuard02REF.Resurrect;
							NVDLC04Road01MMCamp01Scout01REF.Resurrect;
							Set bRespawn to 0;
						Endif
					Endif		
				Endif
			Endif
		Endif
	Endif

END

Scn	NVDLC04Silo01CodeHolderSCRIPT

; This script runs the level's initial setup, and also handles the sequence of events at the level's finale. A codebox houses the script in Silo01

; J.S. 3/7/11

Short	bInit									; 1 = Initial operations that run once.
Short	bSiloOpen							; 1 = Silo doors to Road01 are open, and alarm is likely triggered.
Short	bEDEMainframeActivated		; 1 = EDE has used the security codes to activate the mainframe for player use.
Short	bAlarmOff							; 1 = Alarm goes off. It's possible to open the silo without triggering the alarm.
Short	bTurretRepaired					; 1 = Turrets have been repaired.
Short	bTurretHacked						; 1 = Turrets have been hacked.
Short	bSentryHacked						; 1 = Sentries have been hacked.
Short	bSentryDoorsOpen				; 1 = Sentry doors opened.
Short	bRobotFloor2Disabled			; 1 = Second floor robot has been disabled.
Short	bPowerBlinks						; Used as a toggle for the on/off effect of the silo's mainframe light.


Int		nSecurityLevel						; 0 = All aggressive. 1 = Turrets off. 2 = Turrets friendly. 3 = All bots off.
Int		nCount								; Operational integer.
Int		nState								; Operational integer - defines larger segments than nCount.
Float	fTimer								; Timer float.
Float	fTime									; Stores the time (GameDaysPassed) at which the silo's doors open (to cancel alarm after a few days/hours).



BEGIN OnTriggerEnter Player

	NVDLC04Silo01Bot01AREF.Disable;																							; This OnTriggerEnter block simply removes initial spawns in level (Silo01), and enables the Post-MQ marker (enable parent of new spawns).
	NVDLC04Silo01Bot01BREF.Disable;
	NVDLC04Silo01Bot02AREF.Disable;
	NVDLC04Silo01Bot02BREF.Disable;
	NVDLC04Silo01Floor02Bot01REF.Disable;
	NVDLC04Silo01Floor01Bot01REF.Disable;
	NVDLC04Silo01PostMQMarkerREF.Enable;

END


BEGIN GameMode

	If (GetDisabled == 1)																												; If the codebox is disabled, skip code.
		Return;
	Else																																	; Run code is codebox is enabled.
		If (bInit == 0)																													; Run initial operations.
			NVDLC04Silo01Bot01AREF.SetRestrained 1;																		; Here we set silo's bots to Restrained, Dead, Ghost, Unconscious, and their Aggression to 0;
			NVDLC04Silo01Bot01BREF.SetRestrained 1;
			NVDLC04Silo01Bot02AREF.SetRestrained 1;
			NVDLC04Silo01Bot02BREF.SetRestrained 1;
			NVDLC04Silo01Bot01AREF.Kill;
			NVDLC04Silo01Bot01BREF.Kill;
			NVDLC04Silo01Bot02AREF.Kill;
			NVDLC04Silo01Bot02BREF.Kill;
			NVDLC04Silo01Bot01AREF.SetGhost 1;
			NVDLC04Silo01Bot01BREF.SetGhost 1;
			NVDLC04Silo01Bot02AREF.SetGhost 1;
			NVDLC04Silo01Bot02BREF.SetGhost 1;
			NVDLC04Silo01Floor02Bot01REF.SetAV Perception 1;															; This bot's perception is artificially lowered, to help with stealth actions against it.
			NVDLC04Silo01Bot01AREF.SetUnconscious 1;
			NVDLC04Silo01Bot01BREF.SetUnconscious 1;
			NVDLC04Silo01Bot02AREF.SetUnconscious 1;
			NVDLC04Silo01Bot02BREF.SetUnconscious 1;
			NVDLC04Silo01Bot01AREF.SetAV Aggression 0;
			NVDLC04Silo01Bot01BREF.SetAV Aggression 0;
			NVDLC04Silo01Bot02AREF.SetAV Aggression 0;
			NVDLC04Silo01Bot02BREF.SetAV Aggression 0;

			NVDLC04Silo01Floor01Turret01REF.SetGhost 1;																	; Here we set the silo's turrets to Ghost, and Destroyed, to prevent interaction until alarm sequence.
			NVDLC04Silo01Floor01Turret02REF.SetGhost 1;
			NVDLC04Silo01Floor01Turret01REF.SetDestroyed 1;
			NVDLC04Silo01Floor01Turret02REF.SetDestroyed 1;
			NVDLC04Silo01Floor01Turret03REF.SetGhost 1;
			;NVDLC04Silo01Floor01Turret04REF.SetDestroyed 1;
			;NVDLC04Silo01Floor01Turret04REF.Kill;																			; This turret is destroyed, by default.

			NVDLC04Silo01Floor01Turret01REF.SetPos X -602;
			NVDLC04Silo01Floor01Turret01REF.SetPos Y 2812;
			NVDLC04Silo01Floor01Turret01REF.SetPos Z 14998;

			NVDLC04SiloBlastDoorControlsREF.Playgroup Left 0;															; We force the silo's reveal console to appear off (no red light).
			NVDLC04EDETerminalSilo01ButtonREF.SetDestroyed 1;

			NVDLC04Silo01DeadBot01REF.Kill;																					; We force this bot to die, for aesthetics, although it's not strictly necessary (already dead in editor).

			Set bInit to 1;
			Return;
		Else																																; If initial operations are done, we begin checking for the order of events within silo.
			If (nState == 0)																												; Preliminary field, left open in case we want to script scenes before the alarm scene.

			Elseif (nState == 1)																										; Preliminary field, left open in case we want to script scenes before the alarm scene.
				If (bEDEMainframeActivated == 1)
					If (bPowerBlinks == 0)
						If (fTimer >= 1)
							NVDLC04Silo01MainframeLightREF.Disable;
							Set fTimer to 0;
							Set bPowerBlinks to 1;
							Return;
						Else
							Set fTimer to (fTimer + GetSecondsPassed);
						Endif
					Else
						If (fTimer >= 1)
							NVDLC04Silo01MainframeLightREF.Enable;
							Set fTimer to 0;
							Set bPowerBlinks to 0;
							Return;
						Else
							Set fTimer to (fTimer + GetSecondsPassed);
						Endif
					Endif
				Endif
			Elseif (nState == 2)																										; This value gets set once players use the mainframe to open the silo's doors.
				If (bSiloOpen == 1)																										; This value gets set once players use the mainframe to open the silo's doors.
					If (bAlarmOff == 1)																									; If players have disabled the alarm, we activate turrets, setting them to friendly, and unlock the main door.
						SetAlly NVDLC04Silo01SecurityTurretFaction NVDLC04PlayerFaction;
						NVDLC04Silo01Floor01Turret01REF.SetGhost 0;
						NVDLC04Silo01Floor01Turret02REF.SetGhost 0;
						NVDLC04Silo01Floor01Turret03REF.SetGhost 0;
						NVDLC04Silo01Floor01Turret01REF.SetDestroyed 0;
						NVDLC04Silo01Floor01Turret02REF.SetDestroyed 0;
						NVDLC04Silo01Floor01Turret03REF.SetDestroyed 0;
						NVDLC04Silo01MainExitDoorREF.Unlock;
						Set nCount to 0;
						Set fTimer to 0;
						Set nState to 3;																									; We jump to state 3, which corresponds to "silo's been opened, and alarm sequence finished."
						Return;
					Else																													; If alarm is active when opening the silo's doors to Road01.
						If (nCount == 0)
							If (fTimer >= 1)																								; One second delay, to activate alarm markers, light marker, and bring the bots back to life.
								NVDLC04Silo01AlarmSoundMarker01REF.Enable;
								NVDLC04Silo01AlarmSoundMarker02REF.Enable;
								NVDLC04Silo01MainAlarmMarkerREF.Enable;
								If (bSentryHacked == 0)
									NVDLC04Silo01Bot02AREF.Resurrect;
									NVDLC04Silo01Bot02BREF.Resurrect;
									NVDLC04Silo01Bot02AREF.SetRestrained 0;
									NVDLC04Silo01Bot02BREF.SetRestrained 0;
									NVDLC04Silo01Bot02AREF.SetGhost 0;
									NVDLC04Silo01Bot02BREF.SetGhost 0;
									NVDLC04Silo01Bot02AREF.SetUnconscious 0;
									NVDLC04Silo01Bot02BREF.SetUnconscious 0;
									NVDLC04Silo01Bot02AREF.SetAV Aggression 2;
									NVDLC04Silo01Bot02BREF.SetAV Aggression 2;
									NVDLC04Silo01Bot02ADummyREF.Disable;
									NVDLC04Silo01Bot02BDummyREF.Disable;
									If (bSentryDoorsOpen == 0)
										NVDLC04Silo01SentryWall01REF.PlayGroup Forward 0;
										NVDLC04Silo01SentryWall02REF.PlayGroup Forward 0;
										Set bSentryDoorsOpen to 1;
									Endif
								Endif
								NVDLC04Silo01Bot01AREF.Resurrect;
								NVDLC04Silo01Bot01BREF.Resurrect;
								NVDLC04Silo01Bot01AREF.SetRestrained 0;
								NVDLC04Silo01Bot01BREF.SetRestrained 0;
								NVDLC04Silo01Bot01AREF.SetGhost 0;
								NVDLC04Silo01Bot01BREF.SetGhost 0;
								NVDLC04Silo01Bot01AREF.SetUnconscious 0;
								NVDLC04Silo01Bot01BREF.SetUnconscious 0;
								NVDLC04Silo01Bot01AREF.SetAV Aggression 2;
								NVDLC04Silo01Bot01BREF.SetAV Aggression 2;
								NVDLC04Silo01Bot01ADummyREF.Disable;
								NVDLC04Silo01Bot01BDummyREF.Disable;
								NVDLC04Silo01Sentry01DoorREF.Unlock;
								NVDLC04Silo01Sentry01DoorREF.SetOpenState 1;
								NVDLC04Silo01Bots01RoomTerminalREF.Unlock;
								NVDLC04Silo01Floor01Turret01REF.AddToFaction NVDLC04Silo01SecurityTurretFaction 1;
								NVDLC04Silo01Floor01Turret02REF.AddToFaction NVDLC04Silo01SecurityTurretFaction 1;
								NVDLC04Silo01Floor01Turret03REF.AddToFaction NVDLC04Silo01SecurityTurretFaction 1;
								Set fTimer to 0;
								Set nCount to 1;
								Return;
							Else
								Set fTimer to (fTimer + GetSecondsPassed);
							Endif
						Elseif (nCount == 1)
							If (bSentryHacked == 0)
								If (NVDLC04Silo01Bot01AREF.GetDisabled == 1)
									NVDLC04Silo01Bot01AREF.Enable;
									Return;
								Elseif (NVDLC04Silo01Bot01BREF.GetDisabled == 1)
									NVDLC04Silo01Bot01BREF.Enable;
									Return;
								Elseif (NVDLC04Silo01Bot02AREF.GetDisabled == 1)
									NVDLC04Silo01Bot02AREF.Enable;
									Return;
								Elseif (NVDLC04Silo01Bot02BREF.GetDisabled == 1)
									NVDLC04Silo01Bot02BREF.Enable;
									Return;
								Else
									Set nCount to 2;
									Return;
								Endif
							Else
								If (NVDLC04Silo01Bot01AREF.GetDisabled == 1)
									NVDLC04Silo01Bot01AREF.Enable;
									Return;
								Elseif (NVDLC04Silo01Bot01BREF.GetDisabled == 1)
									NVDLC04Silo01Bot01BREF.Enable;
									Return;
								Else
									Set nCount to 2;
									Return;
								Endif
							Endif
						Elseif (nCount == 2)																							; We move Bot02A into position.
							If (fTimer >= 1)
								If (bSentryHacked == 0)
									;NVDLC04Silo01MainAlarmMarkerREF.Disable;
									NVDLC04Silo01Bot02AREF.MoveTo NVDLC04Silo01AlarmBots02AppearMarkerREF;
								Endif
								Set nCount to 3;
								Return;
							Else
								Set fTimer to (fTimer + GetSecondsPassed);
							Endif		
						Elseif (nCount == 3)																							; Legacy step, in case we want to make the lights flicker on/off during the alarm (enable, disable, alarm marker).
							If (fTimer >= 1)
								;NVDLC04Silo01MainAlarmMarkerREF.Enable;
								Set fTimer to 0;
								Set nCount to 4;
								Return;
							Else
								Set fTimer to (fTimer + GetSecondsPassed);
							Endif
						Elseif (nCount == 4)																							; We move Bot02B into position.
							If (fTimer >= 1)
								;NVDLC04Silo01MainAlarmMarkerREF.Disable;
								If (bSentryHacked == 0)
									;NVDLC04Silo01Bot02BREF.MoveTo NVDLC04Silo01AlarmBots02AppearMarkerREF;
								Endif
								Set fTimer to 0;
								Set nCount to 5;
								Return;
							Else
								Set fTimer to (fTimer + GetSecondsPassed);
							Endif
						Elseif (nCount == 5)																							; We give AI to bots so that they rush into the mainframe's room, and we set Turrets to active.
							If (fTimer >= 1)
								;NVDLC04Silo01MainAlarmMarkerREF.Enable;
								If (bSentryHacked == 0)
									NVDLC04Silo01Bot02AREF.AddScriptPackage NVDLC04PackageSilo01Bots02MoveAttack;
									NVDLC04Silo01Bot02BREF.AddScriptPackage NVDLC04PackageSilo01Bots02Move02Attack;
								Endif
								NVDLC04Silo01Bot01AREF.AddScriptPackage NVDLC04PackageSilo01Bots01MoveAttack;
								NVDLC04Silo01Bot01BREF.AddScriptPackage NVDLC04PackageSilo01Bots01Move02Attack;
								NVDLC04Silo01Floor01Turret01REF.SetGhost 0;
								NVDLC04Silo01Floor01Turret02REF.SetGhost 0;
								NVDLC04Silo01Floor01Turret03REF.SetGhost 0;
								NVDLC04Silo01Floor01Turret04REF.SetGhost 0;
								NVDLC04Silo01Floor01Turret01REF.SetDestroyed 0;
								NVDLC04Silo01Floor01Turret02REF.SetDestroyed 0;
								NVDLC04Silo01Floor01Turret03REF.SetDestroyed 0;
								NVDLC04Silo01Floor01Turret04REF.SetDestroyed 0;
								NVDLC04Silo01Floor01Turret01REF.AddToFaction NVDLC04Silo01SecurityTurretFaction 1;
								NVDLC04Silo01Floor01Turret02REF.AddToFaction NVDLC04Silo01SecurityTurretFaction 1;
								NVDLC04Silo01Floor01Turret03REF.AddToFaction NVDLC04Silo01SecurityTurretFaction 1;
								NVDLC04Silo01Floor01Turret04REF.AddToFaction NVDLC04Silo01SecurityTurretFaction 1;
								If (bTurretHacked == 0)
									SetEnemy NVDLC04Silo01SecurityTurretFaction NVDLC04PlayerFaction 0 1;
								Endif
								NVDLC04Silo01MainExitDoorREF.Unlock;														; Doors to Road01 get unlocked here.
								NVDLC04Silo01MainExitDoorREF.SetOpenState 1;
								Set nCount to 0;
								Set fTimer to 0;
								Set nState to 3;																							; We jump to state 3, which corresponds to "silo's been opened, and alarm sequence finished."
								Return;
							Else
							Set fTimer to (fTimer + GetSecondsPassed);
							Endif
						Endif
					Endif
				Endif
			Elseif (nState == 3)																										; We check for GameDaysPassed from the moment players open the silo's doors. If three days pass since this time, we disable alarm.
				If (nCount == 0)
					Set fTime to GameDaysPassed;
					Set nCount to 1;
					Return;
				Elseif (nCount == 1)
					If (GameDaysPassed >= (fTime + 3))
						If (bAlarmOff == 0)
							NVDLC04Silo01AlarmSoundMarker01REF.Disable;
							NVDLC04Silo01AlarmSoundMarker02REF.Disable;
							NVDLC04Silo01MainAlarmMarkerREF.Disable;
						Endif
					Endif
				Endif
			Endif
		Endif
	Endif

END

SCN NVDLC04DivideWPTunnelerTriggerSCRIPT


;Enables tunnelers and starts their combat.
;DM 3/4/11


Float	fTimer
Int		iStage


BEGIN OnTriggerEnter Player

	Set iStage to 1;
	Set fTimer to 0;

END


BEGIN GameMode

	If  (iStage == 1) && (fTimer == 0)
		;Simple Delay here
		Set iStage to 2;
		Return;
	ElseIf  (iStage == 2) && (fTimer >= 0.5)
		NVDLC04DivideWPTunneler01REF.Enable;
		NVDLC04DivideWPTunneler02REF.Enable;
		Set iStage to 3;
		Return;
	ElseIf (iStage == 3) && (fTimer >= 0.6)
		NVDLC04DivideWPTunneler01REF.MoveTo TunnelerDrop01;
		NVDLC04DivideWPTunneler02REF.MoveTo TunnelerDrop02;
		Set iStage to 4;
		Return;
	ElseIf (iStage == 4) && (fTimer >= 0.7)
		NVDLC04DivideWPTunneler01REF.StartCombat NVDLC04DivideWPScout01REF;
		NVDLC04DivideWPTunneler02REF.StartCombat NVDLC04DivideWPScout01REF;
		WaterSplash01.Disable;
		WaterSplash02.Disable;
		Set iStage to 5;
	ElseIf (iStage == 5) && (fTimer >= 0.8)
		Disable;
		MarkForDelete;
	ElseIf (iStage >= 1) && (fTimer < 1)
		Set fTimer to (fTimer + GetSecondsPassed);
	EndIf

END

SCN NVDLC04DivideDisableTriggerSCRIPT


;Disables linked group of dudes
;DM 3/3/11
;DM 3/9/11 - Added MarkForDelete


REF LinkedDudes;

BEGIN OnTriggerEnter Player

	Set LinkedDudes to GetLinkedRef;
	LinkedDudes.Disable;
	Disable;
	MarkForDelete;

END

Scn	NVDLC04Silo01RevealCodeHolderSCRIPT

; Script to handle the scripted scene where EDE opens access to the animated window reveal of the missile in Hopeville's Missile Silo interior.

; J.S. 3/4/11
; TLS 03/16/2011 - Added ED-E energy blast to power-up sequence.
; TLS 03/23/2011 - Changed ED-E's weapon used.

Short	bActive					; 1 = Player just crossed a trigger near the console used to open the silo's reveal doors.
Short	bAccess					; 1 = EDE has awaken power at the silo, and moves aside to let players use the console that reveals the silo's shaft.
Short	bRevealed				; 1 = The doors to the silo's shaft have opened, and the reveal has taken place.
Short	bMaintOn				; 1 = Maintenance bot has been enabled.
Short	bPowerBlinks			; 1 = Toggle var, to control the blinking of lights on/off during EDE's silo powerup sequence.
Int		nState					; Operational variable, for large sequences.
Int		nCount					; Operational variable, for precise timings.
Float	fTimer					; Timer float.

BEGIN GameMode

	If (GetDisabled == 1)																										; If codebox is disabled, skip reading script.
		Return;
	Else																															; If codebox is enabled, continue with script.
		If (bActive == 1)																											; If trigger to make EDE powerup the silo has been crossed (player crossed).
			If (nState == 0)																										; State 0 runs EDE's approach to the silo reveal doors' console.
				If (NVDLC04DialogueEDE.nCommissary == 0 || NVDLC04Silo01EDEMarker01REF.GetDistance NVDLC04EDERef <= 660)
					;NVDLC04EDEREF.ResetAI;
					NVDLC04EDEREF.AddScriptPackage NVDLC04EDEActivateSilo1;								; We give EDE an AI package to approach the console.
					Set nState to 1;
					Return;
				Endif
			Elseif (nState == 1)																								; This state (1) controls events during EDE's movement toward the console.
				If (nCount == 0)
					NVDLC04EDERef.Say NVDLC04EDEExcited;														; EDE Barks once, positive beep.
					Set nCount to 1;
					Return;
				Elseif (nCount == 1)																							; If EDE has barked, and if it's close to its console destination marker, we'll play a sound once.
					If (NVDLC04EDERef.GetDistance NVDLC04Silo01EDEMarker01REF <= 32)
						NVDLC04EDEREF.UseWeapon NVDLC04WeapEDEUnlockZapper NVDLC04EDEREF NVDLC04SiloRevealTargetREF 1 0 0 1 1;
						NVDLC04SiloBlastDoorControlsREF.PlaySound OBJComputerTerminalForward;
						NVDLC04SiloBlastDoorControlsREF.PlayGroup Forward 0;
						Set nCount to 2;
						Return;
					Endif
				Elseif (nCount == 2)
					If (fTimer >= 1.2)
						NVDLC04SiloBlastDoorControlsREF.PlayGroup Backward 0;
						NVDLC04SiloBlastDoorControlsREF.PlaySound OBJComputerTerminalArc;
						Set nCount to 3;
						Set fTimer to 0;
						Return;
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
					Endif
				Elseif (nCount == 3)																							; We let four seconds pass from the moment that EDE's near the console, then we play a sound, and move to state 2.
					If (fTimer >= 2.5)
						NVDLC04SiloBlastDoorControlsREF.PlaySound3D OBJMainframeBoot;
						Set nCount to 0;
						Set fTimer to 0;
						Set nState to 2;
						Return;
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
					Endif
				Endif
			Elseif (nState == 2)																								; If EDE's just started to power up the silo (sound started to play above).
				Set fTimer to (fTimer + GetSecondsPassed);															; We set a timer to control the blinking of lights during the powerup sequence.
				If (nCount == 0)
					NVDLC04SiloBDControlsLightREF.Disable;															; This light is the red blinking light above the console, synched to whenever the console plays its anims.
					NVDLC04Silo01RevealLightMarkerREF.Enable;													; This marker is the parent of all lights, and light fixtures, involved in the blinking.
					SetStage NVDLC04MQ01a 40;
					Set nCount to 1;
					Return;
				Elseif (nCount == 1 && fTimer >= 0.3)
					NVDLC04Silo01RevealLightMarkerREF.Disable;
					Set nCount to 2;
					Return;
				Elseif (nCount == 2 && fTimer >= 0.4)
					NVDLC04Silo01RevealLightMarkerREF.Enable;
					Set nCount to 3;
					Return;
				Elseif (nCount == 3 && fTimer >= 0.5)
					NVDLC04Silo01RevealLightMarkerREF.Disable;
					Set nCount to 4;
					Return;
				Elseif (nCount == 4 && fTimer >= 0.8)
					NVDLC04SiloBlastDoorControlsREF.PlayGroup Left 0;
					NVDLC04EDETerminalSilo01ButtonREF.PlayGroup Forward 0;
					Set NVDLC04EDETerminalSilo01ButtonREF.nState to 1;
					NVDLC04Silo01RevealLightMarkerREF.Enable;
					Set nCount to 5;
					Return;
				Elseif (nCount == 5 && fTimer >= 0.9)
					NVDLC04Silo01RevealLightMarkerREF.Disable;
					Set nCount to 6;
					Return;
				Elseif (nCount == 6 && fTimer >= 1.1)
					NVDLC04Silo01RevealLightMarkerREF.Enable;
					Set nCount to 7;
					Return;
				Elseif (nCount == 7 && fTimer >= 1.5)
					NVDLC04Silo01RevealLightMarkerREF.Disable;
					Set nCount to 8;
					Return;
				Elseif (nCount == 8 && fTimer >= 1.6)
					NVDLC04Silo01RevealLightMarkerREF.Enable;
					Set nCount to 9;
					Return;
				Elseif (nCount == 9 && fTimer >= 1.7)
					NVDLC04Silo01WelcomeDoorREF.Unlock;
					NVDLC04SiloBDControlsLightREF.Enable;
					NVDLC04SiloBlastDoorControlsREF.Playgroup Specialidle 0;
					NVDLC04SiloBlastDoorControlsREF.SetDestroyed 0;
					NVDLC04EDETerminalSilo01ButtonREF.SetDestroyed 0;
					NVDLC04EDETerminalSilo01ButtonREF.PlayGroup Backward 0;
					Set NVDLC04EDETerminalSilo01ButtonREF.nState to 2;
					Set NVDLC04Silo01RevealBox.bAccess to 1;
					Set nCount to 0;
					Set fTimer to 0;
					Set nState to 3;
					Return;
				Endif
			Elseif (nState == 3)
				If (nCount == 0)
					If (bAccess == 1)
						If (fTimer >= 2)
							NVDLC04EDEREF.AddScriptPackage NVDLC04PackageEDESilo01RevealMoveAside;
							Set fTimer to 0;
							Set nCount to 1;
							Return;
						Else
							Set fTimer to (fTimer + GetSecondsPassed);
						Endif
					Endif
				Elseif (nCount == 1)
					If (bRevealed == 1)
						If (bMaintOn == 0)
							If (fTimer >= 2)
								Set fTimer to 0;
								Set bMaintOn to 1;
								Return;
							Else
								Set fTimer to (fTimer + GetSecondsPassed);
							Endif
						Else
							If (fTimer >= 2)
								If (NVDLC04Silo01MaintenanceBot02REF.GetDisabled != 1)
									NVDLC04Silo01MaintenanceBot02REF.PlayIdle SpecialIdle;
									NVDLC04Silo01MaintenanceBot02REF.PlayGroup SpecialIdle 0;
								Endif
								NVDLC04EDERef.Say NVDLC04EDEHappy;
								Set fTimer to 0;
								Set nCount to 2;
								Return;
							Else
								Set fTimer to (fTimer + GetSecondsPassed);
							Endif
						Endif
					Else
						If (bPowerBlinks == 0)
							If (fTimer >= 1)
								NVDLC04SiloBlastDoorControlsREF.Playgroup Right 0;
								NVDLC04SiloBDControlsLightREF.Disable;
								Set fTimer to 0;
								Set bPowerBlinks to 1;
								Return;
							Else
								Set fTimer to (fTimer + GetSecondsPassed);
							Endif
						Else
							If (fTimer >= 1)
								NVDLC04SiloBlastDoorControlsREF.Playgroup Specialidle 0;
								NVDLC04SiloBDControlsLightREF.Enable;
								Set fTimer to 0;
								Set bPowerBlinks to 0;
								Return;
							Else
								Set fTimer to (fTimer + GetSecondsPassed);
							Endif
						Endif
					Endif
				Elseif (nCount == 2)
					If (fTimer >= 3)
						NVDLC04EDEREF.ResetAI;
						Set fTimer to 0;
						Set nCount to 3;
						Return;
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
					Endif
				Elseif (nCount == 3)
					If (fTimer >= 1.3)
						NVDLC04Silo01ReactorDoorREF.Unlock;
						NVDLC04Silo01ReactorDoorREF.SetOpenState 1;
						SetObjectiveCompleted NVDLC04MQ01a 35 1;
						Set fTimer to 0;
						Set nCount to 4;
						Disable;
						Return;
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
					Endif
				EndIf
			Endif
		Endif
	Endif

END

Scn		NVDLC04Road01MMCamp01GuardsAwakenTriggerSCRIPT

; Enables a Marked Men upon crossing its associated trigger.

; J.S. 3/2/11
; J.S. 8/5/11 - Added new call to update a var to control quest target. About a month ago I greatly expanded this script, to control the Red Glare reveal scene, but didn't add comments on these changes because they completely re-structured the script.

Short	nScene	; 1 = Marked Men Marauder in Camp01 has been enabled, 2 = Marked Men Hunter in Camp01 has been enabled.
Float	fTimer


BEGIN OnTriggerEnter Player

	If (nScene < 1)																														; If not triggered yet,
		NVDLC04Road01MMCamp01Guard01REF.Enable;																	; We enable a camp guard in Camp01.
		;NVDLC04Road01MMCamp01Hunter01REF.RemoveItem NVDLC04MarkedMenHunterTier4RangeWeapons 1;
		;NVDLC04Road01MMCamp01Hunter01REF.RemoveItem NVDLC04MarkedMenHunterTier5RangeWeapons 1;
		;NVDLC04Road01MMCamp01Hunter01REF.RemoveItem NVDLC04MarkedMenHunterTier5PlusRangeWeapons 1;
		Set nScene to 1;																												; Set the doonce var to 1, then disable, and mark for deletion, the trigger volume.
		Set NVDLC04Road01Camp01Box.bTargetReached to 1;
		Return;
	Endif

END

BEGIN GameMode

	If (nScene == 1)
		NVDLC04Road01MMCamp01Hunter01REF.SetGhost 1;																; We set ambush guard in Camp01 to Ghost, and give him the red glare weapon.
		NVDLC04Road01MMCamp01Hunter01REF.SetAV Explosives 10;
		NVDLC04Road01MMCamp01Hunter01REF.SetAV Perception 1;
		NVDLC04Road01MMCamp01Hunter01REF.SetAV Strength 1;
		Set nScene to 2;																												; Set the doonce var to 1, then disable, and mark for deletion, the trigger volume.
		Return;
	Elseif (nScene == 2)
		If (Player.GetPos Z <= 13990)
			If (Player.GetPos Y >= 4700)
				If (Player.GetPos X >= 4800)
					NVDLC04Road01MMCamp01Hunter01REF.Enable;														; We enable a camp guard in Camp01.
					EnableFastTravel 0;
					Set nScene to 3
					Return;
				Endif
			Endif
		Endif
	Elseif (nScene == 3)
		;NVDLC04Road01MMCamp01Hunter01REF.RemoveAllItems;
		Set nScene to 4;
		Return;
	Elseif (nScene == 4)
		NVDLC04Road01MMCamp01Hunter01REF.AddScriptPackage NVDLC04PackageRoad01MMCamp01CombatGuard01;
		;NVDLC04Road01MMCamp01Hunter01REF.AddItemHealthPercent NVDLC04WeapRedGlare 1 0.6;
		;NVDLC04Road01MMCamp01Hunter01REF.AddItem AmmoNVDLC04Rocket 9;
		;NVDLC04Road01MMCamp01Hunter01REF.AddItem NVDLC04CondMarkedArmorNCR 1;
		;NVDLC04Road01MMCamp01Hunter01REF.AddItem NVDLC04MarkedMenHelmets 1;
		;NVDLC04Road01MMCamp01Hunter01REF.EquipItem NVDLC04WeapRedGlare;
		Set nScene to 5;
		Return
	Elseif (nScene == 5)
		Set fTimer to (fTimer + GetSecondsPassed);
		If (NVDLC04Road01MMCamp01Hunter01REF.GetLOS Player == 1 || fTimer >= 4)
			NVDLC04Road01MMCamp01Hunter01REF.AddScriptPackage NVDLC04PackageRoad01MMCamp01RedGlareGuard01;
			NVDLC04Road01MMCamp01Hunter01REF.SetGhost 0;
			Set fTimer to 0;
			Set nScene to 6;
		Endif
	Elseif (nScene == 6)
		Set fTimer to (fTimer + GetSecondsPassed);
		If (fTimer >= 7 || NVDLC04Road01MMCamp01Hunter01REF.GetDistance Player <= 380)
			If (fTimer >= 11 || NVDLC04Road01MMCamp01Hunter01REF.GetCurrentAIPackage != 33)
				NVDLC04Road01MMCamp01Hunter01REF.ResetAI;
				Set nScene to 7;
				Set fTimer to 0;
				Return;
			Endif
		Endif
	Elseif (nScene == 7)
		;NVDLC04Road01MMCamp01Hunter01REF.UnEquipItem NVDLC04WeapRedGlare;
		NVDLC04Road01MMCamp01Hunter01REF.SetGhost 0;
		NVDLC04RedGlareAmbushBlockREF.Disable;
		NVDLC04RedGlareAmbushBlockREF.MarkForDelete;
		Set nScene to 8;
		Return;
	Elseif (nScene == 8)
		If (NVDLC04Road01MMCamp01Hunter01REF.GetDistance Player <= 380 || NVDLC04Road01MMCamp01Hunter01REF.GetDead == 1 || NVDLC04Road01CampBarrierWarhead01REF.GetDisabled == 1)
			;NVDLC04Road01MMCamp01Hunter01REF.RemoveItem NVDLC04WeapRedGlare 1;
			;NVDLC04Road01MMCamp01Hunter01REF.AddItem NVDLC04MarkedMenHunterTier5RangeWeapons 1;
			Set nScene to 9;
			Return;
		Endif
	Elseif (nScene == 9)
		;NVDLC04Road01MMCamp01Hunter01REF.EVP;
		Set nScene to 10;
		Return;
	Elseif (nScene == 10)
		Disable;
		MarkForDelete;
	Endif

END

Scn		NVDLC04Road01MMCamp01CodeHolderSCRIPT

; Script to handle timing of extra explosions, AI behaviors, barks, or any other event that needs to occur when players acquire the Sonic Detonator from MM Camp01.

; J.S. 3/2/11
; J.S. 5/24/11 - Added bDetonator var, and code associated to triggers that turn this var to 1. This is done for better framing of flares/ambush after players get the detonator.
; J.S. 8/5/11 - Added bTargetReached var.

Short	bTargetReached ; Variable used for the laser detonator quest targets - updated through trigger that starts the red glare reveal scene.
Short	bDoOnce		; Variable to control important, one-shot, event.
Short	bDetonator	; Variable to account for the Laser-Detonator being picked up by players.
Int		nState		; Variable to control the scene's states.
Int		nCount		; Variable to assist in each of the scene's states to flow in proper timing/frames.
Float	fTimer		; Variable used for counters.
Ref	rDummy01	; Var to grab reference of flying cars.
Ref	rDummy02	; Var to grab reference of flying cars.


BEGIN OnTriggerEnter Player					; This event is called out by other scripts - players can't physically reach this trigger. It serves to clean up the spawned dudes, to reduce actor count when necessary.
														; We run checks to see if the actors can be safely disabled (to avoid vanishing them in front of players' view).
	If (NVDLC04Road01MMCamp01Rush01REF.GetDead == 1 || (NVDLC04Road01MMCamp01Rush01REF.IsInCombat != 1 && NVDLC04Road01MMCamp01Rush01REF.GetDistance Player >= 1200))
		NVDLC04Road01MMCamp01Rush01REF.Disable;
	Endif
	If (NVDLC04Road01MMCamp01Rush02REF.GetDead == 1 || (NVDLC04Road01MMCamp01Rush02REF.IsInCombat != 1 && NVDLC04Road01MMCamp01Rush02REF.GetDistance Player >= 1200))
		NVDLC04Road01MMCamp01Rush02REF.Disable;
	Endif
	If (NVDLC04Road01MMCamp01Rush03REF.GetDead == 1 || (NVDLC04Road01MMCamp01Rush03REF.IsInCombat != 1 && NVDLC04Road01MMCamp01Rush03REF.GetDistance Player >= 1200))
		NVDLC04Road01MMCamp01Rush03REF.Disable;
	Endif
	If (NVDLC04Road01MMCamp01Rush04REF.GetDead == 1 || (NVDLC04Road01MMCamp01Rush04REF.IsInCombat != 1 && NVDLC04Road01MMCamp01Rush04REF.GetDistance Player >= 1200))
		NVDLC04Road01MMCamp01Rush04REF.Disable;
	Endif
	If (NVDLC04Road01MMCamp01Rush05REF.GetDead == 1 || (NVDLC04Road01MMCamp01Rush05REF.IsInCombat != 1 && NVDLC04Road01MMCamp01Rush05REF.GetDistance Player >= 1200))
		NVDLC04Road01MMCamp01Rush05REF.Disable;
	Endif
	If (NVDLC04Road01MMCamp01Hunter01REF.GetDead == 1 || (NVDLC04Road01MMCamp01Hunter01REF.IsInCombat != 1 && NVDLC04Road01MMCamp01Hunter01REF.GetDistance Player >= 1200))
		NVDLC04Road01MMCamp01Hunter01REF.Disable;
	Endif

END


BEGIN GameMode

	If (GetDisabled == 1)
		Return;																											; To prevent the script from running if we disable the codebox that houses it.
	Else
		If (nState == 0)
			If (nCount == 0)
				If (bDetonator == 1)																					; Before players acquire the Sonic Detonator, we may want to do some stuff in this area.
					NVDLC04EDERef.Say NVDLC04EDEHappy;													; But if players just got the Sonic Detonator, we make EDE happy, and we tell other codeboxes to despawn their encounters.
					NVDLC04Road01MidBox.EnterTrigger Player;
					NVDLC04Road01StartBox.EnterTrigger Player;
					Set fTimer to 0;
					Set nState to 1;
					Return;
				Else
					If (Player.GetItemCount NVDLC04WeapSonicDetonator > 0)
						Set fTimer to (fTimer + GetSecondsPassed);
						If (fTimer >= 15)
							Set bDetonator to 1;
							Return;
						Endif
					Endif
				Endif
			Endif
		Elseif (nState == 1)																							; For when players have acquired the Sonic Detonator, we may want to do some things in this area before players blow it up.
			Set fTimer to (fTimer + GetSecondsPassed);														; We run a timer, and enable the enemies in several steps, for better timing.
			If (nCount == 0 && fTimer > 1)
				NVDLC04Road01MMCamp01Rush01REF.Enable;												; We enable the first dude, and also fire up a flare effect. We repeat this step below, for scene-setting purposes.
				NVDLC04Road01Camp01Flarepoint01REF.FireWeapon NVDLC04WeapFlareGun;
				PlaySound NVDLC04WPNFlareGunFire3DDist;
				Set nCount to 1;
				Return;
			Elseif (nCount == 1 && fTimer > 1.3)
				NVDLC04Road01MMCamp01Rush04REF.Enable;
				Set nCount to 2;
				Return;
			Elseif (nCount == 2 && fTimer > 3)
				If (NVDLC04EDERef.IsInCombat != 1)
					NVDLC04EDERef.Say NVDLC04EDEScared;												; If EDE's not in combat yet, we make it bark a worried line.
				Endif
				NVDLC04Road01MMCamp01Rush02REF.Enable;
				NVDLC04Road01Camp01Flarepoint02REF.FireWeapon NVDLC04WeapFlareGun;
				PlaySound NVDLC04WPNFlareGunFire3DDist;
				Set nCount to 3;
				Return;
			Elseif (nCount == 3 && fTimer > 5)
				NVDLC04Road01MMCamp01Rush03REF.Enable;
				NVDLC04Road01Camp01Flarepoint03REF.FireWeapon NVDLC04WeapFlareGun;
				PlaySound NVDLC04WPNFlareGunFire3DDist;
				Set nCount to 4;
				;Set fTimer to 0;
				Return;
			Elseif (nCount == 4 && fTimer > 5.3)
				If (IsPS3 != 1)
					NVDLC04Road01MMCamp01Rush05REF.Enable;
				Endif
				Set nCount to 0;
				Set fTimer to 0;
				Set nState to 2;																						; Once we're done with all spawns, we set the nState to 2, and reset all operational vars.
				Return;
			Endif
		Elseif (nState == 2)																						; For when players are facing the boss of Road01, before destroying the exit barricade (bridge).
			If (nCount == 0)
				If (NVDLC04Road01CampBarrierWarhead01REF.GetDisabled == 1)					; If the barrier out of Camp01 is destroyed, we'll make EDE bark a happy line, below, after a 3 second timer.
					If (fTimer >= 0.4)
						NVDLC04BarrierCamp01XMarker01REF.PlaceAtMe NVDLC04Silo02ElevatorExplosionSpecial;
						EnableFastTravel 1;																		; We re-enable fast traveling, which was disabled in the NVDLC04Road01GlareBox codebox.
						Set nCount to 1;
						Set fTimer to 0;
						Return;
					Else
						Set fTimer to (fTimer + GetSecondsPassed);
					Endif
				Endif
			Elseif (nCount == 1)
				If (fTimer >= 0.5)
					NVDLC04BarrierCamp01XMarker04REF.PlaceAtMe NVDLC04WarheadNukeExplosion;
					Set nCount to 2;
					Set fTimer to 0;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Elseif (nCount == 2)
				If (fTimer >= 1.2)
					NVDLC04BarrierCamp01XMarker03REF.PlaceAtMe NVDLC04WarheadNukeExplosion;
					NVDLC04BarrierCamp01XMarker01REF.Disable;
					Set nCount to 3;
					Set fTimer to 0;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Elseif (nCount == 3)
				If (fTimer >= 0.7)
					NVDLC04BarrierCamp01XMarker02REF.PlaceAtMe NVDLC04WarheadNukeExplosion;
					Set nCount to 4;
					Set fTimer to 0;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Elseif (nCount == 4)
				If (fTimer >= 0.2)
					NVDLC04BarrierCamp01XMarker02REF.Disable;
					Set nCount to 5;
					Set fTimer to 0;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Elseif (nCount == 5)
				If (fTimer >= 3)
					NVDLC04EDERef.Say NVDLC04EDEHappy;
					Set nCount to 0;
					Set fTimer to 0;
					Set nState to 3;																					; We move the code to nState 3.
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);
				Endif
			Endif																										; We may use nState 3, and 4, but the bridge's explosion has already been handled in the NVDLC04ExitBarrierCodeHolderSCRIPT.
		Elseif (nState == 3)																						; In this state, the bridge has been just blown-up. We're going to use three steps to liven up the event (explosions, baby!).
		Elseif (nState == 4)																						; State 4 handles the aftermath of the bridge's explosion, primarily dealing with AI in the area, or EDE.
		Endif
	Endif

END

SCN NVDLC04KillTriggerSCRIPT


;Kills the player if they fall off the intro building.
;DM 3/2/11
;DM 3/16/11 - Changed to an essential death reload from a kill volume.
;DM 3/23/11 - Changed it back to a kill volume because essential death reload just crashes the system.
; JSH 05/05/11 - Trigger now kills non-player, non-EDE references.
; CES 06/02/11 - Changed the KillActor call to not refernce the player to avoid kill cams.


SHORT bDoOnce;
Ref rEnteringRef;				; Used to track who entered the trigger.

BEGIN OnTrigger

	If (GetActionRef == Player) && (bDoOnce == 0)

		Player.Kill;
		ApplyImageSpaceModifier FadeToBlack9sISFX;
		Set bDoOnce to 1;

	ElseIf ( GetActionREF == NVDLC04EDEREF )

		NVDLC04EDEREF.MoveTo Player;

	Else

		Set rEnteringRef to GetActionRef;

		If ( rEnteringRef.GetDead == 0 )
			rEnteringRef.KillActor;
		EndIf

	EndIf

END

SCN NVDLC04DivideEnableTriggerSCRIPT


;Enables linked group of dudes
;DM 3/2/11
;DM 3/9/11 - Added MarkForDelete


REF LinkedDudes;

BEGIN OnTriggerEnter Player

	Set LinkedDudes to GetLinkedRef;
	LinkedDudes.Enable;
	Disable;
	MarkForDelete;

END

SCN NVDLC04DivideFlareLaunchSCRIPT


;Launches a flare from the linked ref
;DM 3/2/11
;DM 3/9/11 - Added MarkForDelete


REF FlareLaunch;

BEGIN OnTriggerEnter Player

	Set FlareLaunch to GetLinkedRef;
	FlareLaunch.FireWeapon NVDLC04WeapFlareGun;
	PlaySound NVDLC04WPNFlareGunFire3DDist;
	Disable;
	MarkForDelete;

END

SCN NVDLC04DivideFlares01TriggerSCRIPT


;Launches flares when the player enters the trigger
;DM 3/1/11


SHORT Launch;	;Flares can launch
INT DoOnce;		;Increments which flare can go off
Float fTimer;		;Timer delay

BEGIN OnTriggerEnter Player

	Set Launch to 1;

END


BEGIN GameMode

	If (Launch == 1) && (DoOnce == 0) && (fTimer >= .1)
		DivideFlareMarker01.FireWeapon NVDLC04WeapFlareGun;
		PlaySound NVDLC04WPNFlareGunFire3DDist;
		Set DoOnce to 1;
	Elseif	(Launch == 1) && (DoOnce == 1) && (fTimer >= .25)
		DivideFlareMarker02.FireWeapon NVDLC04WeapFlareGun;
		PlaySound NVDLC04WPNFlareGunFire3DDist;
		Set DoOnce to 2;
	Elseif	(Launch == 1) && (DoOnce == 2) && (fTimer >= 1)
		DivideFlareMarker03.FireWeapon NVDLC04WeapFlareGun;
		PlaySound NVDLC04WPNFlareGunFire3DDist;
		Set DoOnce to 3;
	Elseif	(Launch == 1) && (DoOnce == 3) && (fTimer >= 1.5)
		DivideFlareMarker04.FireWeapon NVDLC04WeapFlareGun;
		PlaySound NVDLC04WPNFlareGunFire3DDist;
		Set DoOnce to 4;
	Elseif (Launch ==1) && (DoOnce <= 3) && (fTimer < 2)
		Set fTimer to (fTimer + GetSecondsPassed);
	Endif

END

Scn		NVDLC04Silo01MainframeSCRIPT

; Script to handle opening the exit out of the Silo01. It also triggers the event where bots respond to the alarms and swarm the player. Its levels of interactivity are almost fully fleshed out.

; J.S. 3/1/11
; TLS 03/16/2011 - Moved scripting to OnHit for ED-E's zap.
; TLS 03/29/2011 - Added animated button.
; TLS 04/12/2011 - Added SetDestroyed call.
; TLS 04/13/2011 - Added objective for player to use mainframe.
; TLS 05/12/2011 - Fixing scripting to prevent player being told he needs the codes if he has them.
; J.S. 8/12/11 - Added call to move EDE near the activator, only if EDE's z position is below the first floor of the silo.

Short	bOpenOnce			; 1 = The silo has been opened.
Short	bEDEActivated		; 1 = EDE has activated this mainframe.
Short	bWarningShown	; 1 = MSG box to warn about mainframe's functions has been shown, so we'll use a non-box msg for any next interactions if EDE has not yet unlocked the mainframe.

Int		nDisplay				; 1 = Show messages if EDE has not unlocked the mainframe. 2 = Show messages if EDE has unlocked the mainframe. 3 = Shows Message to ask ED-E to unlock terminal.
Int		nSecurityLevel		; 0 = All aggressive. 1 = Turrets off. 2 = Turrets friendly. 3 = All bots off.
Int		nButton				; Value corresponds with button pressed as defined in the MSG entities used.
Int		nState				; Operational states.
Int		nCount				; Operational counter.
Float	fTimer				; Timer float.


BEGIN OnActivate

	If (IsActionREF Player == 1)																		; If player is the activator agent.
		If (NVDLC04Silo01Box.bEDEMainframeActivated == 1)								; Check for EDE having unlocked the mainframe.
			If (NVDLC04Silo01Box.bSiloOpen == 0)												; If EDE has unlocked the mainframe, we check to see if players have already opened the silo.
				ShowMessage NVDLC04Silo01MainframeAccessMSG;						; Display message that allows opening the silo
				Set nDisplay to 2;
				Return;
			Else
				ShowMessage NVDLC04Silo01MainframeOpenMSG;							; We display a different message if the silo's been already opened.
				Return;
			Endif
		Else																								; If EDE hasn't unlocked the mainframe.
			If (GetHasNote NVDLC04Silo02LaunchCode == 0)
				If (bWarningShown == 0)																; Check to display a message-box for the first time.
					ShowMessage NVDLC04Silo01MainframeMSG;
					Set bWarningShown to 1;
					Set nDisplay to 1;
					Return;
				Else																						; If the warning sign (message-box) has been displayed, then display upper-corner variant (does not stop gameplay).
					ShowMessage NVDLC04Silo01MainframeMSG02;
					Return;
				Endif
			Else
				ShowMessage NVDLC04Silo01MainframeCodesMSG;
				Set nDisplay to 3;
			EndIf
		Endif
	Endif

END


BEGIN OnHit NVDLC04EDEREF

	If (NVDLC04Silo01Box.bEDEMainframeActivated == 0)									; Check to see if the mainframe hasn't been unlocked by EDE already.
		If (Player.GetHasNote NVDLC04Silo02LaunchCode == 1)							; Check to make sure the player has the security codes.
			NVDLC04Silo01MainframeLightREF.Enable;										; The mainframe light turns on. Its blinking effect is scripted in the NVDLC04Silo01Box codebox.
			ShowMessage NVDLC04Silo01MainframeEDEMSG;								; Display a message warning (corner field) about EDE's feat.
			Set NVDLC04Silo01Box.bEDEMainframeActivated to 1;							; Set EDEactivation var to 1;
			NVDLC04Silo01MainframeREF.PlayGroup Left 0;
			Set NVDLC04Silo01Box.nState to 1;													; We move the scripted scene's codebox to state 1 (in case we want to run any gamemode scene at this point).
			SetStage NVDLC04MQ01a 55;
		EndIf
	EndIf

END


BEGIN GameMode																					; This GameMode block will only run when nDisplay is greater than 0 (only happens if players have had a message displayed on activation of mainframe).

	If (nDisplay == 1)																					; The following message is just a warning. Only option is to exit back to normal game mode.
		Set nButton to GetButtonPressed;
		If (nButton == 0)
			Set nDisplay to 0;
			SetObjectiveDisplayed NVDLC04MQ01a 40 1;
			Return;
		Endif
	Elseif (nDisplay == 2)																				; This message allows opening the silo's doors (button 1).
		Set nButton to GetButtonPressed;
		If (nButton == 0)
			Set nDisplay to 0;
			Return;
		Elseif (nButton == 1)																			; If players choose to open the silo's doors, check to see if they haven't been opened yet.
			If (NVDLC04Silo01Box.bSiloOpen != 1)												; If doors are still closed, then we update the silo's scripted scene codebox's variables to make it run its "silo opening / alarm" scene.
				NVDLC04EDETerminalButtonMainframeREF.PlayGroup Forward 1;
				PlaySound NVDLC04DRSProjPurityBulkheadOpen;
				;Set NVDLC04EDETerminalButtonMainframeREF.nState to 2;
				NVDLC04Silo01MainframeLightREF.Disable;									; The mainframe light turns off.
				Set NVDLC04Silo01Box.bSiloOpen to 1;											; Silo's doors will be recognized as open, according to silo's codebox.
				Set NVDLC04Silo01Box.nState to 2;												; Silo's codebox moves to state 2 (alarm / silo door opening state).
				SetObjectiveCompleted NVDLC04MQ01a 55 1;
				SetDestroyed 1;
			Endif
		Endif
	ElseIf (nDisplay == 3)
		Set nButton to GetButtonPressed;
		If (nButton == 0)
			Set nDisplay to 0;
			Return;
		ElseIf (nButton == 1)
			If (NVDLC04EDERef.GetPos Z < 14700)
				NVDLC04EDERef.MoveTo NVDLC04Silo01AlarmBots01FightMarkerREF;
			Endif
			NVDLC04EDERef.Say NVDLC04EDEHappy;															; EDE barks congratulatory note, and receives package to activate the mainframe (goes to the mainframe, OnEnd activates it).
			NVDLC04EDERef.AddScriptPackage NVDLC04PackageEDESilo01MainframeActivate;
			NVDLC04Silo01MainframeREF.PlayGroup Forward 1;
		EndIf
	Endif

END

Scn		NVDLC04Silo01Floor02AwakenBotTriggerSCRIPT

; This script awakens the second floor sentry bot upon crossing its associated trigger.

; J.S. 3/1/11

Short	bDoOnce				; 1 = bot enabled, and trigger is now ready for deletion.

BEGIN OnTriggerEnter Player

	If (bDoOnce == 0)													; Simple DoOnce check, to enable the robot of silo's second floor. We keep it disabled to help performance, and aggro, when players are turning the silo's power back on (bottom level).
		NVDLC04Silo01Floor02Bot01REF.Enable;
		Set bDoOnce to 1;
		Disable;
		MarkForDelete;
	Endif

END

Scn		NVDLC04Road01ExitBarrierCodeHolderSCRIPT

; Script to handle timing of extra explosions, AI behaviors, barks, or any other event that needs to occur when players blow up the bridge leading to the Collapsed Overpass Tunnel.

; J.S. 3/1/11

Short	bDoOnce		; Variable to control important, one-shot, event.
Short	bBossShort	; Variable to control AI Packages used by Blister.
Int		nState		; Variable to control the scene's states.
Int		nCount		; Variable to assist in each of the scene's states to flow in proper timing/frames.
Float	fTimer		; Variable used for timers.
Ref	rDummy01	; Var to grab reference of flying cars.
Ref	rDummy02	; Var to grab reference of flying cars.


BEGIN GameMode

	If (GetDisabled == 1)
		Return;																										; To prevent the script from running if we disable the codebox that houses it.
	Else
		If (nState == 0)
			If (nCount == 0)
				If (Player.GetItemCount NVDLC04WeapSonicDetonator > 0)								; Before players acquire the Sonic Detonator, we may want to do some stuff in this area.
					Set nState to 1;
					Return;
				Endif
			Endif
		Elseif (nState == 1)																						; For when players have acquired the Sonic Detonator, we may want to do some things in this area before players blow it up.
			If (nCount == 0)
				If (NVDLC04Road01BossBarrierWarhead01REF.GetDisabled == 1)					; If Players just blew up the first barricade, we do boss stuff in the next state.
					NVDLC04Road01BossBarrierCar01REF.Disable;										; We get rid of a safeguard car that blocks targeting it from beyond the boss area.
					NVDLC04Road01Camp01Box.EnterTrigger Player;									; We call triggerenter events on two codeboxes responsible for spawns, to clean up actor count (this box handles the Sonic Detonator camp spawns).
					NVDLC04Road01StartBox.EnterTrigger Player;										; This box handles the HQ spawns.
					Set nState to 2;
					Return;
				Endif
			Endif
		Elseif (nState == 2)																						; For when players are facing the boss of Road01, before destroying the exit barricade (bridge).
			If (NVDLC04Road01ExitBarrierWarhead01REF.GetDisabled == 0)							; If the bridge barricade is intact, we want to do different things than otherwise.
				Set fTimer to (fTimer + GetSecondsPassed);												; We run a timer, fire up a few flares, and enable the encounter spawns for the boss battle.
				If (nCount == 0 && fTimer > 4)
					NVDLC04EDERef.Say NVDLC04EDEScared;
					NVDLC04Road01BossCampMM01REF.Enable;
					NVDLC04Road01BossCampFlarepoint01REF.FireWeapon NVDLC04WeapFlareGun;
					PlaySound NVDLC04WPNFlareGunFire3DDist;
					Set nCount to 1;
					Return;
				Elseif (nCount == 1 && fTimer > 5)
					NVDLC04Road01BossCampMM02REF.Enable;
					NVDLC04Road01BossCampFlarepoint02REF.FireWeapon NVDLC04WeapFlareGun;
					PlaySound NVDLC04WPNFlareGunFire3DDist;
					Set nCount to 2;
					Return;
				Elseif (nCount == 2 && fTimer > 6)
					NVDLC04Road01BossCampMM03REF.Enable;
					NVDLC04Road01BossCampFlarepoint03REF.FireWeapon NVDLC04WeapFlareGun;
					PlaySound NVDLC04WPNFlareGunFire3DDist;
					Set nCount to 3;
					Return;
				Elseif (nCount == 3 && fTimer > 6)
					NVDLC04Road01BossREF.Enable;
					Set nCount to 4;
					Return;
				Elseif (nCount == 4 && fTimer > 6.2)
					If (IsPS3 != 1)
						NVDLC04Road01BossCampMM04REF.Enable;
					Endif
					Set nCount to 5;
					Return;
				Elseif (nCount == 5 && fTimer > 6.3)
					If (IsPS3 != 1)
						NVDLC04Road01BossCampMM05REF.Enable;
					Endif
					Set nCount to 6;
					Return;
				Elseif (nCount == 6)
					If (bBossShort == 0)
						If (NVDLC04Road01BossREF.GetDead != 1)
							If (Player.GetEquipped NVDLC04WeapSonicDetonator == 1)
								If (fTimer >= 3)
									If (Player.GetDistance NVDLC04Road01MMBossCampRushBossMarker <= 2048)
										NVDLC04Road01BossREF.AddScriptPackage NVDLC04PackageRoad01BossFacePlayerMoveShort;
										Set bBossShort to 1;
										Set fTimer to 0;
										Return;
									Endif
								Endif
							Endif
						Endif
					Else
						If (fTimer >= 3)
							If (NVDLC04Road01BossREF.GetDead != 1)
								If (Player.GetEquipped NVDLC04WeapSonicDetonator != 1)
									NVDLC04Road01BossREF.AddScriptPackage NVDLC04PackageRoad01BossFacePlayerMove;
									Set bBossShort to 0;
									Set fTimer to 0;
									Return;
								Endif
							Endif
						Endif
					Endif
				Endif
			Else																										; In the event of the exit barricade being disabled, we jump to nState 3. Note there's no safeguard code to spawn NPCs.
				Set nCount to 0;																					; This is due to being materially impossible to blow the warhead in less than 6.3 secs from the moment of blowing up the one leading to the boss area.
				Set fTimer to 0;
				Set nState to 3;
				EnableFastTravel 1;																				; Safety measure, in case players found a way out of the laser-detonator area without blowing up the first warhead.
				If (NVDLC04Road01BossREF.GetDead != 1)												; We force Blister to move near the explosion, if he's alive when the warhead goes off.
					NVDLC04Road01BossREF.AddScriptPackage NVDLC04PackageRoad01BossFacePlayerMoveShort;
					Set bBossShort to 1;
				Endif
				Return;
			Endif
		Elseif (nState == 3)																						; In this state, the bridge has been just blown-up. We're going to use three steps to liven up the event (explosions, baby!).
			If (nCount == 0)																							; First step of this state occurs here.
				If (fTimer > 0.4)																						; A simple, 0.4 delay.
					NVDLC04BarrierExitXMarker01REF.PlaceAtMe NVDLC04WarheadNukeExplosion;	; Fire!
					Set rDummy01 to (NVDLC04BarrierExitXMarker03REF.PlaceAtMe Car01);
					NVDLC04BarrierExitXMarker03REF.SetPos Z 13260;
					NVDLC04Road01BossBarricadeCar01REF.Enable;
					Set fTimer to 0;
					Set nCount to 1;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);											; We run a timer to control what we're to do during the following steps, if the one second delay has not occurred yet.
				Endif
			Elseif (nCount == 1)
				If (fTimer > 1.2)																						; delay.
					NVDLC04BarrierExitXMarker03REF.PlaceAtMe NVDLC04WarheadNukeExplosion;	; Fire!
					NVDLC04BarrierExitXMarker01REF.PlaceAtMe BusNukeExplosion;
					Set rDummy02 to (NVDLC04BarrierExitXMarker02REF.PlaceAtMe Car01);
					NVDLC04BarrierExitXMarker02REF.SetPos Z 14000;
					NVDLC04BarrierExitXMarker02REF.SetPos X 11300;
					Set fTimer to 0;
					Set nCount to 2;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);											; We run a timer to control what we're to do during the following steps, if the one second delay has not occurred yet.
				Endif
			Elseif (nCount == 2)
				If (fTimer > 0.8)																						; one second delay.
					NVDLC04BarrierExitXMarker02REF.PlaceAtMe NVDLC04WarheadNukeExplosion;	; Fire!
					NVDLC04BarrierExitXMarker02REF.PlaceAtMe BusNukeExplosion;
					PlaySound NVDLC04QSTCarFly
					Set fTimer to 0;
					Set nCount to 3;
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);											; We run a timer to control what we're to do during the following steps, if the one second delay has not occurred yet.
				Endif
			Elseif (nCount == 3)
				If (fTimer >= 3.1)																					; 3 sec delay.
					rDummy02.PlaceAtMe SmallNukeExplosion;											; Fire!
					rDummy02.Disable;
					rDummy02.MarkForDelete;
					NVDLC04EDERef.Say NVDLC04EDEHappy;
					Set fTimer to 0;
					Set nCount to 0;
					Set nState to 4;																					; Fireworks of scene are over, and we pass to the next stage - where we check for AI behaviors, or barks, that should accompany the aftermath.
					Return;
				Else
					Set fTimer to (fTimer + GetSecondsPassed);											; We run a timer to control what we're to do during the following steps, if the one second delay has not occurred yet.
				Endif
			Endif
		Elseif (nState == 4)																						; State 4 handles the aftermath of the bridge's explosion, primarily dealing with AI in the area, or EDE.
		Endif
	Endif

END

SCN NVDLC04UlyssesTrigger3SCRIPT


; Script for Ulysses' third greeting through ED-E.
; TLS 02/28/2011
; TLS 03/23/2011 - Added timer
; TLS 04/06/2011 - Moved quest incrementing here.
; TLS 04/13/2011 - Removed timer and added package for ED-E to fire his greeting.
; CFA 08/04/2011 - Added a faction call before the dialogue starts to make sure the current faction is correct.
; CFA 08/15/2011 - Checking Rep Thresh, not baseline Rep, since faction scaling is different than the adjectives.
; CFA 08/17/2011 - Fixing Rep Thresh checks to fix the "all reps zero" dropthrough = NCR error.


Short nNCRRep;
Short nLegionRep;
Short nHouseRep; 

BEGIN OnTriggerEnter Player

		; Updating the faction checks in case the player returned to Mojave and came back.
		Set nNCRRep to GetReputationThreshold RepNVNCR 1;
		Set nLegionRep to GetReputationThreshold RepNVCaesarsLegion 1;
		Set nHouseRep to GetReputationThreshold RepNVTheStrip 1;

          If (nNCRRep > 1) && (nNCRRep >= nLegionRep) && (nNCRRep >= nHouseRep)
               Set NVDLC04DialogueUlysses.nFaction to 1; (NCR)
          ElseIf (nLegionRep > 1) && (nLegionRep >= nNCRRep) && (nLegionRep >= nHouseRep)
               Set NVDLC04DialogueUlysses.nFaction to 2; (Legion)
          ElseIf (nHouseRep > 1) && (nHouseRep >= nLegionRep) && (nHouseRep >= nNCRRep)
               Set NVDLC04DialogueUlysses.nFaction to 3; (House)
		Else
			Set NVDLC04DialogueUlysses.nFaction to 0; (None of the above)
		EndIf

	SetStage NVDLC04MQ02 20;
	NVDLC04EDEREF.MoveTo NVDLC04SunstoneEDEMarkerREF;
	NVDLC04EDEREF.AddScriptPackage NVDLC04EDEUlyssesCutIn03;
	Disable;
	MarkForDelete;


END

SCN NVDLC04UlyssesTrigger1SCRIPT


; Script for Ulysses' first greeting through ED-E.
; TLS 02/28/2011

; J.S. 3/7/11 - Commented out conversation calls, and moved this function to a Dialogue Package given by the Hopeville's start codebox (NVDLC04Road01StartBox).
; J.S. 3/7/11 - Also removed the checks for distance, or worldspace position, of EDE. We want to move EDE out of the player's view, and set it up for the convo AI.
; TLS 04/06/2011 - Moved quest incrementing to this trigger.
; TLS 04/07/2011 - If player hasn't used ED-E to unlock a commissary, hide the objective.
; TLS 04/07/2011 - Moving quest increment to Ulysses' Goodbye
; CFA 08/04/2011 - Added a faction call before the dialogue starts to make sure the current faction is correct.
; CFA 08/15/2011 - Checking Rep Thresh, not baseline Rep, since faction scaling is different than the adjectives.
; CFA 08/17/2011 - Fixing the dropthrough if all Rep values are zero (were treating player as NCR).


short bUlysses;

Short nNCRRep;
Short nLegionRep;
Short nHouseRep; 

BEGIN OnTriggerEnter Player

	If (bUlysses == 0)

		; Updating the faction checks in case the player returned to Mojave and came back.
		Set nNCRRep to GetReputationThreshold RepNVNCR 1;
		Set nLegionRep to GetReputationThreshold RepNVCaesarsLegion 1;
		Set nHouseRep to GetReputationThreshold RepNVTheStrip 1;

          If (nNCRRep > 1) && (nNCRRep >= nLegionRep) && (nNCRRep >= nHouseRep)
               Set NVDLC04DialogueUlysses.nFaction to 1; (NCR)
          ElseIf (nLegionRep > 1) && (nLegionRep >= nNCRRep) && (nLegionRep >= nHouseRep)
               Set NVDLC04DialogueUlysses.nFaction to 2; (Legion)
          ElseIf (nHouseRep > 1) && (nHouseRep >= nLegionRep) && (nHouseRep >= nNCRRep)
               Set NVDLC04DialogueUlysses.nFaction to 3; (House)
		Else
			Set NVDLC04DialogueUlysses.nFaction to 0; (None of the above)
		EndIf

		;If (NVDLC04EDEREF.GetDistance Player > 250) || (NVDLC04EDEREF.GetInWorldSpace NVDLC04Road01World == 0)
			;NVDLC04EDEREF.MoveToMarkerWithFade NVDLC04EDEHopevilleMarkerREF;
			;NVDLC04EDEREF.StartConversation Player NVDLC04EDEUlyssesTopic001;
		;Else
			;NVDLC04EDEREF.StartConversation Player NVDLC04EDEUlyssesTopic001;
		;EndIf
		;Set NVDLC04Road01StartBox.bEDEMoved to 1;
		If (GetObjectiveCompleted NVDLC04MQ01a 25 == 0)
			SetObjectiveDisplayed NVDLC04MQ01a 25 0;
		EndIf
		Set bUlysses to 1;
		Disable;
		MarkForDelete;
	EndIf

END

