SCN NVDLC04MissileLaunchQuestSCRIPT


; Timer for Silo 2 missile launch sequence
; TLS 02/24/2011
; TLS 03/15/2011 - Added ED-E Audio Log package
; TLS 03/16/2011 - Added explosions and siren.
; TLS 03/25/2011 - Added variable for ED-E Audio Log package.
; TLS 03/31/2011 - Tweaked timing.
; TLS 04/12/2011 - Tweaked timing.
; JSH 05/06/11 - Re-enabled player controls at end of launch sequence.
; TLS 05/31/2011 - Removing ED-E waiting check.
; TLS 06/14/2011 - Changed Enable ref from door to enable parent of door.
; TLS 06/16/2011 - Remove Restrained on ED-E at end of sequence.
; CES 06/21/11 - Added ability to fast travel to Silo02 Nuke Site immediately.
; J.S. 06/23/11 - Changed the call on NVDLC04CourierMileMarkerREF to disable it, rather than enable it.
; CES 06/27/11 - Reorded the disable of the NVDLC04CourierMileMarkerREF to be before the showmap calls so that it works properly.
; CES 06/21/11 - Removed ability to fast travel to Silo02 Nuke Site immediately.
; CES 07/21/11 - Commented out controller rumbles to prevent bug 81012.
; JSH 08/12/11 - Enabled a certain group of enemies in case they're approaching player.


Short nTimerStage;				; Timer stage sequence
Float fTimer;						; Timer countdown


BEGIN GameMode

	If (fTimer > 0)
		Set fTimer to (fTimer - GetSecondsPassed);
	Else
		If (nTimerStage == 0)
			; STAGE 0 - Doors open
			IncrementScriptedChallenge NVDLC04AchievementDoomedtoRepeat;
			Silo02LaunchSmoke01REF.Enable 1;
			Silo02LaunchSmoke02REF.Enable 1;
			Silo02LaunchSmoke03REF.Enable 1;
			Silo02LaunchSmoke04REF.Enable 1;
			PlaySound AMBHooverTrbnRmSteamOS;
			Set nTimerStage to 1;
			Set fTimer to .75;
			Return;
		ElseIf (nTimerStage == 1)
			; STAGE 1 - Start rumble, turn off steam
			StartQuest NVDLC04MissileExplosionTimer;						; JSH 03/25/11
			NVDLC04MissileLaunchREF.PlayGroup Forward 1;
			PlayMusic NVDLC04TransitionStinger;
			PlaySound musNVDLC04WarheardExplode;
			; Controller rumble variables
;			Set ControlRumble.Stage3FireOnTimerValue to 8
;			Set ControlRumble.Stage4FireOnTimerValue to 13
;			Set ControlRumble.Stage5FireOnTimerValue to 16
;			Set ControlRumble.Stage6FireOnTimerValue to 20
;			Set ControlRumble.Stage7FireOnTimerValue
;			Set ControlRumble.Stage8FireOnTimerValue
;			Set ControlRumble.Stage9FireOnTimerValue
;			Set ControlRumble.Stage10FireOnTimerValue

			;Strength of left and right rumble
;			Set ControlRumble.stage2RumbleLeft to 0.25
;			Set ControlRumble.stage2RumbleRight to 0.25
;			Set ControlRumble.stage3RumbleLeft to 0.5
;			Set ControlRumble.stage3RumbleRight to 0.5
;			Set ControlRumble.stage4RumbleLeft to 1
;			Set ControlRumble.stage4RumbleRight to 1
;			Set ControlRumble.stage5RumbleLeft to 0.25
;			Set ControlRumble.stage5RumbleRight to 0.25
;			Set ControlRumble.stage6RumbleLeft to 0.1
;			Set ControlRumble.stage6RumbleRight to 0.1
;			Set ControlRumble.stage7RumbleLeft
;			Set ControlRumble.stage7RumbleRight
;			Set ControlRumble.stage8RumbleLeft
;			Set ControlRumble.stage8RumbleRight
;			Set ControlRumble.stage9RumbleLeft
;			Set ControlRumble.stage9RumbleRight
;			Set ControlRumble.stage10RumbleLeft
;			Set ControlRumble.stage10RumbleRight

;			Set ControlRumble.Stage1Duration to 0
;			Set ControlRumble.Stage2Duration to 8
;			Set ControlRumble.Stage3Duration to 5
;			Set ControlRumble.Stage4Duration to 3
;			Set ControlRumble.Stage5Duration to 4
;			Set ControlRumble.Stage6Duration to 10
;			Set ControlRumble.Stage7Duration
;			Set ControlRumble.Stage8Duration
;			Set ControlRumble.Stage9Duration
;			Set ControlRumble.Stage10Duration
;			Set ControlRumble.timerMaxValue to 240

;			SetStage ControlRumble 1;
			Silo02LaunchSmoke01REF.Disable 1;
			Silo02LaunchSmoke02REF.Disable 1;
			Silo02LaunchSmoke03REF.Disable 1;
			Silo02LaunchSmoke04REF.Disable 1;
			Set nTimerStage to 2;
			Set fTimer to 13;
			Return;
		ElseIf (nTimerStage == 2)
			; STAGE 2 - Launch!
			NVDLC04MissileLaunchREF.PlayGroup Backward 1;
			Set nTimerStage to 3;
			Set fTimer to 2.5;
			Return;
		ElseIf (nTimerStage == 3)
			NVDLC04Silo02LaunchExplosion01REF.PlaceAtMe NVDLC04Silo02LaunchExplosionSmall;
			NVDLC04Silo02LaunchAlarmREF.Enable;
			Set fTimer to 1.5;
			Set nTimerStage to 4;
			Return;
		ElseIf (nTimerStage == 4)
			NVDLC04Silo02LaunchExplosion02REF.PlaceAtMe NVDLC04Silo02LaunchExplosionSmall;
			Set fTimer to 0.75;
			Set nTimerStage to 5;
			Return;
		ElseIf (nTimerStage == 5)
			NVDLC04Silo02LaunchExplosion03REF.PlaceAtMe NVDLC04Silo02LaunchExplosionBig;
			Player.PlaceAtMe NVDLC04DivideRumble;
			Set nTimerStage to 6;
			Set fTimer to 28;
			Return;
		ElseIf (nTimerStage == 6)
			NVDLC04Silo2DoorREFDUPLICATE000.Unlock;
			SetStage NVDLC04MQ01c 100;
			NVDLC04Silo02LaunchAlarmREF.Disable;
			;ShowMessage NVDLC04NukeSilo2MSG;
			NVDLC04CourierMileMarkerREF.Disable;
			ShowMap NVDLC04NukedSilo2MapMarkerREF;
			NVDLC04NukedSilo2DoorMapMarkerREF.Enable;
			ShowMap NVDLC04NukedSilo2DoorMapMarkerREF;
			;StopQuest NVDLC04MissileLaunchQuest;
			Set NVDLC04DialogueEDE.nAudioLog05 to 1;
			If (NVDLC04DialogueEDE.bEDEHired == 1)
				NVDLC04EDEREF.AddScriptPackage NVDLC04EDELog05PACKAGE;
			EndIf
			Set nTimerStage to 7;
			Set fTimer to 2;
			EnablePlayerControls;
			NVDLC04Road02EnemyParentMarker.Enable;																		; JSH 08/12/11 - Enabled a certain group of enemies in case they're approaching player.
			Return;
		ElseIf (nTimerStage == 7)
			ShowMessage NVDLC04NukeSilo2MSG;
			StopQuest NVDLC04MissileLaunchQuest;
			NVDLC04EDEREF.SetRestrained 0;
		EndIf
	EndIf

END

SCN NVDLC04LaunchControlActivatorSCRIPT


; Activator that launches the missile at the end of Act 1.
; TLS 02/24/2011
; TLS 03/31/2011 - Changed terminal to an ED-E access terminal
; TLS 04/12/2011 - Added SetDestroyed call.
; TLS 04/14/2011 - Added timer for lever switch animation.
; JSH 05/06/11 - Disabled player controls at start of missile launch.
; JSH 05/16/11 - Prevent player from using terminal while in combat.
; TLS 06/16/2011 - Added teleport to good viewing position.
; J.S. 7/12/11 - Edited script to conform with standards set in Silo01's blast-door controls. The changes work in tandem with a new activator lever, and associated script, for this Road02 launch control console.
; JSH 08/12/11 - Disabled a certain group of enemies in case they're approaching player.


Short bAccess;
Short bDisplay;
Short	bLeverMSGOnce;					; J.S. 7/12/11 - Added var to control the one-time display of message pointing to the lever usage.
Short	bLaunched;							; J.S. 7/12/11 - Added var to which the lever's script refers in order to control its one-time launch kick-off. Technically, these are not needed because we're setting the activators to "Destroyed." Still, it's a good safeguard practice.
Short nButton;
Short nTimerStage;
Float fTimer;


BEGIN OnActivate

	If (IsActionREF Player == 1)

		If ( Player.IsInCombat == 1 )
			ShowMessage MQ05CantUseNowMessage;
			Return;
		EndIf

		If (bAccess == 0)
			ShowMessage NVDLC04EDETerminalPlayerMSG;
			Set bDisplay to 1;
		ElseIf (bAccess == 1)
			If (bLeverMSGOnce == 0)																			; <---- J.S. 7/12/11 Start of OnActivate changes.
				ShowMessage NVDLC04AshtonLaunchUseLeverMSG;
				Set bLeverMSGOnce to 1;
				SetDestroyed 1;
				Return;
			Endif

			;NVDLC04TerminalMissileButtonREF.PlayGroup Left 0;										; These calls are now handled by the lever activator.
			;NVDLC04TerminalMissileButtonREF.PlaySound NVDLC04ObjSiloControlRight;
			;Set fTimer to 1;
			;Set nTimerStage to 3;
			;DisablePlayerControls;																				; -----> J.S. 7/12/11 End of OnActivate changes.
		EndIf
	EndIf

END


BEGIN GameMode

	If (bDisplay == 1)
		Set nButton to GetButtonPressed
		If (nButton == 0)
			Set bDisplay to 0;
		ElseIf (nButton == 1)
			NVDLC04EDEREF.AddScriptPackage NVDLC04EDEActivateMissileLaunch;
			Set nButton to 0;
			Set bDisplay to 0;
			PlayGroup Forward 0;
			If NVDLC04EDEMissileLaunchTriggerREF.nTimer < 1
				Set NVDLC04EDEMissileLaunchTriggerREF.nTimer to 1;
				Return;
			Endif
		EndIf
	EndIf

	If (nTimerStage == 1)
		If (fTimer > 0)
			Set fTimer to (fTimer - GetSecondsPassed);
		Else
			Set bAccess to 1;
			Set NVDLC04EDETerminalLeverLaunch01REF.nState to 2;									; J.S. 7/12/11 The lever's state is set to 2, which means its script recognizes that it's ready to be activated.
			NVDLC04EDETerminalLeverLaunch01REF.SetDestroyed 0;								; J.S. 7/12/11 The lever is now set to Destroyed 0, which means it now displays a CSA.
			NVDLC04EDETerminalLeverLaunch01REF.PlayGroup Forward 0;
			ShowMessage NVDLC04EDETerminalMSG;
			;Set nTimerStage to 0;																				; J.S. 7/12/11 This call is redundant.
			PlayGroup Left 0;
			PlaySound NVDLC04ObjSiloControlLeft;
			NVDLC04LaunchCollisionREF.Disable;
			Set nTimerStage to 2;
			Set fTimer to 1;
			NVDLC04EDEREF.ResetAI;
		EndIf
	ElseIf (nTimerStage == 2)
		If (fTimer > 0)
			Set fTimer to (fTimer - GetSecondsPassed);
		Else
			NVDLC04MissileLaunchTerminalREF.PlayGroup SpecialIdle 0;
			Set nTimerStage to 0;
		EndIf
	ElseIf (nTimerStage == 3)
		If (fTimer > 0)
			Set fTimer to (fTimer - GetSecondsPassed);
		Else
			ApplyImageSpaceModifier FadeToBlackAndBackISFX;
			DisablePlayerControls;
			Set fTimer to 1;
			Set nTimerStage to 4;
			NVDLC04Road02EnemyParentMarker.Disable;												; JSH 08/12/11 - Disabled a certain group of enemies in case they're approaching player.
		EndIf
	ElseIf (nTimerStage == 4)
		If (fTimer > 0)
			Set fTimer to (fTimer - GetSecondsPassed);
		Else
			Player.MoveTo NVDLC04MissileLaunchViewMarkerREF;
			NVDLC04EDEREF.SetRestrained 1;
			NVDLC04EDEREF.MoveTo NVDLC04EDELog05MarkerREF;
			Set fTimer to 0.5;
			Set nTimerStage to 5;
		EndIf
	ElseIf (nTimerStage == 5)
		If (fTimer > 0)
			Set fTimer to (fTimer - GetSecondsPassed);
		Else
			NVDLC04EDEREF.MoveTo NVDLC04EDELog05MarkerREF;
			Set fTimer to 2.5;
			Set nTimerStage to 6;
		EndIf
	ElseIf (nTimerStage == 6)
		If (fTimer > 0)
			Set fTimer to (fTimer - GetSecondsPassed);
		Else
			StartQuest NVDLC04MissileLaunchQuest;
			Set nTimerStage to 0;
		EndIf

	EndIf

END

