	; if the player is not in Doc's house, it means the player has completed chargen and has exited Doc Mitchell's house.
	If ( nEnableDLC == 0 )
		If ( Player.GetInCell GSDocMitchellHouse == 0 )
			Set nEnableDLC To 1;
			Set fStartTimer To 5;
		EndIf
	ElseIf ( nEnableDLC == 1 )
		If ( fStartTimer <= 0 )
			SetStage NVDLC04MQ00 10;
			ShowMessage NVDLC04LoadMessage01;
			ShowMessage NVDLC04LoadMessage02;
			Set nEnableDLC To 2;
		Else
			Set fStartTimer To (fStartTimer - GetSecondsPassed);
		EndIf

	EndIf

END

scn NVDLC04Silo1ElevatorSCRIPT
; runs the animated sequence for the Silo Elevator
; created by JG


; update 2/15/2011- Added entrance and exit door toggles
; update 2/14/2011- Added collision toggles
; update 2/9/2011- Adjusted the playgroups controlled at each nStage
; J.S. 3/5/11 - Added call to set vars in the elevator's sequence codebox. We do this to help the scripting of the scene once the elevator starts its ride, and once it arrives to its destination.
; TLS 03/18/2011 - Added quest objectives and teleport to keep ED-E on the elevator
; J.S. 03/18/11 - Added calls to disable, and enable, pertinent sections of navmesh in the elevator cell according to position of elevator in relation to the enter, and exit, doors out of this interior.


short bActive; tells the GameMode script when to run
short nStage; tells the GameMode script what script block to run
Int		nExplosion ; 1 = We've triggered an explosion at a precise time in the elevator ride down to silo02.
Float fTimer; tracks how long each playgroup has run


BEGIN OnActivate

	If (NVDLC04ElevatorBox.bElevatorOn == 0)
		NVDLC04Silo2ElevatorControlsREF.playgroup left 1
		;set bActive to 1;
		Set NVDLC04ElevatorBox.bElevatorOn to 1;
		DisableNavmesh NVDLC04ElevNav01REF;															; These are refs of navmesh, which we "cut" out, temporarily, to prevent AI from wanting to step out of the elevator during the lift-down sequence.
		DisableNavmesh NVDLC04ElevNav02REF;
		NVDLC04Silo02ElevBlockPlane01REF.Enable;
		NVDLC04Silo02ElevBlockPlane02REF.Enable;
		NVDLC04SiloElevatorStartCollision02REF.enable;
		PlaySound NVDLC04OBJElevatorStart;
		NVDLC04OBJElevatorLPREF.enable;
		If (NVDLC04EDEREF.Waiting == 0)
			NVDLC04EDEREF.MoveToMarkerWithFade NVDLC04EDELiftMarkerREF;
		EndIf
		If (GetDistance NVDLC04ElevTunnelerVictim02REF >= 768)
			NVDLC04ElevTunnelerVictim02REF.Disable;
		Endif
		If (GetDistance NVDLC04ElevTunnelerVictim03REF >= 768)
			NVDLC04ElevTunnelerVictim03REF.Disable;
		Endif
		SetDestroyed 1;
;		disable;
	Endif

End


Begin GameMode

	If (bActive == 1)

		Set fTimer to (fTimer + GetSecondsPassed);

; stage 0- controls "intro"- 30 seconds

		If (nStage == 0)
			If (fTimer > 0 && fTimer < 30)

				SetObjectiveDisplayed NVDLC04MQ02 5 0;							; Temporarily hide objective during ride.
				SetObjectiveDisplayed NVDLC04MQ02 7 1;
				NVDLC04SiloElevatorStartCollisionREF.disable;
				NVDLC04Silo1ElevatorDoorStartREF.disable;
				NVDLC04SiloElevatorStartCollision02REF.enable;
				NVDLC04SiloElevatorLightREF.enable;
				NVDLC04SiloElevatorREF.playgroup forward 1;
				NVDLC04SiloElevatorLights01Ref.playgroup forward 1;
				NVDLC04SiloElevatorLights02Ref.playgroup forward 1;
				NVDLC04SiloElevatorLights02bRef.playgroup forward 1;
				NVDLC04SiloElevatorLights03Ref.playgroup forward 1;
				NVDLC04SiloElevatorLights03bRef.playgroup forward 1;
				NVDLC04SiloElevatorLights04Ref.playgroup forward 1;
				NVDLC04SiloElevatorLights04bRef.playgroup forward 1;
				NVDLC04SiloElevatorLights05Ref.playgroup forward 1;
				NVDLC04SiloElevatorLights05bRef.playgroup forward 1;
				NVDLC04SiloElevatorLights06Ref.playgroup forward 1;
				NVDLC04SiloElevatorShaft01Ref.playgroup forward 1;
				NVDLC04SiloElevatorShaft02Ref.playgroup forward 1;
				NVDLC04SiloElevatorShaft03Ref.playgroup forward 1;
				NVDLC04SiloElevatorShaft04Ref.playgroup forward 1;
				NVDLC04SiloElevatorShaft05Ref.playgroup forward 1;
				NVDLC04SiloElevatorShaft06Ref.playgroup forward 1;
				Set nStage to 1;
				Return;
			Endif

; stage 1- controls "loop"- intervals of 30 seconds

		Elseif (nStage == 1)	
			If (fTimer >= 30 && fTimer < 60)

				NVDLC04SiloElevatorLights01Ref.playgroup backward 1;
				NVDLC04SiloElevatorLights02Ref.playgroup backward 1;
				NVDLC04SiloElevatorLights02bRef.playgroup backward 1;
				NVDLC04SiloElevatorLights03Ref.playgroup backward 1;
				NVDLC04SiloElevatorLights03bRef.playgroup backward 1;
				NVDLC04SiloElevatorLights04Ref.playgroup backward 1;
				NVDLC04SiloElevatorLights04bRef.playgroup backward 1;
				NVDLC04SiloElevatorLights05Ref.playgroup backward 1;
				NVDLC04SiloElevatorLights05bRef.playgroup backward 1;
				NVDLC04SiloElevatorLights06Ref.playgroup backward 1;
				;NVDLC04SiloElevatorShaftRef.playgroup backward 1;
				Set nStage to 2;
				Return;
			EndIf

; stage 2- controls "end"- 15 seconds
		Elseif (nStage == 2)
			If (nExplosion == 0)
				If (fTimer >= 49)
					NVDLC04Silo02ElevatorFireClose01REF.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
					If (NVDLC04ElevTunnelerWave01BruteREF.GetDisabled != 1)
						NVDLC04ElevTunnelerWave01BruteREF.KillActor NVDLC04UlyssesREF 5;
					Endif 
					If (NVDLC04ElevTunnelerWave01AmbREF.GetDisabled != 1)
						NVDLC04ElevTunnelerWave01AmbREF.KillActor NVDLC04UlyssesREF 1;
					Endif 
					Set nExplosion to 1;
					Return;
				Endif
			Elseif (nExplosion == 1)
				If (fTimer >= 51)
					NVDLC04Silo02ElevatorFireClose02REF.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
					Set nExplosion to 2;
					Return;
				Endif
			Endif
			If (fTimer >= 60 && fTimer < 75)

				NVDLC04SiloElevatorLights01Ref.playgroup left 1;
				NVDLC04SiloElevatorLights02Ref.playgroup left 1;
				NVDLC04SiloElevatorLights02bRef.playgroup left 1;
				NVDLC04SiloElevatorLights03Ref.playgroup left 1;
				NVDLC04SiloElevatorLights03bRef.playgroup left 1;
				NVDLC04SiloElevatorLights04Ref.playgroup left 1;
				NVDLC04SiloElevatorLights04bRef.playgroup left 1;
				NVDLC04SiloElevatorLights05Ref.playgroup left 1;
				NVDLC04SiloElevatorLights05bRef.playgroup left 1;
				NVDLC04SiloElevatorLights06Ref.playgroup left 1;
				;NVDLC04SiloElevatorShaftRef.playgroup left 1;
				Set nStage to 3;
				Return;
			Endif

; stage 3- shuts down elevator and disables it

		Elseif (nStage == 3)
			If (nExplosion == 2)
				If (fTimer >= 63)
					NVDLC04Silo02ElevatorFireClose01REF.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
					If (NVDLC04ElevTunnelerWave01VenREF.GetDisabled != 1)
						NVDLC04ElevTunnelerWave01VenREF.KillActor NVDLC04UlyssesREF 3;
					Endif
					If (NVDLC04ElevTunnelerWave01BruteREF.GetDisabled != 1)
						NVDLC04ElevTunnelerWave01BruteREF.KillActor NVDLC04UlyssesREF 5;
					Endif 
					If (NVDLC04ElevTunnelerWave01AmbREF.GetDisabled != 1)
						NVDLC04ElevTunnelerWave01AmbREF.KillActor NVDLC04UlyssesREF 1;
					Endif 
					Set nExplosion to 3;
					Return;
				Endif
			Elseif (nExplosion == 3)
				If (fTimer >= 64)
					NVDLC04Silo02ElevatorFireClose02REF.PlaceAtMe NVDLC04Silo02ElevatorExplosionBig;
					If (NVDLC04ElevTunnelerWave02VenREF.GetDisabled != 1)
						NVDLC04ElevTunnelerWave02VenREF.KillActor NVDLC04UlyssesREF 1;
					Endif
					If (NVDLC04ElevTunnelerWave02BruteREF.GetDisabled != 1)
						NVDLC04ElevTunnelerWave02BruteREF.KillActor NVDLC04UlyssesREF 3;
					Endif 
					If (NVDLC04ElevTunnelerWave02AmbREF.GetDisabled != 1)
						NVDLC04ElevTunnelerWave02AmbREF.KillActor NVDLC04UlyssesREF 7;
					Endif
					If (NVDLC04ElevTunnelerVictim02REF.GetDead != 1)
						NVDLC04ElevTunnelerVictim02REF.KillActor NVDLC04UlyssesREF 3;
					Endif
					If (NVDLC04ElevTunnelerVictim03REF.GetDead != 1)
						NVDLC04ElevTunnelerVictim03REF.KillActor NVDLC04UlyssesREF 1;
					Endif
					Set nExplosion to 4;
					Return;
				Endif
			Elseif (nExplosion == 4)
				If (fTimer >= 65)
					Set NVDLC04ElevatorBox.bRideEnd to 1;
					Set nExplosion to 5;
				Endif
			Endif
			If (fTimer >= 75)
				EnableNavmesh NVDLC04ElevNav02REF;										; This ref of navmesh is enabled now, since we're at the bottom of the elevator, and want to connect its door with the elevator's nav.
				NVDLC04SiloElevatorEndCollision02REF.disable;
				NVDLC04SiloElevatorShaftCollisionREF.disable;
				NVDLC04SiloElevatorEndCollisionREF.enable;
				NVDLC04Silo1ElevatorDoorEndREF.enable;
				NVDLC04SiloElevatorLightREF.disable;
				NVDLC04SiloElevatorLights01Ref.playgroup right 1;
				NVDLC04SiloElevatorLights02Ref.playgroup right 1;
				NVDLC04SiloElevatorLights02bRef.playgroup right 1;
				NVDLC04SiloElevatorLights03Ref.playgroup right 1;
				NVDLC04SiloElevatorLights03bRef.playgroup right 1;
				NVDLC04SiloElevatorLights04Ref.playgroup right 1;
				NVDLC04SiloElevatorLights04bRef.playgroup right 1;
				NVDLC04SiloElevatorLights05Ref.playgroup right 1;
				NVDLC04SiloElevatorLights05bRef.playgroup right 1;
;				NVDLC04SiloElevatorLights06Ref.playgroup right 1;
				NVDLC04SiloElevatorLights06Ref.disable
				NVDLC04SiloElevatorLightsEndREF.enable 0
				NVDLC04SiloElevatorShaft01Ref.playgroup right 1;
				NVDLC04SiloElevatorShaft02Ref.playgroup right 1;
				NVDLC04SiloElevatorShaft03Ref.playgroup right 1;
				NVDLC04SiloElevatorShaft04Ref.playgroup right 1;
				NVDLC04SiloElevatorShaft05Ref.playgroup right 1;
				NVDLC04SiloElevatorShaft06Ref.playgroup right 1;
				NVDLC04OBJElevatorLPREF.disable;
				Set nStage to 4;
				Return;
			Endif

		Elseif (nStage == 4)
			If (fTimer >= 77)
				NVDLC04Silo02ElevBlockPlane01REF.Disable;
				NVDLC04Silo02ElevBlockPlane02REF.Disable;
				SetObjectiveCompleted NVDLC04MQ02 7 1;
				SetObjectiveDisplayed NVDLC04MQ02 5 1;										; Show objective to navigate Ashton Silo again.
				Set nStage to 5;
				Return;
			Endif
		Endif

	Endif
End

