SCN RWMervinCharacterSCRIPT

BEGIN OnDeath

PlaceAtMe 40mmGrenadeExplosion

END

BEGIN OnActivate Player

If (RadWorldMervinDialogue.Busy == 1);
    ShowMessage RWBusyMessage;
ElseIf (RadWorldMervinDialogue.Busy == 0);
    Activate;
endif;
End

SCN RWHoraceDisappearScript

;

short nEndDay;

BEGIN GameMode

	if (nEndDay == 0)
		set nEndDay to GameDaysPassed + 3;
	endif

	if (nEndDay > 0 && GameDaysPassed >= nEndDay) && player.GetInCell RadWorldSuburbHome02 == 0
		RWHoraceREF.disable
		RWHoraceREF.markfordelete
		RWHoraceNote01REF.enable
		RWHoracePassportREF.enable
		StopQuest rdwrldHoraceDisappearTimer;
	endif

END

SCN RWLegion30WaterBarrelSCRIPT

short nButton

BEGIN OnActivate
	if( IsActionRef player && rdwrldLegionQuest01.NCRCampWaterPoisoned == 0)
		ShowMessage RWLegion30WaterPoisonMessage;
	endif
END

BEGIN GameMode
	if (rdwrldLegionQuest01.NCRCampWaterPoisoned == 0)
		set nButton to GetButtonPressed;

		if (nButton == 1)
			set rdwrldLegionQuest01.NCRCampWaterPoisoned to 1;
			StartQuest RWLegion30WaterBarrelTimerQuest
			RWLegion30WaterREF.Disable;
			Player.RemoveItem BleakVenom 3;
		endif
	endif
END

SCN RWArmyCommWallSCRIPT

int WallBroke

short nButton

BEGIN OnActivate
	if( IsActionRef player)
		ShowMessage RWArmyCommWallMessage;
	endif
END

BEGIN GameMode
	if (WallBroke == 0)
		set nButton to GetButtonPressed;

		if (nButton == 1)
			set WallBroke to 1;
			RWArmyCommWallWholeREF.disable;
			RWArmyCommWallDecalREF.disable;
			RWArmyCommWallActivatorREF.disable;
			RWArmyCommWallWholeREF.markfordelete;
			RWArmyCommWallDecalREF.markfordelete;
			RWArmyCommWallActivatorREF.markfordelete;
			RWArmyCommWallHoleREF.enable;
			RWArmyCommWallDustREF.enable;
			ApplyImageSpaceModifier ExplosionInFace
			PlaySound FXMeleePowerFistConc
			PlaySound FXExplosionImpactDirt
		endif
	endif
END

SCN RWHannahScript

BEGIN OnDeath

if RWHannahREF.GetInCell RadWorldGraveDiggerHut == 0
	AddReputation RWHavasuRep 0 5
endif

END

BEGIN OnActivate Player

If (RadWorldHavasuMiscDialogue.SlaveGagged == 1 && RadWorldHavasuMiscDialogue.SlaveQuestHannah == 1);
    ShowMessage RWMendezSlaveGagged;
ElseIf (RadWorldHavasuMiscDialogue.SlaveGagged == 0 && RadWorldHavasuMiscDialogue.SlaveQuestHannah == 0);
    Activate;
endif;
End

