SCN RWBoatTravelSCRIPT

; Boat that takes the player to and from Havasu.
; 

Ref rLinkedRef;


BEGIN OnActivate

	If (IsActionRef Player)
		Set rLinkedRef to GetLinkedRef;
		PurgeCellBuffers;
		Player.MoveTo rLinkedRef;
		Autosave
	EndIf

END

SCN RWJimmysQuest02TutorialSCRIPT

BEGIN OnTrigger player
	if(RWJimmysCrateREF.GetDisabled == 0 && rdwrldJimmyQuest02 < 10)
		ShowMessage VCGTutorialGrab2;
		SetStage rdwrldJimmyQuest02 10
	elseif (rdwrldJimmyQuest02 == 10)
		Disable;
		MarkForDelete;
	endif
END

SCN RWRisqueMagazineScript

BEGIN OnActivate

	If (IsActionRef Player == 1)
		Player.additem RWMagazineRisque 1
		IncrementScriptedChallenge RWRisqueMagazineChallenge;
		Disable;
		MarkForDelete;
	EndIf

END

SCN RWThirdQuestTimerScript

;timer script for when you initiate stage 20 of the third quest.

short nEndDay;

BEGIN GameMode

	if (nEndDay == 0)
		set nEndDay to GameDaysPassed + 3;
	endif

	if (nEndDay > 0 && GameDaysPassed >= nEndDay)
		SetStage rdwrldThirdQuest 30;
		StopQuest rdwrldThirdQuestTimer;
	endif

END
